import { AsyncBaseBridge } from "./AsyncBaseBridge";
import { AgentId, AgentCommand } from "../types/agents";

// Enums matching Rust
export type UpdateChannel = "stable" | "insider";
export type PerformanceProfile = "safe" | "balanced" | "aggressive";
export type ChatStyle = "block" | "bubble";
export type ReasoningDisplay = "never" | "onDemand" | "always";

export interface GeneralSettings {
  theme: string;
  language: string;
  updateChannel: UpdateChannel;
  autoCheckUpdates: boolean;
  interfaceScale: number;
  defaultAgent: string;
  autoSaveInterval: number;
}

export interface ModelsRuntimeSettings {
  provider: string;
  ollamaEndpoint: string;
  defaultModel: string;
  contextProfile: string;
  contextWindow: number;
  temperature: number;
  performanceProfile: PerformanceProfile;
  autoUnload: boolean;
  idleTimeoutSeconds: number;
  localLlmServiceEnabled: boolean;
  localLlmServicePort: number;
  allowExternalApps: boolean;
  openaiApiKey?: string;
  anthropicApiKey?: string;
}

export interface InterfaceSettings {
  chatStyle: ChatStyle;
  showReasoning: ReasoningDisplay;
  autoNameThreads: boolean;
  enterToSend: boolean;
  regenerateShortcut: string;
  imageInputCap: number;
}

export interface DeveloperSettings {
  showDebugTelemetry: boolean;
  showPromptTemplates: boolean;
  separateReasoningChannel: boolean;
  manualOverrides: boolean;
}

export interface DatabaseSettings {
  path: string;
  poolSize: number;
  autoBackup: boolean;
  backupFrequency: string;
  backupLocation: string;
}

export interface PersonalitySettings {
  aldenSystemPrompt: string;
  aliceSystemPrompt: string;
  sentrySystemPrompt: string;
  aldenVoiceId?: string;
  aliceVoiceId?: string;
  sentryVoiceId?: string;
  proactiveSuggestions: boolean;
  learningEnabled: boolean;
}

export interface ModuleSettings {
