}

export class SettingsBridge extends AsyncBaseBridge<SettingsProps> {
  private settings: SettingsConfig | null = null;

  constructor() {
    super({ agentId: "settings" as AgentId });
  }

  protected async onInitialize(): Promise<void> {
    console.log("[SettingsBridge] Initializing");
  }

  protected async onDestroy(): Promise<void> {
    this.settings = null;
  }

  protected getMockResponse<T>(command: AgentCommand, args?: any): T {
    switch (command) {
      case "system_get_settings":
        return this.getDefaultSettings() as unknown as T;
      case "system_save_settings":
        console.log("Mock saving settings:", args);
        return undefined as unknown as T;
      default:
        return super.getMockResponse(command, args);
    }
  }

  private createAuthenticatedRequest<T>(payload: T): any {
    return {
      auth_token: `demo_token_${this.userId}`,
      ...(payload as any),
    };
  }

  public async getProps(): Promise<SettingsProps> {
    if (!this.settings) {
      await this.loadSettings();
    }

    if (!this.settings) {
      throw new Error("Failed to load settings");
    }

    return {
      settings: this.settings,
      updateSettings: this.updateSettings.bind(this),
      saveSettings: this.saveSettings.bind(this),
      resetSettings: this.resetSettings.bind(this),
    };
  }

  private async loadSettings(): Promise<void> {
    try {
      this.settings = await this.invokeCommand<SettingsConfig>(
        "system_get_settings" as AgentCommand,
        {
          request: this.createAuthenticatedRequest({}),
        },
      );

      // Deep merge with defaults to ensure new fields are populated
      const defaults = this.getDefaultSettings();
      this.settings = {
        ...defaults,
        ...this.settings,
        personality: {
          ...defaults.personality,
          ...(this.settings?.personality || {}),
          // Explicitly ensure sentrySystemPrompt is populated if missing in loaded data
          sentrySystemPrompt:
            (this.settings?.personality as any)?.sentrySystemPrompt ||
            defaults.personality.sentrySystemPrompt,
        },
      };
    } catch (error) {
      console.warn(
        "Failed to load settings from backend, using defaults",
        error,
      );
      this.settings = this.getDefaultSettings();
    }
  }

  public updateSettings(section: keyof SettingsConfig, data: any): void {
    if (this.settings) {
      this.settings[section] = { ...this.settings[section], ...data };
      this.notifySubscribers({ type: "settings_updated", data: this.settings });
    }
  }

  public async saveSettings(): Promise<void> {
    if (this.settings) {
      await this.invokeCommand<void>("system_save_settings" as AgentCommand, {
        request: this.createAuthenticatedRequest({ settings: this.settings }),
      });
      this.notifySubscribers({ type: "settings_saved", data: this.settings });
    }
  }

  public async resetSettings(section?: keyof SettingsConfig): Promise<void> {
    await this.loadSettings();
    this.notifySubscribers({ type: "settings_reset", data: this.settings });
  }

