"""Preference management backed by the Vault service."""

from __future__ import annotations

from dataclasses import dataclass, field, asdict
from datetime import datetime, timezone
from typing import Any, Dict, Iterable, List, Mapping, Optional


class PreferencesError(Exception):
    """Base error for preference management failures."""


class PreferencesAccessError(PreferencesError):
    """Raised when an actor attempts to access preferences they do not own."""


class PreferencesValidationError(PreferencesError):
    """Raised when an update payload is malformed or violates policy."""


@dataclass
class PersonaDefaults:
    """Persona defaults that drive Hearthlink's conversational behaviour."""

    persona: str = "hearthlink"
    language: str = "en-US"
    timezone: str = "UTC"
    voice_profile: str = "balanced-support"
    accessibility_mode: bool = True


@dataclass
class VoiceSettings:
    """Voice loop configuration and accessibility aids."""

    voice: str = "nova"
    speech_rate: float = 1.0
    pitch: float = 1.0
    volume: float = 0.8
    captions_enabled: bool = True


@dataclass
class NotificationSettings:
    """Workspace and security notification preferences."""

    desktop_notifications: bool = True
    email_notifications: bool = False
    quiet_hours_start: str = "21:00"
    quiet_hours_end: str = "07:00"
    escalate_security_events: bool = True


@dataclass
class MemoryPolicy:
    """Retention and privacy controls for stored memories."""

    retention_days: int = 30
    purge_on_persona_revoke: bool = True
    share_analytics: bool = False
    classification: str = "restricted"


@dataclass
class PreferencesBundle:
    """Aggregate view of the stored preferences for a single owner."""

    owner_id: str
    schema_version: str
    persona_defaults: PersonaDefaults = field(default_factory=PersonaDefaults)
    voice_settings: VoiceSettings = field(default_factory=VoiceSettings)
    notifications: NotificationSettings = field(default_factory=NotificationSettings)
    memory_policy: MemoryPolicy = field(default_factory=MemoryPolicy)
    classification: str = "restricted"
    version: int = 1
    updated_at: str = field(default_factory=lambda: datetime.now(timezone.utc).isoformat())
    audit_trail: List[Dict[str, Any]] = field(default_factory=list)
    delegates: List[str] = field(default_factory=list)

    def to_payload(self) -> Dict[str, Any]:
        """Serialise the bundle for Vault storage."""

        return {
            "owner_id": self.owner_id,
            "schema_version": self.schema_version,
            "persona_defaults": asdict(self.persona_defaults),
            "voice_settings": asdict(self.voice_settings),
            "notifications": asdict(self.notifications),
            "memory_policy": asdict(self.memory_policy),
            "classification": self.classification,
            "version": self.version,
            "updated_at": self.updated_at,
            "audit_trail": list(self.audit_trail),
            "delegates": list(self.delegates),
        }

    @classmethod
    def from_payload(cls, payload: Mapping[str, Any]) -> "PreferencesBundle":
        """Rehydrate a bundle from Vault storage."""

        return cls(
            owner_id=str(payload.get("owner_id")),
            schema_version=str(payload.get("schema_version", "v1")),
            persona_defaults=PersonaDefaults(**payload.get("persona_defaults", {})),
            voice_settings=VoiceSettings(**payload.get("voice_settings", {})),
            notifications=NotificationSettings(**payload.get("notifications", {})),
            memory_policy=MemoryPolicy(**payload.get("memory_policy", {})),
            classification=str(payload.get("classification", "restricted")),
            version=int(payload.get("version", 1)),
            updated_at=str(payload.get("updated_at", datetime.now(timezone.utc).isoformat())),
            audit_trail=list(payload.get("audit_trail", [])),
            delegates=list(payload.get("delegates", [])),
        )


class PreferencesManager:
    """Coordinates persistence and policy enforcement for user preferences."""

    def __init__(
        self,
        vault: Any,
        *,
        schema_version: str = "v1",
        namespace: str = "preferences",
        classification: str = "restricted",
        operators: Optional[Iterable[str]] = None,
    ) -> None:
        self._vault = vault
        self._schema_version = schema_version
        self._namespace = namespace
        self._classification = classification
        self._operators = {op for op in operators or []}

    def load_preferences(self, owner_id: str, actor_id: str) -> PreferencesBundle:
        """Return the stored preferences for an owner, creating defaults when necessary."""

        memory_id = self._memory_id(owner_id)
        record = self._vault.get_communal(memory_id, actor_id)

        if record is None:
            if not self._can_initialise(owner_id, actor_id):
                raise PreferencesAccessError(
                    f"Actor '{actor_id}' cannot initialise preferences for '{owner_id}'."
                )
            bundle = self._default_bundle(owner_id)
            self._persist(memory_id, bundle, actor_id)
            return bundle

        payload = record.get("data") if isinstance(record, Mapping) else None
        if not isinstance(payload, Mapping):
            raise PreferencesError("Vault returned an unexpected payload format for preferences.")

        bundle = PreferencesBundle.from_payload(payload)
        self._enforce_access(bundle, actor_id)
        return bundle

    def update_preferences(
        self,
        owner_id: str,
        actor_id: str,
        updates: Mapping[str, Any],
        *,
        delegates: Optional[Iterable[str]] = None,
    ) -> PreferencesBundle:
        """Apply preference updates and persist them to the Vault."""

        if not isinstance(updates, Mapping):
            raise PreferencesValidationError("Updates payload must be a mapping.")

        bundle = self.load_preferences(owner_id, actor_id)
        memory_id = self._memory_id(owner_id)

        changes: Dict[str, Any] = {}
        for section, value in updates.items():
            if not hasattr(bundle, section):
                raise PreferencesValidationError(f"Unknown preferences section '{section}'.")

            current_section = getattr(bundle, section)
            if isinstance(current_section, (PersonaDefaults, VoiceSettings, NotificationSettings, MemoryPolicy)):
                if not isinstance(value, Mapping):
                    raise PreferencesValidationError(
                        f"Section '{section}' requires a mapping of field updates."
                    )
                for field_name, field_value in value.items():
                    if not hasattr(current_section, field_name):
                        raise PreferencesValidationError(
                            f"Unknown field '{section}.{field_name}' in update payload."
                        )
                    if getattr(current_section, field_name) != field_value:
                        setattr(current_section, field_name, field_value)
                        changes[f"{section}.{field_name}"] = field_value
            else:
                if current_section != value:
                    setattr(bundle, section, value)
                    changes[section] = value

        if delegates is not None:
            cleaned_delegates = sorted({str(delegate) for delegate in delegates})
            if cleaned_delegates != sorted(bundle.delegates):
                bundle.delegates = cleaned_delegates
                changes["delegates"] = cleaned_delegates

        if not changes:
            return bundle

        bundle.version += 1
        bundle.updated_at = self._now()
        bundle.audit_trail.append(
            {
                "timestamp": bundle.updated_at,
                "actor": actor_id,
                "changes": dict(changes),
            }
        )
        self._persist(memory_id, bundle, actor_id)
        return bundle

    def export_preferences(self, owner_id: str, actor_id: str) -> Dict[str, Any]:
        """Return a serialisable copy of the owner's preferences for backup/export flows."""

        bundle = self.load_preferences(owner_id, actor_id)
        return bundle.to_payload()

    def reset_preferences(self, owner_id: str, actor_id: str) -> PreferencesBundle:
        """Restore the owner's preferences to default values while preserving delegate assignments."""

        existing = self.load_preferences(owner_id, actor_id)
        memory_id = self._memory_id(owner_id)

        default_bundle = self._default_bundle(owner_id)
        default_bundle.delegates = list(existing.delegates)
        default_bundle.audit_trail = list(existing.audit_trail)
        default_bundle.version = existing.version + 1
        default_bundle.updated_at = self._now()
        default_bundle.audit_trail.append(
            {
                "timestamp": default_bundle.updated_at,
                "actor": actor_id,
                "changes": {"reset": True},
            }
        )
        self._persist(memory_id, default_bundle, actor_id)
        return default_bundle

    def _persist(self, memory_id: str, bundle: PreferencesBundle, actor_id: str) -> None:
        payload = bundle.to_payload()
        self._vault.create_or_update_communal(memory_id, payload, actor_id)

    def _memory_id(self, owner_id: str) -> str:
        return f"{self._namespace}:{owner_id}"

    def _default_bundle(self, owner_id: str) -> PreferencesBundle:
        return PreferencesBundle(
            owner_id=owner_id,
            schema_version=self._schema_version,
            classification=self._classification,
        )

    def _enforce_access(self, bundle: PreferencesBundle, actor_id: str) -> None:
        if not self._can_access(bundle, actor_id):
            raise PreferencesAccessError(
                f"Actor '{actor_id}' does not have access to preferences for '{bundle.owner_id}'."
            )

    def _can_access(self, bundle: PreferencesBundle, actor_id: str) -> bool:
        return actor_id in {bundle.owner_id, *bundle.delegates} or actor_id in self._operators

    def _can_initialise(self, owner_id: str, actor_id: str) -> bool:
        return actor_id == owner_id or actor_id in self._operators

    @staticmethod
    def _now() -> str:
        return datetime.now(timezone.utc).isoformat()

