import React, { useState } from 'react';
import { User, Sparkles, Brain, Briefcase, Check, ChevronRight, ChevronLeft, X, Zap, Smile, Coffee, Code, Terminal, Shield, Palette, Eye, Crown, Heart, Lightbulb } from '@/components/ui/HearthlinkIcons';
import type { MimicPersona, AldenTraits } from '../../types/agents';

interface GuidedPersonaCreatorProps {
  onComplete: (persona: Omit<MimicPersona, 'persona_id' | 'created_at' | 'active'>) => void;
  onCancel: () => void;
}

type WizardStep = 'identity' | 'appearance' | 'personality' | 'workstyle' | 'review';

interface CreatorState {
  name: string;
  description: string;
  avatarStyle: string;
  avatarFeatures: {
    skinColor: string;
    hairColor: string;
    eyeColor: string;
  };
  traits: AldenTraits;
  quirks: string[];
  expertise: string[];
  communicationStyle: string;
  workStyle: string;
}

const INITIAL_STATE: CreatorState = {
  name: '',
  description: '',
  avatarStyle: 'cyberpunk',
  avatarFeatures: {
    skinColor: '#f5d0b0',
    hairColor: '#4a3b2a',
    eyeColor: '#3b82f6',
  },
  traits: {
    openness: 50,
    conscientiousness: 50,
    extraversion: 50,
    agreeableness: 50,
    emotional_stability: 50,
  },
  quirks: [],
  expertise: [],
  communicationStyle: 'Professional',
  workStyle: 'Collaborator',
};

// Helper Icon
const SearchIcon = ({ size }: { size: number }) => (
  <svg width={size} height={size} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
    <circle cx="11" cy="11" r="8"></circle>
    <line x1="21" y1="21" x2="16.65" y2="16.65"></line>
  </svg>
);

// Simple icon wrapper since Users isn't imported
const UsersIcon = ({ size }: { size: number }) => (
  <svg width={size} height={size} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
    <path d="M17 21v-2a4 4 0 0 0-4-4H5a4 4 0 0 0-4 4v2"></path>
    <circle cx="9" cy="7" r="4"></circle>
    <path d="M23 21v-2a4 4 0 0 0-3-3.87"></path>
    <path d="M16 3.13a4 4 0 0 1 0 7.75"></path>
  </svg>
);

const QUIRKS_LIST = [
  { id: 'detail', label: 'Detail-Obsessed', icon: <SearchIcon size={14} /> },
  { id: 'creative', label: 'Wildly Creative', icon: <Sparkles size={14} /> },
  { id: 'direct', label: 'Brutally Direct', icon: <Zap size={14} /> },
  { id: 'empathetic', label: 'Highly Empathetic', icon: <Smile size={14} /> },
  { id: 'caffeinated', label: 'Hyper-Caffeinated', icon: <Coffee size={14} /> },
  { id: 'security', label: 'Paranoid Security', icon: <Shield size={14} /> },
];

const WORK_STYLES = [
  { id: 'collaborator', label: 'Collaborator', description: 'Works side-by-side, asks for input frequently.', icon: <UsersIcon size={20} /> },
  { id: 'autonomous', label: 'Autonomous', description: 'Takes a goal and runs with it, reporting back when done.', icon: <Briefcase size={20} /> },
  { id: 'mentor', label: 'Mentor', description: 'Explains concepts and guides you through solutions.', icon: <Brain size={20} /> },
  { id: 'executor', label: 'Executor', description: 'Focuses purely on speed and output efficiency.', icon: <Zap size={20} /> },
];

const ARCHETYPES = [
  { 
    id: 'leader', 
    label: 'The Leader', 
    icon: <Crown size={16} />,
    traits: { openness: 70, conscientiousness: 90, extraversion: 85, agreeableness: 60, emotional_stability: 80 } 
  },
  { 
    id: 'creator', 
    label: 'The Creator', 
    icon: <Lightbulb size={16} />,
    traits: { openness: 95, conscientiousness: 40, extraversion: 60, agreeableness: 70, emotional_stability: 60 } 
  },
  { 
    id: 'supporter', 
    label: 'The Supporter', 
    icon: <Heart size={16} />,
    traits: { openness: 60, conscientiousness: 80, extraversion: 50, agreeableness: 95, emotional_stability: 90 } 
  },
];

export const GuidedPersonaCreator: React.FC<GuidedPersonaCreatorProps> = ({ onComplete, onCancel }) => {
  const [step, setStep] = useState<WizardStep>('identity');
  const [data, setData] = useState<CreatorState>(INITIAL_STATE);
  const [isGenerating, setIsGenerating] = useState(false);

  const updateData = (updates: Partial<CreatorState>) => {
    setData(prev => ({ ...prev, ...updates }));
  };

  const nextStep = () => {
    const steps: WizardStep[] = ['identity', 'appearance', 'personality', 'workstyle', 'review'];
    const currentIndex = steps.indexOf(step);
    if (currentIndex < steps.length - 1) {
      setStep(steps[currentIndex + 1]);
    }
  };

  const prevStep = () => {
    const steps: WizardStep[] = ['identity', 'appearance', 'personality', 'workstyle', 'review'];
    const currentIndex = steps.indexOf(step);
    if (currentIndex > 0) {
      setStep(steps[currentIndex - 1]);
    }
  };

  const handleComplete = () => {
    const newPersona: Omit<MimicPersona, 'persona_id' | 'created_at' | 'active'> = {
      name: data.name,
      description: data.description,
      traits: data.traits,
      expertise_areas: data.expertise,
      communication_style: `${data.communicationStyle} - ${data.workStyle}`,
    };
    onComplete(newPersona);
  };

  // --- Step Renderers ---

  const renderIdentity = () => (
    <div className="space-y-6 animate-in fade-in slide-in-from-right-4 duration-300">
      <div className="text-center mb-8">
        <h2 className="text-2xl font-bold text-white mb-2">Who are we creating?</h2>
        <p className="text-slate-400">Define the core identity of your new AI team member.</p>
      </div>
      
      <div className="space-y-4">
        <div>
          <label className="block text-sm font-medium text-purple-300 mb-1">Name</label>
          <input
            type="text"
            value={data.name}
            onChange={e => updateData({ name: e.target.value })}
            className="w-full bg-slate-950 border border-purple-500/30 rounded-lg px-4 py-3 text-white focus:outline-none focus:border-purple-500 focus:ring-1 focus:ring-purple-500 transition-all placeholder-slate-600"
            placeholder="e.g. Atlas, CodeNinja, Sarah"
            autoFocus
          />
        </div>
        <div>
          <label className="block text-sm font-medium text-purple-300 mb-1">Role / Description</label>
          <textarea
            value={data.description}
            onChange={e => updateData({ description: e.target.value })}
            className="w-full bg-slate-950 border border-purple-500/30 rounded-lg px-4 py-3 text-white focus:outline-none focus:border-purple-500 focus:ring-1 focus:ring-purple-500 transition-all h-32 resize-none placeholder-slate-600"
