  const renderAppearance = () => (
    <div className="space-y-6 animate-in fade-in slide-in-from-right-4 duration-300">
      <div className="text-center mb-8">
        <h2 className="text-2xl font-bold text-white mb-2">Visual Identity</h2>
        <p className="text-slate-400">Customize their avatar appearance.</p>
      </div>

      <div className="flex gap-8">
        {/* Preview */}
        <div className="w-1/3 flex flex-col items-center gap-4">
          <div className="w-40 h-40 rounded-full border-4 border-purple-500/30 bg-slate-900 flex items-center justify-center overflow-hidden shadow-[0_0_30px_rgba(168,85,247,0.2)] relative">
            <User size={80} className="text-slate-700 absolute" />
