            <div className="absolute top-0 w-full h-1/3 opacity-60" style={{ backgroundColor: data.avatarFeatures.hairColor }} />
            <div className="absolute top-1/3 w-full h-1/6 flex justify-center gap-4">
               <div className="w-3 h-3 rounded-full" style={{ backgroundColor: data.avatarFeatures.eyeColor }} />
               <div className="w-3 h-3 rounded-full" style={{ backgroundColor: data.avatarFeatures.eyeColor }} />
            </div>
          </div>
          <span className="text-sm text-slate-400 font-medium">Preview</span>
        </div>

        {/* Controls */}
        <div className="flex-1 space-y-6">
          <div>
            <label className="flex items-center gap-2 text-sm font-medium text-purple-300 mb-2">
              <Palette size={16} /> Skin Tone
            </label>
            <div className="flex gap-2">
              {['#f5d0b0', '#e0ac69', '#8d5524', '#3a2212', '#e0e0e0', '#a5b4fc'].map(color => (
                <button
                  key={color}
                  onClick={() => updateData({ avatarFeatures: { ...data.avatarFeatures, skinColor: color } })}
                  className={`w-8 h-8 rounded-full border-2 transition-transform hover:scale-110 ${
                    data.avatarFeatures.skinColor === color ? 'border-purple-500 scale-110 ring-2 ring-purple-500/30' : 'border-transparent'
                  }`}
                  style={{ backgroundColor: color }}
                />
              ))}
            </div>
          </div>

          <div>
            <label className="flex items-center gap-2 text-sm font-medium text-purple-300 mb-2">
              <Sparkles size={16} /> Hair Color
            </label>
            <input 
              type="color" 
              value={data.avatarFeatures.hairColor}
              onChange={(e) => updateData({ avatarFeatures: { ...data.avatarFeatures, hairColor: e.target.value } })}
              className="w-full h-10 rounded cursor-pointer bg-transparent"
            />
          </div>

          <div>
            <label className="flex items-center gap-2 text-sm font-medium text-purple-300 mb-2">
              <Eye size={16} /> Eye Color
            </label>
            <div className="flex gap-2">
              {['#3b82f6', '#22c55e', '#ef4444', '#eab308', '#a855f7', '#000000'].map(color => (
                <button
                  key={color}
                  onClick={() => updateData({ avatarFeatures: { ...data.avatarFeatures, eyeColor: color } })}
                  className={`w-8 h-8 rounded-full border-2 transition-transform hover:scale-110 ${
                    data.avatarFeatures.eyeColor === color ? 'border-purple-500 scale-110 ring-2 ring-purple-500/30' : 'border-transparent'
                  }`}
                  style={{ backgroundColor: color }}
                />
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );

  const renderPersonality = () => (
    <div className="space-y-6 animate-in fade-in slide-in-from-right-4 duration-300">
      <div className="text-center mb-6">
        <h2 className="text-2xl font-bold text-white mb-2">Personality Matrix</h2>
        <p className="text-slate-400">Define their behavioral traits and quirks.</p>
      </div>

      {/* Archetypes */}
      <div className="flex justify-center gap-4 mb-8">
        {ARCHETYPES.map(arch => (
          <button
            key={arch.id}
            onClick={() => updateData({ traits: arch.traits })}
            className="flex flex-col items-center gap-2 p-3 rounded-xl bg-slate-800/50 border border-slate-700 hover:border-purple-500 hover:bg-purple-500/10 transition-all w-28 group"
          >
            <div className="p-2 rounded-full bg-slate-700 group-hover:bg-purple-500 group-hover:text-white text-slate-300 transition-colors">
              {arch.icon}
            </div>
            <span className="text-xs font-medium text-slate-300 group-hover:text-white">{arch.label}</span>
          </button>
        ))}
      </div>

      <div className="space-y-6 bg-slate-900/30 p-6 rounded-xl border border-white/5">
        {/* Traits Sliders */}
        <div className="space-y-4">
          {Object.entries(data.traits).map(([trait, value]) => (
            <div key={trait}>
              <div className="flex justify-between text-xs text-slate-400 mb-1 uppercase tracking-wider">
                <span>{trait.replace('_', ' ')}</span>
                <span className="text-purple-400 font-bold">{value}%</span>
              </div>
              <input
                type="range"
                min="0"
                max="100"
                value={value}
                onChange={e => updateData({ traits: { ...data.traits, [trait]: parseInt(e.target.value) } })}
                className="w-full h-2 bg-slate-700 rounded-lg appearance-none cursor-pointer accent-purple-500 hover:accent-purple-400 transition-all"
              />
            </div>
          ))}
        </div>

        {/* Quirks Selector */}
        <div>
          <label className="block text-sm font-medium text-purple-300 mb-3">Quirks & Traits</label>
          <div className="flex flex-wrap gap-2">
            {QUIRKS_LIST.map(quirk => (
              <button
                key={quirk.id}
                onClick={() => {
                  const newQuirks = data.quirks.includes(quirk.id)
                    ? data.quirks.filter(q => q !== quirk.id)
                    : [...data.quirks, quirk.id];
                  updateData({ quirks: newQuirks });
                }}
                className={`flex items-center gap-2 px-3 py-1.5 rounded-full text-sm border transition-all ${
                  data.quirks.includes(quirk.id)
                    ? 'bg-purple-500 text-white border-purple-500 shadow-[0_0_10px_rgba(168,85,247,0.4)]'
                    : 'bg-slate-800/50 text-slate-400 border-slate-700 hover:border-purple-500/50'
                }`}
              >
                {quirk.icon}
                {quirk.label}
              </button>
            ))}
          </div>
        </div>
      </div>
    </div>
  );

  const renderWorkStyle = () => (
    <div className="space-y-6 animate-in fade-in slide-in-from-right-4 duration-300">
      <div className="text-center mb-8">
        <h2 className="text-2xl font-bold text-white mb-2">Work Style</h2>
        <p className="text-slate-400">How should they collaborate with you?</p>
      </div>

      <div className="grid grid-cols-1 gap-4">
        {WORK_STYLES.map(style => (
          <button
            key={style.id}
            onClick={() => updateData({ workStyle: style.label })}
            className={`flex items-center gap-4 p-4 rounded-xl border-2 transition-all text-left ${
              data.workStyle === style.label
                ? 'border-purple-500 bg-purple-500/20 shadow-[0_0_15px_rgba(168,85,247,0.3)]'
                : 'border-slate-700 bg-slate-800/50 hover:border-purple-500/50'
            }`}
          >
            <div className={`p-3 rounded-lg ${
              data.workStyle === style.label ? 'bg-purple-500 text-white' : 'bg-slate-700 text-slate-400'
            }`}>
              {style.icon}
            </div>
            <div>
              <h3 className={`font-bold ${data.workStyle === style.label ? 'text-white' : 'text-slate-200'}`}>
                {style.label}
              </h3>
              <p className="text-sm text-slate-400">{style.description}</p>
            </div>
            {data.workStyle === style.label && (
              <Check className="ml-auto text-purple-400" />
            )}
          </button>
        ))}
      </div>
    </div>
  );

  const renderReview = () => (
    <div className="space-y-6 animate-in fade-in slide-in-from-right-4 duration-300">
      <div className="text-center mb-8">
        <h2 className="text-2xl font-bold text-white mb-2">Ready to Initialize?</h2>
        <p className="text-slate-400">Review your new team member's profile.</p>
      </div>

      <div className="bg-slate-900/80 border border-purple-500/30 rounded-xl p-6 relative overflow-hidden">
        <div className="absolute top-0 left-0 w-full h-1 bg-gradient-to-r from-purple-500 via-pink-500 to-blue-500" />
        
        <div className="flex items-start gap-6">
          <div className="w-24 h-24 bg-slate-800 rounded-xl flex items-center justify-center shrink-0 border border-slate-700 relative overflow-hidden">
             <User size={40} className="text-purple-400 absolute z-0" />
             <div className="absolute inset-0 opacity-60 mix-blend-overlay z-10" style={{ backgroundColor: data.avatarFeatures.skinColor }} />
             <div className="absolute top-0 w-full h-1/3 opacity-50 z-20" style={{ backgroundColor: data.avatarFeatures.hairColor }} />
          </div>
          <div>
            <h3 className="text-2xl font-bold text-white">{data.name || 'Unnamed Persona'}</h3>
            <p className="text-purple-400 font-medium mb-2">{data.workStyle} Ã¢â‚¬Â¢ {data.avatarStyle}</p>
            <p className="text-slate-400 text-sm italic">"{data.description || 'No description provided.'}"</p>
          </div>
        </div>

        <div className="mt-6 grid grid-cols-2 gap-4">
          <div className="bg-slate-800/50 p-3 rounded-lg">
            <span className="text-xs text-slate-500 uppercase tracking-wider block mb-1">Quirks</span>
            <div className="flex flex-wrap gap-1">
              {data.quirks.length > 0 ? data.quirks.map(q => (
                <span key={q} className="text-xs bg-purple-500/20 text-purple-300 px-1.5 py-0.5 rounded">
                  {QUIRKS_LIST.find(i => i.id === q)?.label}
                </span>
              )) : <span className="text-slate-500 text-xs">None selected</span>}
            </div>
          </div>
          <div className="bg-slate-800/50 p-3 rounded-lg">
            <span className="text-xs text-slate-500 uppercase tracking-wider block mb-1">Personality</span>
            <div className="flex gap-1">
               <div className="h-1.5 flex-1 bg-slate-700 rounded-full overflow-hidden">
                  <div className="h-full bg-purple-500" style={{ width: `${data.traits.openness}%` }} />
               </div>
               <div className="h-1.5 flex-1 bg-slate-700 rounded-full overflow-hidden">
                  <div className="h-full bg-blue-500" style={{ width: `${data.traits.conscientiousness}%` }} />
               </div>
               <div className="h-1.5 flex-1 bg-slate-700 rounded-full overflow-hidden">
                  <div className="h-full bg-green-500" style={{ width: `${data.traits.extraversion}%` }} />
               </div>
            </div>
            <div className="text-[10px] text-slate-500 mt-1 text-right">O / C / E</div>
          </div>
        </div>
      </div>
    </div>
  );

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/80 backdrop-blur-sm p-4">
      <div className="w-full max-w-2xl bg-[#0f0a1a]/95 backdrop-blur-xl border border-purple-500/30 rounded-2xl shadow-2xl flex flex-col max-h-[90vh] overflow-hidden">
        {/* Header */}
        <div className="p-6 border-b border-purple-900/30 flex justify-between items-center bg-[#1a0f2e]/50">
          <div className="flex items-center gap-3">
            <div className="p-2 bg-purple-500/20 rounded-lg text-purple-400">
              <Sparkles size={20} />
            </div>
            <div>
              <h1 className="text-lg font-bold text-white">Persona Forge</h1>
              <div className="flex gap-1 mt-1">
                {['identity', 'appearance', 'personality', 'workstyle', 'review'].map((s, i) => (
                  <div 
                    key={s} 
                    className={`h-1 w-6 rounded-full transition-all ${
                      s === step ? 'bg-purple-500 w-8' : 
                      ['identity', 'appearance', 'personality', 'workstyle', 'review'].indexOf(step) > i ? 'bg-purple-500/50' : 'bg-slate-800'
                    }`} 
                  />
                ))}
              </div>
            </div>
          </div>
          <button
            onClick={onCancel}
            className="text-slate-400 hover:text-white transition-colors"
            aria-label="Close persona creator"
          >
            <X size={24} />
          </button>
        </div>

        {/* Content */}
        <div className="flex-1 overflow-y-auto p-8 scrollbar-thin scrollbar-thumb-purple-900/50">
          {step === 'identity' && renderIdentity()}
          {step === 'appearance' && renderAppearance()}
          {step === 'personality' && renderPersonality()}
          {step === 'workstyle' && renderWorkStyle()}
          {step === 'review' && renderReview()}
        </div>

        {/* Footer */}
        <div className="p-6 border-t border-purple-900/30 bg-[#1a0f2e]/50 flex justify-between items-center">
          <button
            onClick={prevStep}
            disabled={step === 'identity'}
            className={`flex items-center gap-2 px-4 py-2 rounded-lg font-medium transition-colors ${
              step === 'identity' 
                ? 'text-slate-600 cursor-not-allowed' 
                : 'text-slate-300 hover:text-white hover:bg-white/5'
            }`}
          >
            <ChevronLeft size={18} />
            Back
          </button>

          {step === 'review' ? (
            <button
              onClick={handleComplete}
              className="flex items-center gap-2 px-6 py-2 bg-purple-600 hover:bg-purple-500 text-white rounded-lg font-bold shadow-[0_0_20px_rgba(168,85,247,0.4)] transition-all transform hover:scale-105"
            >
              Initialize Persona
              <Sparkles size={18} />
            </button>
          ) : (
            <button
              onClick={nextStep}
              disabled={step === 'identity' && !data.name}
              className={`flex items-center gap-2 px-6 py-2 bg-purple-600 hover:bg-purple-500 text-white rounded-lg font-bold transition-all ${
                step === 'identity' && !data.name ? 'opacity-50 cursor-not-allowed' : 'shadow-lg hover:shadow-purple-500/25'
              }`}
            >
              Next Step
              <ChevronRight size={18} />
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

