import type {
  PersonaForgeState,
  PersonaForgeStep,
  SpecialTrait,
} from '@/types/persona-forge';
import { computeFrictionProfile } from '@/lib/persona-forge-validator';

const ROLE_PREVIEW: Record<string, { defaults: string; prioritizes: string; resists: string }> = {
  Coordinator: { defaults: 'clarity', prioritizes: 'alignment', resists: 'chaos' },
  Analyst: { defaults: 'rigor', prioritizes: 'evidence', resists: 'guesswork' },
  Creator: { defaults: 'imagination', prioritizes: 'novelty', resists: 'rigidity' },
  Diplomat: { defaults: 'empathy', prioritizes: 'harmony', resists: 'conflict' },
  Engineer: { defaults: 'precision', prioritizes: 'execution', resists: 'ambiguity' },
  Director: { defaults: 'vision', prioritizes: 'momentum', resists: 'drift' },
};

const ENGINE_PREVIEW: Record<string, string> = {
  'Logic Engine': 'Builds conclusions step-by-step, favoring structured reasoning and explicit checks.',
  'Synapse Spark': 'Connects disparate ideas quickly, emphasizing creative synthesis over strict structure.',
  'Resonance Core': 'Balances empathy with analysis, adapting logic to context and emotional tone.',
  'Nexus Processor': 'Optimizes for throughput, knitting insights into clear, decisive recommendations.',
};

const VOICE_PREVIEW: Record<string, string> = {
  Formal: '"Based on the evidence presented, the optimal path is clear and actionable."',
  Casual: '"Here is what I would do next â€” quick, clear, and easy to follow."',
  Socratic: '"What outcome matters most, and what constraints are we not questioning yet?"',
  Direct: '"Do this first. Then do that. Skip the noise."',
};

const traitSummary = (traits: SpecialTrait[]): string => {
  if (traits.length === 0) {
    return 'No special traits selected; default balance across friction bands.';
  }

  const count = traits.length;
  return `Traits selected (${count}): ${traits.join(', ')}.`;
};

const frictionBars = (traits: SpecialTrait[]): string => {
  if (traits.length === 0) {
    return 'Trait friction bias is neutral.';
  }

  const lowCount = traits.filter(trait => ['Empathic', 'Patient', 'Pragmatic'].includes(trait)).length;
  const highCount = traits.filter(trait => ['Bold', 'Candid', 'Meticulous'].includes(trait)).length;
  const mediumCount = traits.length - lowCount - highCount;
  return `Trait friction bias â€” Low: ${lowCount}, Medium: ${mediumCount}, High: ${highCount}.`;
};

export const generatePreviewText = (state: PersonaForgeState, step: PersonaForgeStep): string => {
  switch (step) {
    case 1: {
      if (!state.coreRole) {
        return 'Select a core role to generate defaults, priorities, and resistances.';
      }
      const preview = ROLE_PREVIEW[state.coreRole];
      return `Defaults to ${preview.defaults}, prioritizes ${preview.prioritizes}, resists ${preview.resists}.`;
    }
    case 2:
      return state.thinkingEngine
        ? ENGINE_PREVIEW[state.thinkingEngine]
        : 'Select a thinking engine to preview reasoning style.';
    case 3:
      return state.voiceProtocol
        ? VOICE_PREVIEW[state.voiceProtocol]
        : 'Select a voice protocol to preview tone.';
    case 4:
      return `${traitSummary(state.specialTraits)} ${frictionBars(state.specialTraits)}`;
    case 5: {
      const profile = computeFrictionProfile(state.specialTraits, state.behavioralSliders);
      return `Low friction: ${profile.low.join(', ') || 'None'}. Medium friction: ${
        profile.medium.join(', ') || 'None'
      }. High friction: ${profile.high.join(', ') || 'None'}.`;
    }
    case 6:
      return state.relationship
        ? `${state.personaName || 'Persona'} will operate as a ${state.relationship}.`
        : 'Choose a relationship to finalize identity.';
    case 7: {
      if (!state.coreRole || !state.thinkingEngine || !state.voiceProtocol || !state.relationship) {
        return 'Complete all required steps to generate the full manifest.';
      }
      const profile = computeFrictionProfile(state.specialTraits, state.behavioralSliders);
      return [
        `Persona: ${state.personaName || 'Unnamed'}`,
        `Role: ${state.coreRole} (${state.relationship})`,
        `Engine: ${state.thinkingEngine}, Voice: ${state.voiceProtocol}`,
        `Traits: ${state.specialTraits.join(', ') || 'None'}`,
        `Friction â€” Low: ${profile.low.length}, Medium: ${profile.medium.length}, High: ${profile.high.length}`,
      ].join(' | ');
    }
    default:
      return '';
  }
};
