describe('Persona Forge store', () => {
  beforeEach(() => {
    jest.useFakeTimers();
    jest.setSystemTime(new Date('2026-01-01T00:00:00.000Z'));
    usePersonaForgeStore.getState().resetForge();
  });

  afterEach(() => {
    jest.useRealTimers();
  });

  it('initializes with sensible defaults', () => {
    const state = usePersonaForgeStore.getState();
    expect(state.currentStep).toBe(1);
    expect(state.isComplete).toBe(false);
    expect(state.coreRole).toBeNull();
    expect(state.thinkingEngine).toBeNull();
    expect(state.voiceProtocol).toBeNull();
    expect(state.specialTraits).toEqual([]);
    expect(state.behavioralSliders).toEqual({
      analysis_creativity: 0,
      support_decisiveness: 0,
      risk_caution: 0,
    });
    expect(state.personaName).toBe('');
    expect(state.relationship).toBeNull();
  });

  it('enforces canProceed rules per step', () => {
    const store = usePersonaForgeStore.getState();
    expect(store.canProceed(1)).toBe(false);
    store.setRole(CORE_ROLES[0]);
    expect(store.canProceed(1)).toBe(true);
    expect(store.canProceed(2)).toBe(false);
    store.setThinkingEngine(THINKING_ENGINES[0]);
    expect(store.canProceed(2)).toBe(true);
    store.setVoiceProtocol(VOICE_PROTOCOLS[0]);
    expect(store.canProceed(3)).toBe(true);
    expect(store.canProceed(4)).toBe(true);
    expect(store.canProceed(5)).toBe(true);
    expect(store.canProceed(6)).toBe(false);
    store.setIdentity('Nova', RELATIONSHIPS[0]);
    expect(store.canProceed(6)).toBe(true);
  });

  it('moves through steps only when validation passes', () => {
    const store = usePersonaForgeStore.getState();
    store.nextStep();
    expect(store.currentStep).toBe(1);

    store.setRole(CORE_ROLES[1]);
    store.nextStep();
    expect(store.currentStep).toBe(2);

    store.nextStep();
    expect(store.currentStep).toBe(2);

    store.setThinkingEngine(THINKING_ENGINES[1]);
    store.nextStep();
    expect(store.currentStep).toBe(3);

    store.setVoiceProtocol(VOICE_PROTOCOLS[1]);
    store.nextStep();
    expect(store.currentStep).toBe(4);
  });

  it('limits special traits to three', () => {
    const store = usePersonaForgeStore.getState();
    store.toggleTrait(SPECIAL_TRAITS[0]);
    store.toggleTrait(SPECIAL_TRAITS[1]);
    store.toggleTrait(SPECIAL_TRAITS[2]);
    store.toggleTrait(SPECIAL_TRAITS[3]);
    expect(store.specialTraits.length).toBe(3);
  });

  it('clamps slider values to -1..1', () => {
    const store = usePersonaForgeStore.getState();
    store.updateSlider('analysis_creativity', 2);
    expect(store.behavioralSliders.analysis_creativity).toBe(1);
    store.updateSlider('support_decisiveness', -5);
    expect(store.behavioralSliders.support_decisiveness).toBe(-1);
  });

  it('generates previews for each step', () => {
    const store = usePersonaForgeStore.getState();
    store.setRole(CORE_ROLES[2]);
    expect(store.generatePreview(1)).toContain('Defaults to');

    store.setThinkingEngine(THINKING_ENGINES[2]);
    expect(store.generatePreview(2)).toContain('context');

    store.setVoiceProtocol(VOICE_PROTOCOLS[2]);
    expect(store.generatePreview(3)).toContain('?');

    store.toggleTrait(SPECIAL_TRAITS[4]);
    expect(store.generatePreview(4)).toContain('Traits selected');

    expect(store.generatePreview(5)).toContain('Low friction');

    store.setIdentity('Atlas', RELATIONSHIPS[1]);
    expect(store.generatePreview(6)).toContain('Atlas');

    expect(store.generatePreview(7)).toContain('Persona:');
  });

  it('compiles a valid PersonaSpec', () => {
    const store = usePersonaForgeStore.getState();
    store.setRole(CORE_ROLES[3]);
    store.setThinkingEngine(THINKING_ENGINES[3]);
    store.setVoiceProtocol(VOICE_PROTOCOLS[3]);
    store.toggleTrait(SPECIAL_TRAITS[5]);
    store.updateSlider('risk_caution', 0.5);
    store.setIdentity('Orion', RELATIONSHIPS[2]);

    const spec = store.compilePersonaSpec();
    expect(spec).not.toBeNull();
    if (!spec) {
      return;
    }
    expect(parsePersonaSpec(spec)).toEqual(spec);
    expect(spec.frictionProfile.low.length).toBeGreaterThan(0);
    expect(Object.isFrozen(spec)).toBe(true);
    expect(Object.isFrozen(spec.behavioralSliders)).toBe(true);
  });
});
