import React, { useEffect } from 'react';
import { usePersonaForgeStore } from '@/stores/usePersonaForgeStore';
import { ProgressBar } from './ProgressBar';
import { StepNavigation } from './StepNavigation';
import { Step1CoreRole } from './steps/Step1CoreRole';
import { Step2ThinkingEngine } from './steps/Step2ThinkingEngine';
import { Step3VoiceProtocol } from './steps/Step3VoiceProtocol';
import { Step4SpecialTraits } from './steps/Step4SpecialTraits';
import { Step5BehavioralTuning } from './steps/Step5BehavioralTuning';
import { Step6Identity } from './steps/Step6Identity';
import { Step7Review } from './steps/Step7Review';

interface PersonaForgeModalProps {
  isOpen: boolean;
  onClose: () => void;
  onDeploy?: (personaId: string) => void;
}

const TOTAL_STEPS = 7;

export const PersonaForgeModal: React.FC<PersonaForgeModalProps> = ({
  isOpen,
  onClose,
  onDeploy,
}) => {
  const {
    currentStep,
    nextStep,
    prevStep,
    resetForge,
    canProceed,
    compilePersonaSpec,
  } = usePersonaForgeStore();

  if (!isOpen) return null;

  const handleNext = () => {
    if (currentStep < TOTAL_STEPS) {
      nextStep();
    } else if (currentStep === TOTAL_STEPS && onDeploy) {
      // Deploy logic: compile and pass to Agent 3
      const spec = compilePersonaSpec();
      if (spec) {
        onDeploy(spec.id);
      }
    }
  };

  const handlePrev = () => {
    prevStep();
  };

  const handleCancel = () => {
    resetForge();
    onClose();
  };

  const canGoNext = (): boolean => {
    return canProceed(currentStep);
  };

  const renderStep = () => {
    switch (currentStep) {
      case 1:
        return <Step1CoreRole />;
      case 2:
        return <Step2ThinkingEngine />;
      case 3:
        return <Step3VoiceProtocol />;
      case 4:
        return <Step4SpecialTraits />;
      case 5:
        return <Step5BehavioralTuning />;
      case 6:
        return <Step6Identity />;
      case 7:
        return <Step7Review />;
      default:
        return null;
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-2xl shadow-2xl w-full max-w-[800px] max-h-[90vh] overflow-hidden flex flex-col">
        {/* Header */}
        <div className="px-6 pt-6 pb-4 border-b border-gray-200">
          <h2 className="text-2xl font-bold text-gray-900 mb-4">Persona Forge</h2>
          <ProgressBar currentStep={currentStep} totalSteps={TOTAL_STEPS} />
        </div>

        {/* Content Area */}
        <div className="flex-1 overflow-y-auto px-6 py-6">
          {renderStep()}
        </div>

        {/* Navigation Footer */}
        <div className="px-6 pb-6">
          <StepNavigation
            currentStep={currentStep}
            totalSteps={TOTAL_STEPS}
            canGoNext={canGoNext()}
            onPrev={handlePrev}
            onNext={handleNext}
            onCancel={handleCancel}
            isLastStep={currentStep === TOTAL_STEPS}
          />
        </div>
      </div>
    </div>
  );
};

