import { create } from 'zustand';
import type {
  BehavioralSlider,
  CoreRole,
  PersonaForgeState,
  PersonaForgeStep,
  PersonaSpec,
  Relationship,
  SpecialTrait,
  ThinkingEngine,
  VoiceProtocol,
} from '@/types/persona-forge';
import { generatePreviewText } from '@/lib/persona-preview-generator';
import {
  canProceed,
  clampSliderValue,
  computeFrictionProfile,
  validatePersonaSpec,
} from '@/lib/persona-forge-validator';

export interface PersonaForgeActions {
  nextStep: () => void;
  prevStep: () => void;
  resetForge: () => void;
  setRole: (role: CoreRole) => void;
  setThinkingEngine: (engine: ThinkingEngine) => void;
  setVoiceProtocol: (protocol: VoiceProtocol) => void;
  toggleTrait: (trait: SpecialTrait) => void;
  updateSlider: (slider: keyof BehavioralSlider, value: number) => void;
  setIdentity: (name: string, relationship: Relationship) => void;
  canProceed: (step: PersonaForgeStep) => boolean;
  generatePreview: (step: PersonaForgeStep) => string;
  compilePersonaSpec: () => PersonaSpec | null;
}

type PersonaForgeStore = PersonaForgeState & PersonaForgeActions;

const DEFAULT_SLIDERS: BehavioralSlider = {
  analysis_creativity: 0,
  support_decisiveness: 0,
  risk_caution: 0,
};

const INITIAL_STATE: PersonaForgeState = {
  currentStep: 1,
  isComplete: false,
  coreRole: null,
  thinkingEngine: null,
  voiceProtocol: null,
  specialTraits: [],
  behavioralSliders: { ...DEFAULT_SLIDERS },
  personaName: '',
  relationship: null,
  frictionProfile: null,
  decisionTrace: [],
  currentPreview: '',
};

const createDecisionEntry = (
  step: PersonaForgeStep,
  decision: string,
  reasoning?: string,
) => ({
  step,
  timestamp: new Date().toISOString(),
  decision,
  reasoning,
});

const appendDecision = (
  state: PersonaForgeState,
  entry: ReturnType<typeof createDecisionEntry>,
): PersonaForgeState['decisionTrace'] => {
  return [...state.decisionTrace, entry];
};

const createPersonaId = (state: PersonaForgeState): string => {
  const seed = `${state.personaName}|${state.coreRole}|${state.thinkingEngine}|${state.voiceProtocol}|${state.relationship}`;
  let hash = 0;
  for (let i = 0; i < seed.length; i += 1) {
    hash = (hash * 31 + seed.charCodeAt(i)) >>> 0;
  }
  const slug = state.personaName
    .trim()
    .toLowerCase()
    .replace(/[^a-z0-9]+/g, '-')
    .replace(/(^-|-$)/g, '');
  return `${slug || 'persona'}-${hash.toString(16)}`;
};

const deepFreeze = <T,>(value: T): T => {
  if (value && typeof value === 'object') {
    Object.freeze(value);
    for (const key of Object.keys(value)) {
      const nested = (value as Record<string, unknown>)[key];
      if (nested && typeof nested === 'object' && !Object.isFrozen(nested)) {
        deepFreeze(nested);
      }
    }
  }
  return value;
};

export const usePersonaForgeStore = create<PersonaForgeStore>((set, get) => ({
  ...INITIAL_STATE,
  nextStep: () => {
    const state = get();
    if (state.currentStep >= 7) {
      return;
    }
    if (!canProceed(state, state.currentStep)) {
      set({
        decisionTrace: appendDecision(
          state,
          createDecisionEntry(state.currentStep, 'Blocked next step', 'Validation failed.'),
        ),
      });
      return;
    }
    const nextStep = (state.currentStep + 1) as PersonaForgeStep;
    const preview = generatePreviewText(state, nextStep);
    set({
      currentStep: nextStep,
      isComplete: nextStep === 7,
      currentPreview: preview,
      decisionTrace: appendDecision(state, createDecisionEntry(nextStep, `Moved to step ${nextStep}`)),
    });
  },
  prevStep: () => {
    const state = get();
    if (state.currentStep <= 1) {
      return;
    }
    const prevStep = (state.currentStep - 1) as PersonaForgeStep;
    const preview = generatePreviewText(state, prevStep);
    set({
      currentStep: prevStep,
      isComplete: false,
      currentPreview: preview,
      decisionTrace: appendDecision(state, createDecisionEntry(prevStep, `Moved to step ${prevStep}`)),
    });
  },
  resetForge: () => {
    const resetEntry = createDecisionEntry(1, 'Forge reset');
    set({
      ...INITIAL_STATE,
      decisionTrace: [resetEntry],
    });
  },
  setRole: (role: CoreRole) => {
    const state = get();
    const preview = generatePreviewText({ ...state, coreRole: role }, state.currentStep);
    set({
      coreRole: role,
      currentPreview: preview,
      decisionTrace: appendDecision(state, createDecisionEntry(state.currentStep, `Selected role: ${role}`)),
    });
  },
  setThinkingEngine: (engine: ThinkingEngine) => {
    const state = get();
    const preview = generatePreviewText({ ...state, thinkingEngine: engine }, state.currentStep);
    set({
      thinkingEngine: engine,
      currentPreview: preview,
      decisionTrace: appendDecision(
        state,
        createDecisionEntry(state.currentStep, `Selected thinking engine: ${engine}`),
      ),
    });
  },
  setVoiceProtocol: (protocol: VoiceProtocol) => {
    const state = get();
    const preview = generatePreviewText({ ...state, voiceProtocol: protocol }, state.currentStep);
    set({
      voiceProtocol: protocol,
      currentPreview: preview,
      decisionTrace: appendDecision(
        state,
        createDecisionEntry(state.currentStep, `Selected voice protocol: ${protocol}`),
      ),
    });
  },
  toggleTrait: (trait: SpecialTrait) => {
    const state = get();
    let nextTraits = state.specialTraits;
    let reasoning: string | undefined;

    if (state.specialTraits.includes(trait)) {
      nextTraits = state.specialTraits.filter(current => current !== trait);
    } else if (state.specialTraits.length < 3) {
      nextTraits = [...state.specialTraits, trait];
    } else {
      reasoning = 'Trait limit reached.';
    }

    const frictionProfile = computeFrictionProfile(nextTraits, state.behavioralSliders);
    const preview = generatePreviewText(
      { ...state, specialTraits: nextTraits, frictionProfile },
      state.currentStep,
    );
    set({
      specialTraits: nextTraits,
      frictionProfile,
      currentPreview: preview,
      decisionTrace: appendDecision(
        state,
        createDecisionEntry(state.currentStep, `Toggled trait: ${trait}`, reasoning),
      ),
    });
  },
  updateSlider: (slider: keyof BehavioralSlider, value: number) => {
    const state = get();
    const clamped = clampSliderValue(value);
    const nextSliders = {
      ...state.behavioralSliders,
      [slider]: clamped,
    };
    const frictionProfile = computeFrictionProfile(state.specialTraits, nextSliders);
    const preview = generatePreviewText(
      { ...state, behavioralSliders: nextSliders, frictionProfile },
      state.currentStep,
    );
    set({
      behavioralSliders: nextSliders,
      frictionProfile,
      currentPreview: preview,
      decisionTrace: appendDecision(
        state,
        createDecisionEntry(state.currentStep, `Updated slider: ${slider}`, `Value: ${clamped}`),
      ),
    });
  },
  setIdentity: (name: string, relationship: Relationship) => {
    const state = get();
    const preview = generatePreviewText(
      { ...state, personaName: name, relationship },
      state.currentStep,
    );
    set({
      personaName: name,
      relationship,
      currentPreview: preview,
      decisionTrace: appendDecision(
        state,
        createDecisionEntry(state.currentStep, `Set identity: ${name}`, `Relationship: ${relationship}`),
      ),
    });
  },
  canProceed: (step: PersonaForgeStep) => {
    const state = get();
    return canProceed(state, step);
  },
  generatePreview: (step: PersonaForgeStep) => {
    const state = get();
    const preview = generatePreviewText(state, step);
    set({
      currentPreview: preview,
      decisionTrace: appendDecision(state, createDecisionEntry(step, `Generated preview for step ${step}`)),
    });
    return preview;
  },
  compilePersonaSpec: () => {
    const state = get();
    if (!canProceed(state, 6)) {
      return null;
    }

    if (!state.coreRole || !state.thinkingEngine || !state.voiceProtocol || !state.relationship) {
      return null;
    }

    const frictionProfile = computeFrictionProfile(state.specialTraits, state.behavioralSliders);
    const createdAt = state.decisionTrace[0]?.timestamp ?? new Date(0).toISOString();

    const spec: PersonaSpec = {
      id: createPersonaId(state),
      name: state.personaName,
      coreRole: state.coreRole,
      relationship: state.relationship,
      thinkingEngine: state.thinkingEngine,
      voiceProtocol: state.voiceProtocol,
      specialTraits: [...state.specialTraits],
      behavioralSliders: { ...state.behavioralSliders },
      frictionProfile,
      createdAt,
      version: 1,
    };

    if (!validatePersonaSpec(spec)) {
      return null;
    }

    return deepFreeze(spec);
  },
}));

