    high: ['Unbounded theorizing'],
  },
  Visionary: {
    low: ['Long-range planning', 'Big-picture framing'],
    medium: ['Scenario exploration'],
    high: ['Short-term myopia'],
  },
  Meticulous: {
    low: ['Detail verification', 'Edge-case tracking'],
    medium: ['Careful pacing'],
    high: ['Loose standards'],
  },
  Bold: {
    low: ['Decisive action', 'High-velocity execution'],
    medium: ['Calculated risks'],
    high: ['Excessive caution'],
  },
  Patient: {
    low: ['Deliberate pacing', 'Thorough walkthroughs'],
    medium: ['Incremental progress'],
    high: ['Rushed conclusions'],
  },
  Candid: {
    low: ['Direct feedback', 'Plainspoken summaries'],
    medium: ['Measured honesty'],
    high: ['Vague hedging'],
  },
};

const sliderProfile = (sliders: BehavioralSlider): FrictionProfile => {
  const low: string[] = [];
  const medium: string[] = [];
  const high: string[] = [];

  if (sliders.analysis_creativity >= 0.5) {
    low.push('Structured reasoning', 'Evidence-driven analysis');
    high.push('Freeform riffing');
  } else if (sliders.analysis_creativity <= -0.5) {
    low.push('Rapid ideation', 'Exploratory brainstorming');
    high.push('Exhaustive analysis');
  } else {
    medium.push('Balanced analysis', 'Measured creativity');
  }

  if (sliders.support_decisiveness >= 0.5) {
    low.push('Clear directives', 'Rapid decisions');
    high.push('Open-ended support loops');
  } else if (sliders.support_decisiveness <= -0.5) {
    low.push('Collaborative check-ins', 'Gentle guidance');
    high.push('Hardline directives');
  } else {
    medium.push('Calibrated guidance', 'Shared decisions');
  }

  if (sliders.risk_caution >= 0.5) {
    low.push('Risk assessment', 'Contingency planning');
    high.push('Unbounded risk-taking');
  } else if (sliders.risk_caution <= -0.5) {
    low.push('Bold experiments', 'Speculative leaps');
    high.push('Overly cautious plans');
  } else {
    medium.push('Balanced risk posture');
  }

  return { low, medium, high };
};

const mergeFriction = (base: FrictionProfile, next: FrictionProfile): FrictionProfile => {
  return {
    low: [...base.low, ...next.low],
    medium: [...base.medium, ...next.medium],
    high: [...base.high, ...next.high],
  };
};

const dedupe = (values: string[]): string[] => {
  const seen = new Set<string>();
  const result: string[] = [];
  for (const value of values) {
    if (!seen.has(value)) {
      seen.add(value);
      result.push(value);
    }
  }
  return result;
};

export const computeFrictionProfile = (
  specialTraits: SpecialTrait[],
  behavioralSliders: BehavioralSlider,
): FrictionProfile => {
  let profile = sliderProfile(behavioralSliders);
  for (const trait of specialTraits) {
    profile = mergeFriction(profile, TRAIT_FRICTION_MAP[trait]);
  }

  return {
    low: dedupe(profile.low),
    medium: dedupe(profile.medium),
    high: dedupe(profile.high),
  };
};

export const clampSliderValue = (value: number): number => {
  if (Number.isNaN(value)) {
    return 0;
  }
  return Math.min(1, Math.max(-1, value));
};

export const validatePersonaForgeState = (state: PersonaForgeState): boolean => {
  return personaForgeStateSchema.safeParse(state).success;
};

export const canProceed = (state: PersonaForgeState, step: PersonaForgeStep): boolean => {
  switch (step) {
    case 1:
      return stepOneSchema.safeParse({ coreRole: state.coreRole }).success;
    case 2:
      return stepTwoSchema.safeParse({ thinkingEngine: state.thinkingEngine }).success;
    case 3:
      return stepThreeSchema.safeParse({ voiceProtocol: state.voiceProtocol }).success;
    case 4:
      return stepFourSchema.safeParse({ specialTraits: state.specialTraits }).success;
    case 5:
      return stepFiveSchema.safeParse({ behavioralSliders: state.behavioralSliders }).success;
    case 6:
      return stepSixSchema.safeParse({ personaName: state.personaName, relationship: state.relationship }).success;
    case 7:
      return canProceed(state, 6);
    default:
      return false;
  }
};

export const validatePersonaSpec = (spec: PersonaSpec): boolean => {
  return personaSpecSchema.safeParse(spec).success;
};

export const parsePersonaSpec = (spec: unknown): PersonaSpec => {
  return personaSpecSchema.parse(spec);
};

