import React, { useEffect, useMemo, useState } from 'react';
import { MessageSquareText } from 'lucide-react';
import { usePersonaForgeStore } from '@/stores/usePersonaForgeStore';
import { VOICE_PROTOCOLS, VoiceProtocol } from '@/types/persona-forge';

const protocolSamples: Record<VoiceProtocol, { description: string; sample: string }> = {
  Formal: {
    description: 'Professional and structured communication',
    sample:
      'I recommend conducting a comprehensive analysis of the available options before proceeding with implementation. This approach ensures optimal outcomes and minimizes potential risks.',
  },
  Casual: {
    description: 'Friendly and conversational tone',
    sample:
      'Hey! I have been thinking about this, and I would say we should probably dig into the options a bit before jumping in. It will help us avoid surprises.',
  },
  Socratic: {
    description: 'Question-driven exploration and guided discovery',
    sample:
      'What outcomes are you hoping to achieve? Have you considered what might happen if we approach this differently? What assumptions are we making about the constraints?',
  },
  Direct: {
    description: 'Concise and action-oriented',
    sample:
      'Analyze options first. Choose the best fit. Implement immediately. This minimizes risk and maximizes results.',
  },
};

const useTypingEffect = (text: string, speed = 18) => {
  const [displayed, setDisplayed] = useState('');

  useEffect(() => {
    setDisplayed('');
    if (!text) {
      return undefined;
    }

    let index = 0;
    const interval = setInterval(() => {
      index += 1;
      setDisplayed(text.slice(0, index));
      if (index >= text.length) {
        clearInterval(interval);
      }
    }, speed);

    return () => clearInterval(interval);
  }, [text, speed]);

  return displayed;
};

