    high: 'Resists rushing to conclusions',
  },
  Candid: {
    low: 'Provides honest feedback',
    medium: 'Points out uncomfortable truths',
    high: 'Delivers blunt assessments',
  },
};

export const Step4SpecialTraits: React.FC = () => {
  const { specialTraits, toggleTrait } = usePersonaForgeStore();

  const selectedTraits = specialTraits;
  const fillPercent = Math.min(100, (selectedTraits.length / MAX_TRAITS) * 100);

  const handleToggleTrait = (trait: SpecialTrait) => {
    toggleTrait(trait);
  };

  const isTraitSelected = (trait: SpecialTrait) => {
    return selectedTraits.includes(trait);
  };

  return (
    <div className="space-y-6">
      <div>
        <h3 className="text-xl font-semibold text-slate-100 mb-2">Add Special Traits</h3>
