        <p className="text-sm text-slate-400 mt-1">
          {selectedTraits.length} / {MAX_TRAITS} selected
        </p>
      </div>

      <div className="flex flex-wrap gap-3">
        {SPECIAL_TRAITS.map((trait) => {
          const isSelected = isTraitSelected(trait);
          const isDisabled = !isSelected && selectedTraits.length >= MAX_TRAITS;

          return (
            <button
              key={trait}
              onClick={() => handleToggleTrait(trait)}
              disabled={isDisabled}
              aria-label={`Toggle trait ${trait}`}
              className={`px-4 py-2 rounded-full border-2 transition-all font-medium text-sm ${
                isSelected
                  ? 'border-cyan-400 bg-cyan-500/20 text-cyan-100 shadow-lg'
                  : isDisabled
                  ? 'border-slate-700 text-slate-500 cursor-not-allowed'
                  : 'border-slate-600 text-slate-200 hover:border-cyan-300 hover:shadow-md'
              }`}
            >
              {trait}
            </button>
          );
        })}
      </div>

      <div className="rounded-xl border border-slate-700/70 bg-slate-900/60 p-5">
        <div className="flex items-center justify-between mb-3">
          <h4 className="font-semibold text-slate-100">Friction Bars</h4>
          <span className="text-xs text-slate-400">Fills as traits are added</span>
        </div>
        <div className="space-y-3">
          {[
            { label: 'Low', color: 'bg-emerald-400' },
            { label: 'Medium', color: 'bg-amber-400' },
            { label: 'High', color: 'bg-rose-400' },
          ].map((bar) => (
            <div key={bar.label} className="space-y-1">
              <div className="flex justify-between text-xs font-medium text-slate-400">
                <span>{bar.label} friction</span>
                <span>{Math.round(fillPercent)}%</span>
              </div>
              <div className="h-2 w-full rounded-full bg-slate-800">
                <div
                  className={`h-2 rounded-full transition-all ${bar.color}`}
                  style={{ width: `${fillPercent}%` }}
                />
              </div>
            </div>
          ))}
        </div>
      </div>

      {selectedTraits.length > 0 && (
        <div className="mt-6 p-6 bg-gradient-to-br from-indigo-900/40 to-slate-900/70 rounded-xl border border-indigo-400/40">
          <h4 className="font-semibold text-slate-100 mb-4">Friction Profile Preview</h4>
          <div className="space-y-4">
            {(['low', 'medium', 'high'] as const).map((level) => (
              <div key={level} className="space-y-2">
                <div className="flex items-center gap-2">
                  <div
                    className={`w-3 h-3 rounded-full ${
                      level === 'low'
