import React from 'react';
import { usePersonaForgeStore } from '@/stores/usePersonaForgeStore';
import { BehavioralSlider } from '@/types/persona-forge';

interface SliderConfig {
  key: keyof BehavioralSlider;
  leftLabel: string;
  rightLabel: string;
  description: string;
}

const sliderConfigs: SliderConfig[] = [
  {
    key: 'analysis_creativity',
    leftLabel: 'Analysis',
    rightLabel: 'Creativity',
    description: 'Balance between systematic analysis and creative exploration',
  },
  {
    key: 'support_decisiveness',
    leftLabel: 'Support',
    rightLabel: 'Decisiveness',
    description: 'Balance between collaborative support and decisive direction',
  },
  {
    key: 'risk_caution',
    leftLabel: 'Risk',
    rightLabel: 'Caution',
    description: 'Balance between bold action and careful deliberation',
  },
];

const getFrictionBehaviors = (sliders: BehavioralSlider) => {
  const analysis = Math.round(((sliders.analysis_creativity + 1) / 2) * 100);
  const decisiveness = Math.round(((sliders.support_decisiveness + 1) / 2) * 100);
  const caution = Math.round(((sliders.risk_caution + 1) / 2) * 100);

  return {
    low: [
      analysis < 40 ? 'Provides data-driven recommendations' : 'Offers creative possibilities',
      decisiveness > 60 ? 'Makes clear recommendations' : 'Asks for user input',
      caution > 60 ? 'Highlights potential issues' : 'Encourages exploration',
    ],
    medium: [
      analysis < 40 ? 'Requests supporting evidence' : 'Explores alternative perspectives',
      decisiveness > 60 ? 'States preferred approach' : 'Presents multiple options',
      caution > 60 ? 'Suggests risk mitigation' : 'Acknowledges tradeoffs',
    ],
    high: [
      analysis < 40 ? 'Demands rigorous validation' : 'Challenges conventional approaches',
      decisiveness > 60 ? 'Insists on specific direction' : 'Requires user decision',
      caution > 60 ? 'Blocks risky actions' : 'Pushes for bold moves',
    ],
  };
};

export const Step5BehavioralTuning: React.FC = () => {
  const { behavioralSliders, updateSlider } = usePersonaForgeStore();

  const sliders = behavioralSliders;

  const handleSliderChange = (key: keyof BehavioralSlider, value: number) => {
    const normalized = (value - 50) / 50;
    updateSlider(key, normalized);
  };

  const frictionBehaviors = getFrictionBehaviors(sliders);

  return (
    <div className="space-y-6">
      <div>
        <h3 className="text-xl font-semibold text-slate-100 mb-2">Behavioral Tuning</h3>
        <p className="text-slate-300">
          Fine-tune how this persona balances competing behavioral dimensions.
        </p>
      </div>

      <div className="space-y-8">
        {sliderConfigs.map((config) => {
          const normalizedValue = sliders[config.key];
          const displayValue = Math.round(((normalizedValue + 1) / 2) * 100);

          return (
            <div key={config.key} className="space-y-3">
              <div className="flex justify-between items-center">
                <span className="text-sm font-medium text-slate-200">{config.leftLabel}</span>
                <span className="text-xs text-slate-400">{config.description}</span>
                <span className="text-sm font-medium text-slate-200">{config.rightLabel}</span>
              </div>

              <div className="relative">
                <input
                  type="range"
                  min="0"
                  max="100"
                  value={displayValue}
                  onChange={(e) => handleSliderChange(config.key, Number(e.target.value))}
                  aria-label={`${config.leftLabel} to ${config.rightLabel} slider`}
                  className="w-full h-2 rounded-lg appearance-none cursor-pointer slider-thumb bg-slate-800"
                  style={{
                    background: `linear-gradient(to right, rgb(59, 130, 246) 0%, rgb(217, 70, 239) ${displayValue}%, rgb(30, 41, 59) ${displayValue}%, rgb(30, 41, 59) 100%)`,
                  }}
                />
                <div
                  className="absolute top-6 text-xs font-medium text-cyan-200"
                  style={{ left: `calc(${displayValue}% - 12px)` }}
                >
                  {displayValue}
                </div>
              </div>
            </div>
          );
        })}
      </div>

      <div className="mt-8 p-6 bg-gradient-to-br from-fuchsia-900/40 to-slate-900/70 rounded-xl border border-fuchsia-400/30">
        <h4 className="font-semibold text-slate-100 mb-4">Friction Bands Preview</h4>
        <div className="space-y-4">
          {(['low', 'medium', 'high'] as const).map((level) => (
            <div key={level} className="space-y-2">
              <div className="flex items-center gap-2">
                <div
                  className={`w-3 h-3 rounded-full ${
                    level === 'low' ? 'bg-green-500' : level === 'medium' ? 'bg-yellow-500' : 'bg-red-500'
                  }`}
                />
                <span className="text-sm font-medium text-slate-200 capitalize">{level} friction</span>
              </div>
              <ul className="ml-5 space-y-1">
                {frictionBehaviors[level].map((behavior, idx) => (
                  <li key={idx} className="text-sm text-slate-300">
                    - {behavior}
                  </li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};
