{"command":"@'
import React from 'react';
import { AnimatePresence, motion } from 'framer-motion';
import { usePersonaForgeStore } from '@/stores/usePersonaForgeStore';
import { ProgressBar } from './components/ProgressBar';
import { StepNavigation } from './components/StepNavigation';
import { Step1CoreRole } from './steps/Step1CoreRole';
import { Step2ThinkingEngine } from './steps/Step2ThinkingEngine';
import { Step3VoiceProtocol } from './steps/Step3VoiceProtocol';
import { Step4SpecialTraits } from './steps/Step4SpecialTraits';
import { Step5BehavioralTuning } from './steps/Step5BehavioralTuning';
import { Step6Identity } from './steps/Step6Identity';
import { Step7Review } from './steps/Step7Review';

interface PersonaForgeModalProps {
  isOpen: boolean;
  onClose: () => void;
  onDeploy?: (personaId: string) => void;
}

const TOTAL_STEPS = 7;

const stepTransition = {
  initial: { opacity: 0, x: 24 },
  animate: { opacity: 1, x: 0 },
  exit: { opacity: 0, x: -24 },
};

export const PersonaForgeModal: React.FC<PersonaForgeModalProps> = ({
  isOpen,
  onClose,
  onDeploy,
}) => {
  const {
    currentStep,
    nextStep,
    prevStep,
    resetForge,
    canProceed,
    compilePersonaSpec,
  } = usePersonaForgeStore();

  const handleDeploy = () => {
    if (!onDeploy) {
      return;
    }
    const spec = compilePersonaSpec();
    if (spec) {
      onDeploy(spec.id);
    }
  };

  const handleNext = () => {
    if (currentStep < TOTAL_STEPS) {
      nextStep();
    } else if (currentStep === TOTAL_STEPS) {
      handleDeploy();
    }
  };

  const handlePrev = () => {
    prevStep();
  };

  const handleCancel = () => {
    resetForge();
    onClose();
  };

  const canGoNext = (): boolean => {
    return canProceed(currentStep);
  };

  const renderStep = () => {
    switch (currentStep) {
      case 1:
        return <Step1CoreRole />;
      case 2:
        return <Step2ThinkingEngine />;
      case 3:
        return <Step3VoiceProtocol />;
      case 4:
        return <Step4SpecialTraits />;
      case 5:
        return <Step5BehavioralTuning />;
      case 6:
        return <Step6Identity />;
      case 7:
        return <Step7Review onCompile={handleDeploy} />;
      default:
        return null;
    }
  };

  return (
    <AnimatePresence>
      {isOpen && (
        <motion.div
          className="fixed inset-0 z-50 flex items-center justify-center bg-slate-950/70 p-4 backdrop-blur-sm"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
        >
          <motion.div
            className="relative w-full max-w-[800px] max-h-[90vh] overflow-hidden rounded-2xl border border-slate-700/60 bg-slate-900/90 shadow-2xl backdrop-blur-xl"
            initial={{ opacity: 0, scale: 0.97, y: 20 }}
            animate={{ opacity: 1, scale: 1, y: 0 }}
            exit={{ opacity: 0, scale: 0.97, y: 10 }}
            transition={{ type: 'spring', stiffness: 220, damping: 24 }}
          >
            <div className="pointer-events-none absolute -top-24 right-10 h-48 w-48 rounded-full bg-cyan-500/10 blur-3xl" />
            <div className="pointer-events-none absolute -bottom-24 left-10 h-56 w-56 rounded-full bg-fuchsia-500/10 blur-3xl" />

            <div className="relative px-6 pt-6 pb-4 border-b border-slate-800/80">
              <div className="flex items-start justify-between gap-4">
                <div>
                  <h3 className="text-lg font-orbitron font-semibold text-slate-100 tracking-[0.2em]">
                    FORGE NEW PERSONA
                  </h3>
                  <p className="text-xs text-slate-400 mt-2">
                    Step {currentStep} of {TOTAL_STEPS}
                  </p>
                </div>
                <div className="min-w-[220px]">
                  <ProgressBar currentStep={currentStep} totalSteps={TOTAL_STEPS} />
                </div>
              </div>
            </div>

            <div className="flex-1 overflow-y-auto px-6 py-6">
              <AnimatePresence mode="wait">
                <motion.div
                  key={currentStep}
                  variants={stepTransition}
                  initial="initial"
                  animate="animate"
                  exit="exit"
                  transition={{ duration: 0.25, ease: 'easeOut' }}
                >
                  {renderStep()}
                </motion.div>
              </AnimatePresence>
            </div>

            <div className="px-6 pb-6">
              <StepNavigation
                currentStep={currentStep}
                totalSteps={TOTAL_STEPS}
                canGoNext={canGoNext()}
                onPrev={handlePrev}
                onNext={handleNext}
                onCancel={handleCancel}
                isLastStep={currentStep === TOTAL_STEPS}
              />
            </div>
          </motion.div>
        </motion.div>
      )}
    </AnimatePresence>
  );
};
