import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';

interface StepNavigationProps {
  currentStep: number;
  totalSteps: number;
  canGoNext: boolean;
  onPrev: () => void;
  onNext: () => void;
  onCancel: () => void;
  isLastStep?: boolean;
}

export const StepNavigation: React.FC<StepNavigationProps> = ({
  currentStep,
  totalSteps,
  canGoNext,
  onPrev,
  onNext,
  onCancel,
  isLastStep = false,
}) => {
  const [shake, setShake] = useState(false);

  useEffect(() => {
    if (!shake) {
      return undefined;
    }
    const timer = setTimeout(() => setShake(false), 420);
    return () => clearTimeout(timer);
  }, [shake]);

  const triggerShake = () => {
    setShake(true);
  };

  return (
    <div className="flex items-center justify-between pt-6 border-t border-slate-800/80">
      <div className="flex gap-3">
        <button
          onClick={onCancel}
          aria-label="Cancel persona forge"
          className="px-4 py-2 text-sm font-medium text-slate-300 hover:text-slate-100 transition-colors"
        >
          Cancel
        </button>
        {currentStep > 1 && (
          <button
            onClick={onPrev}
            aria-label="Go back"
            className="px-4 py-2 text-sm font-medium text-cyan-200 hover:text-cyan-100 transition-colors"
          >
            Back
          </button>
        )}
      </div>
      <div className="relative">
        <motion.button
          type="button"
          onClick={onNext}
          disabled={!canGoNext}
          aria-label={isLastStep ? 'Deploy persona' : 'Continue'}
          animate={shake ? { x: [0, -6, 6, -4, 4, 0] } : { x: 0 }}
          transition={{ duration: 0.4 }}
          className={`px-6 py-2 text-sm font-semibold rounded-lg transition-all ${
            canGoNext
              ? 'bg-gradient-to-r from-cyan-400 via-fuchsia-400 to-amber-300 text-slate-950 hover:shadow-lg hover:scale-105'
              : 'bg-slate-700 text-slate-400 cursor-not-allowed'
          }`}
        >
          {isLastStep ? 'Deploy Persona' : 'Continue'}
        </motion.button>
        {!canGoNext && (
          <button
            type="button"
            tabIndex={-1}
            aria-hidden="true"
            onClick={triggerShake}
            className="absolute inset-0 rounded-lg"
          />
        )}
      </div>
    </div>
  );
};
