    const state = usePersonaForgeStore.getState();
    expect(state.currentStep).toBe(1);
    expect(state.isComplete).toBe(false);
    expect(state.coreRole).toBeNull();
    expect(state.thinkingEngine).toBeNull();
    expect(state.voiceProtocol).toBeNull();
    expect(state.specialTraits).toEqual([]);
    expect(state.behavioralSliders).toEqual({
