import React, { useState, useMemo } from 'react';
import { helpCatalog, HelpModuleId, searchHelpCatalog } from '../../data/helpCatalog';
import { Search, ChevronRight, BookOpen, Terminal, Hash, Filter } from '@/components/ui/HearthlinkIcons';
import { Input } from '@/components/ui/input';
import { cn } from '@/lib/utils';

interface HelpProps {
  isStandalone?: boolean;
}

const Help: React.FC<HelpProps> = ({ isStandalone = false }) => {
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedModuleId, setSelectedModuleId] = useState<HelpModuleId | 'all'>('all');
  const [selectedEntryId, setSelectedEntryId] = useState<string | null>(null);

  // Filter and search logic
  const filteredEntries = useMemo(() => {
    return searchHelpCatalog(searchQuery, {
      modules: selectedModuleId === 'all' ? undefined : [selectedModuleId],
    });
  }, [searchQuery, selectedModuleId]);

  // Group entries by module for the sidebar
  const groupedEntries = useMemo(() => {
    const groups: Record<string, typeof filteredEntries> = {};
    filteredEntries.forEach(entry => {
      if (!groups[entry.moduleId]) {
        groups[entry.moduleId] = [];
      }
      groups[entry.moduleId].push(entry);
    });
    return groups;
  }, [filteredEntries]);

  // Get selected entry details
  const selectedEntry = useMemo(() => {
    if (!selectedEntryId && filteredEntries.length > 0) return filteredEntries[0];
    return filteredEntries.find(e => e.id === selectedEntryId) || filteredEntries[0];
  }, [selectedEntryId, filteredEntries]);

  return (
    <div className={cn(
      "flex bg-background/50 backdrop-blur-sm border-white/10 overflow-hidden shadow-2xl",
      isStandalone ? "h-screen rounded-none border-0 mt-0" : "h-[calc(100vh-6rem)] rounded-xl border mt-4"
    )}>
      {/* Sidebar - Navigation & Filters */}
      <div className="w-80 border-r border-white/10 bg-black/20 flex flex-col">
        <div className="p-4 border-b border-white/10 space-y-4">
          <h2 className="text-xl font-orbitron font-bold text-cyan-400 tracking-wider flex items-center gap-2">
            <BookOpen className="w-5 h-5" /> HELP CENTER
          </h2>
          
          <div className="relative">
            <div className="absolute left-3 top-1/2 -translate-y-1/2 text-slate-400 pointer-events-none">
              <Search className="w-4 h-4" />
            </div>
            <Input 
              placeholder="Search guides..." 
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="pl-9 bg-slate-900/50 border-white/10 focus:border-cyan-500/50 text-sm"
            />
          </div>

          <div className="flex gap-2 overflow-x-auto pb-2 scrollbar-thin scrollbar-thumb-cyan-900/50">
            <button
               onClick={() => setSelectedModuleId('all')}
               className={cn(
                 "px-3 py-1 rounded-full text-xs font-medium whitespace-nowrap transition-colors border",
                 selectedModuleId === 'all' 
                   ? "bg-cyan-500/20 border-cyan-500/50 text-cyan-300" 
                   : "bg-transparent border-white/10 text-slate-400 hover:text-slate-200"
               )}
            >
              All
            </button>
            {helpCatalog.modules.map(mod => (
              <button
                key={mod.id}
                onClick={() => setSelectedModuleId(mod.id)}
                className={cn(
                  "px-3 py-1 rounded-full text-xs font-medium whitespace-nowrap transition-colors border",
                  selectedModuleId === mod.id
                    ? "bg-cyan-500/20 border-cyan-500/50 text-cyan-300" 
                    : "bg-transparent border-white/10 text-slate-400 hover:text-slate-200"
                )}
              >
                {mod.name.split(' ')[0]}
              </button>
            ))}
          </div>
        </div>

        <div className="flex-1 overflow-y-auto scrollbar-thin scrollbar-thumb-white/10 scrollbar-track-transparent">
          <div className="p-3 space-y-4">
            {Object.entries(groupedEntries).map(([modId, entries]) => {
              const moduleInfo = helpCatalog.modules.find(m => m.id === modId);
              return (
                <div key={modId} className="space-y-1">
                  <h3 className="px-3 text-xs font-bold text-slate-500 uppercase tracking-widest mb-2">
                    {moduleInfo?.name.split(' (')[0] || modId}
                  </h3>
                  {entries.map(entry => (
                    <button
                      key={entry.id}
                      onClick={() => setSelectedEntryId(entry.id)}
                      className={cn(
                        "w-full text-left px-3 py-2 rounded-lg text-sm transition-all flex items-center justify-between group",
                        (selectedEntry && selectedEntry.id === entry.id)
                          ? "bg-cyan-500/10 text-cyan-300"
                          : "text-slate-400 hover:bg-white/5 hover:text-slate-200"
                      )}
                    >
                      <span className="truncate">{entry.title}</span>
                      {(selectedEntry && selectedEntry.id === entry.id) && <ChevronRight className="w-4 h-4 opacity-50" />}
                    </button>
                  ))}
                </div>
              );
            })}
            
            {filteredEntries.length === 0 && (
               <div className="p-4 text-center text-slate-500 text-sm">
                 No matches found for "{searchQuery}"
               </div>
            )}
          </div>
        </div>
      </div>

      {/* Main Content Area */}
      <div className="flex-1 flex flex-col h-full bg-slate-900/30">
        {selectedEntry ? (
          <>
            {/* Entry Header */}
            <div className="p-8 border-b border-white/10 bg-gradient-to-b from-black/20 to-transparent">
              <div className="flex items-center gap-2 text-xs text-cyan-500/70 font-mono mb-4 uppercase tracking-wider">
                <span>{helpCatalog.modules.find(m => m.id === selectedEntry.moduleId)?.name}</span>
                <ChevronRight className="w-3 h-3" />
                <span>{selectedEntry.title}</span>
              </div>
              <h1 className="text-3xl font-bold text-white mb-2">{selectedEntry.title}</h1>
              <p className="text-lg text-slate-300 leading-relaxed max-w-3xl">{selectedEntry.description}</p>
              
              <div className="flex flex-wrap gap-2 mt-4">
                 {selectedEntry.tags.map(tag => (
                   <span key={tag} className="px-2 py-0.5 rounded text-[10px] uppercase font-bold tracking-wider bg-slate-800 text-slate-400 border border-white/5">
                     <Hash className="w-3 h-3 inline-block mr-1 opacity-50" />
                     {tag}
                   </span>
                 ))}
              </div>
            </div>

            {/* Entry Content */}
            <div className="flex-1 overflow-y-auto p-8 scrollbar-thin scrollbar-thumb-white/10 scrollbar-track-transparent">
              <div className="max-w-3xl space-y-8 pb-20">
                
                {/* Actions (How To) */}
                <section className="space-y-4">
                  <h3 className="text-sm font-bold text-cyan-400 uppercase tracking-widest flex items-center gap-2">
                    <div className="w-1 h-4 bg-cyan-500 rounded-full" />
                    How to Use
                  </h3>
                  <div className="space-y-3">
                    {selectedEntry.actions.map((action, idx) => (
                      <div key={idx} className="flex gap-4 p-4 rounded-xl bg-white/5 border border-white/5 hover:border-cyan-500/20 transition-colors">
                        <div className="flex-shrink-0 w-6 h-6 rounded-full bg-cyan-500/20 text-cyan-300 flex items-center justify-center text-xs font-bold font-mono border border-cyan-500/30">
                          {idx + 1}
                        </div>
                        <p className="text-slate-300 text-sm leading-relaxed">{action}</p>
                      </div>
                    ))}
                  </div>
                </section>

                {/* Shortcuts */}
                {selectedEntry.shortcuts && selectedEntry.shortcuts.length > 0 && (
                  <section className="space-y-4">
                    <h3 className="text-sm font-bold text-purple-400 uppercase tracking-widest flex items-center gap-2">
                      <Terminal className="w-4 h-4" />
                      Shortcuts
                    </h3>
                    <div className="grid grid-cols-1 sm:grid-cols-2 gap-3">
                      {selectedEntry.shortcuts.map((shortcut, idx) => (
                        <div key={idx} className="flex items-center justify-between p-3 rounded-lg bg-slate-900/50 border border-purple-500/20">
                           <span className="text-sm text-slate-400">Action {idx + 1}</span>
                           <kbd className="px-2 py-1 rounded bg-slate-800 border border-slate-700 text-xs font-mono text-purple-300 shadow-sm">
                             {shortcut}
                           </kbd>
                        </div>
                      ))}
                    </div>
                  </section>
                )}

                {/* Context Hints */}
                {selectedEntry.contexts && selectedEntry.contexts.length > 0 && (
                   <div className="p-4 rounded-lg bg-yellow-500/5 border border-yellow-500/10 flex gap-3">
                      <Filter className="w-5 h-5 text-yellow-500/50 flex-shrink-0" />
                      <div className="space-y-1">
                        <h4 className="text-sm font-medium text-yellow-500/80">Context Awareness</h4>
                        <p className="text-xs text-yellow-500/60 leading-relaxed">
                          Agents recognize this topic when you are navigating: 
                          <span className="font-mono ml-1 text-yellow-400">{selectedEntry.contexts.join(', ')}</span>
                        </p>
                      </div>
                   </div>
                )}

              </div>
            </div>
          </>
        ) : (
          <div className="flex-1 flex flex-col items-center justify-center text-center p-8 opacity-50">
             <BookOpen className="w-16 h-16 text-slate-700 mb-4" />
             <h3 className="text-xl font-bold text-slate-500">Select a topic</h3>
             <p className="text-slate-600">Choose a guide from the sidebar to view details</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Help;

