/**
 * Floating Action Button (FAB) Component
 *
 * Provides quick access to the Quick Capture feature with keyboard shortcut (Cmd+E / Ctrl+E).
 * Fixed to bottom-right corner with hover animations.
 * Now uses PremiumButton for consistent theming.
 *
 * @version 2.0.0
 * @date 2025-11-30
 */

import React, { useEffect, useCallback } from 'react';
import { Button } from '@/components/ui/button';

interface FloatingActionButtonProps {
  onClick: () => void;
  tooltip?: string;
}

export const FloatingActionButton: React.FC<FloatingActionButtonProps> = ({
  onClick,
  tooltip = 'Quick Capture (Cmd+E)'
}) => {
  // Handle keyboard shortcut: Cmd+E (Mac) / Ctrl+E (Windows)
  const handleKeyDown = useCallback((e: KeyboardEvent) => {
    // Check for Cmd/Ctrl + E combination
    if ((e.metaKey || e.ctrlKey) && e.key === 'e') {
      e.preventDefault();
      onClick();
    }
  }, [onClick]);

  useEffect(() => {
    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [handleKeyDown]);

  return (
    <Button
      onClick={onClick}
      variant="gradient"
      size="icon"
      className="fixed bottom-6 right-6 w-14 h-14 rounded-full z-50 hover:scale-110"
      aria-label={tooltip}
      title={tooltip}
    >
      <span className="text-sm font-semibold tracking-wide" aria-hidden="true">
        QC
      </span>
    </Button>
  );
};

export default FloatingActionButton;

