import React, { useState, useEffect, useCallback, useMemo, useRef } from 'react';
import { Rnd } from 'react-rnd';
import { Activity, Brain, Heart, Zap, Shield, GripHorizontal, ArrowLeft, RefreshCw } from '@/components/ui/HearthlinkIcons';
import { AliceBridge, BehavioralSignal } from '../../bridges/AliceBridge';
import { MetricCard } from '@/components/shared/premium/MetricCard';
import type { AliceInsight, AliceBehavioralProfile } from '../../types/agents';
import { BackToLauncher } from '../shared/BackToLauncher';

// --- Types & Interfaces ---

export interface AliceDashboardProps {
  userId?: string;
  onBackToLauncher?: () => void;
}

// MetricCard consolidated to shared component
interface LayoutItem {
  id: string;
  x: number;
  y: number;
  w: number;
  h: number;
  zIndex: number;
}

const BiometricStatusCard: React.FC = () => {
  const [heartRate] = useState(Math.floor(Math.random() * 41) + 72); // 72-112 BPM

  return (
    <div data-testid="biometric-status-card" className="module-card p-4 bg-slate-900/80 border border-violet-500/20 rounded-xl">
      <h3 className="text-sm font-medium mb-2 text-slate-200">Biometric Status</h3>
      <div className="flex items-center gap-4">
        <div data-testid="heart-rate-display" className="text-2xl font-bold text-slate-100">
          {heartRate} BPM
        </div>
        <span data-testid="biometric-source" className="text-xs text-slate-400">
          Simulation
        </span>
      </div>
    </div>
  );
};

// --- Main Component ---

export const AliceDashboard: React.FC<AliceDashboardProps> = ({ userId = 'default-user', onBackToLauncher }) => {
  // Data State
  const [insights, setInsights] = useState<AliceInsight[]>([]);
  const [profile, setProfile] = useState<AliceBehavioralProfile | null>(null);
  const [signals, setSignals] = useState<BehavioralSignal[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [successMessage, setSuccessMessage] = useState<string | null>(null);

  // Layout State
  const [layout, setLayout] = useState<LayoutItem[]>([
    { id: 'focus', x: 20, y: 80, w: 300, h: 160, zIndex: 10 },
    { id: 'stress', x: 340, y: 80, w: 300, h: 160, zIndex: 10 },
    { id: 'load', x: 660, y: 80, w: 300, h: 160, zIndex: 10 },
    { id: 'energy', x: 980, y: 80, w: 300, h: 160, zIndex: 10 },
    { id: 'analysis', x: 20, y: 260, w: 800, h: 400, zIndex: 10 },
    { id: 'interventions', x: 840, y: 260, w: 440, h: 400, zIndex: 10 },
  ]);
  const [draggingId, setDraggingId] = useState<string | null>(null);
  const maxZIndexRef = useRef(10);

  // --- Data Logic ---

  const createBridge = useCallback(() => new AliceBridge({ userId }), [userId]);

  const loadAliceData = useCallback(async () => {
    setError(null);
    try {
      // Don't set loading true on refresh to avoid flickering if we have data
      if (insights.length === 0) setLoading(true);
      
      const bridge = createBridge();
      await bridge.initialize();
      const [insightsResponse, profileResponse, signalsResponse] = await Promise.all([
        bridge.getInsights(),
        bridge.getProfile(),
        bridge.getSignals(10),
      ]);

      if (insightsResponse.success && insightsResponse.data) {
        setInsights(insightsResponse.data);
      } else if (!insightsResponse.success) {
        console.warn('Failed to load insights:', insightsResponse.error);
        // Don't block UI on partial failure
      }

      if (profileResponse.success && profileResponse.data) {
        setProfile(profileResponse.data);
      } else if (!profileResponse.success) {
        console.warn('Failed to load profile:', profileResponse.error);
      }

      if (signalsResponse.success && signalsResponse.data) {
        setSignals(signalsResponse.data);
      } else if (!signalsResponse.success) {
        console.warn('Failed to load signals:', signalsResponse.error);
      }

    } catch (err: unknown) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to load Alice data.';
      setError(errorMessage);
    } finally {
      setLoading(false);
    }
  }, [createBridge, insights.length]);

  useEffect(() => {
    loadAliceData();
  }, [loadAliceData]);

  useEffect(() => {
    if (successMessage) {
      const timer = setTimeout(() => setSuccessMessage(null), 3000);
      return () => clearTimeout(timer);
    }
  }, [successMessage]);

  const handleApplyRecommendation = async (insight: AliceInsight) => {
    setError(null);
    try {
      const bridge = createBridge();
      await bridge.initialize();
      const response = await bridge.generateInsights({
        applied_recommendation: insight.observation,
        insight_id: insight.insight_id,
      });
      if (response.success) {
        setSuccessMessage(`Recommendation applied successfully!`);
        loadAliceData();
      } else {
        setError(response.error || 'Failed to apply recommendation.');
      }
    } catch (err: unknown) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to apply recommendation.';
      setError(errorMessage);
    }
  };

  const handleFeedback = async (insight: AliceInsight, helpful: boolean) => {
    setError(null);
    try {
      const bridge = createBridge();
      await bridge.initialize();
      if (!helpful) {
        const response = await bridge.dismissInsight(insight.insight_id);
        if (response.success) {
          setSuccessMessage(`Feedback recorded. Insight dismissed.`);
          loadAliceData();
        } else {
          setError(response.error || 'Failed to record feedback.');
        }
      } else {
        setSuccessMessage(`Feedback recorded as helpful. Thank you!`);
      }
    } catch (err: unknown) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to record feedback.';
      setError(errorMessage);
    }
  };
 
  const handleSimulatePulse = async () => {
    setError(null);
    try {
      const bridge = createBridge();
      await bridge.initialize();
      
      // Check for consent first
      const consentCheck = await bridge.checkConsent(['behavioral_insights']);
      if (consentCheck.success && !consentCheck.data) {
        console.log('[AliceDashboard] Consent missing, recording default consent...');
        await bridge.recordConsent(true, ['behavioral_insights'], 'Auto-granted for pulse simulation');
      }
      
      // Generate some semi-random data to see the math work
      const obs = {
        transcript: "Working on code review and some documentation.",
        completed_tasks: Math.floor(Math.random() * 5),
        total_tasks: 5,
        interruptions: Math.floor(Math.random() * 3),
        context_switches: Math.floor(Math.random() * 4),
        sentiment: (Math.random() * 2) - 1.0, // -1 to 1
        self_reported_stress: Math.floor(Math.random() * 100),
        self_reported_energy: Math.floor(Math.random() * 100),
        focus_rating: Math.floor(Math.random() * 100),
      };
      
      const response = await bridge.recordObservation(obs);
      if (response.success) {
        setSuccessMessage(`Observation recorded! Pulse updated.`);
        loadAliceData();
      } else {
        setError(response.error || 'Failed to record observation.');
      }
    } catch (err: unknown) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to simulate pulse.';
      setError(errorMessage);
    }
  };
 
  // --- Layout Logic ---

  const updateLayout = useCallback((id: string, updates: Partial<LayoutItem>) => {
    setLayout(prev => prev.map(item => 
      item.id === id ? { ...item, ...updates } : item
    ));
  }, []);

  const handleDragStart = useCallback((id: string) => {
    setDraggingId(id);
    const newZIndex = ++maxZIndexRef.current;
    updateLayout(id, { zIndex: newZIndex });
  }, [updateLayout]);

  const handleDragEnd = useCallback(() => {
    setDraggingId(null);
  }, []);

  const createDragHandlers = useCallback((id: string) => ({
    onDragStart: () => handleDragStart(id),
    onDragStop: (e: any, d: { x: number; y: number }) => {
      updateLayout(id, { x: d.x, y: d.y });
      handleDragEnd();
    },
    onResizeStart: () => handleDragStart(id),
    onResizeStop: (e: any, direction: any, ref: HTMLElement, delta: any, position: { x: number; y: number }) => {
      updateLayout(id, { 
        w: parseInt(ref.style.width), 
        h: parseInt(ref.style.height),
        x: position.x,
        y: position.y
      });
      handleDragEnd();
    },
  }), [handleDragStart, handleDragEnd, updateLayout]);

