import React, { useState, useEffect, useRef, useCallback } from "react";
import { VaultBridge, type MemorySlice } from "../../bridges/VaultBridge";
import { LoadingSpinner } from "../shared/LoadingSpinner";
import { X, Search, Sparkles, List } from "@/components/ui/HearthlinkIcons";
import { emitTutorialEvent } from "@/utils/tutorialEvents";

type SearchMode = "keyword" | "semantic";

interface MemorySearchBarProps {
  userId: string;
  onMemorySelect?: (memory: MemorySlice) => void;
}

type MemorySliceWithScore = MemorySlice & { similarity_score?: number };

export const MemorySearchBar: React.FC<MemorySearchBarProps> = ({
  userId,
  onMemorySelect,
}) => {
  const [query, setQuery] = useState("");
  const [results, setResults] = useState<MemorySliceWithScore[]>([]);
  const [isOpen, setIsOpen] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [selectedIndex, setSelectedIndex] = useState(0);
  const [searchError, setSearchError] = useState<string | null>(null);
  const [searchMode, setSearchMode] = useState<SearchMode>("keyword");
  const inputRef = useRef<HTMLInputElement>(null);
  const containerRef = useRef<HTMLDivElement>(null);
  const debounceTimerRef = useRef<NodeJS.Timeout | null>(null);
  const hasEmittedSearchEvent = useRef(false);

  // Debounced search function
  useEffect(() => {
    if (!query.trim()) {
      setResults([]);
      setSelectedIndex(0);
      return;
    }

    if (!hasEmittedSearchEvent.current) {
      emitTutorialEvent("hl:event:vault:search");
      hasEmittedSearchEvent.current = true;
    }

    // Clear previous debounce timer
    if (debounceTimerRef.current) {
      clearTimeout(debounceTimerRef.current);
    }

    setIsLoading(true);
    setSearchError(null);

    debounceTimerRef.current = setTimeout(async () => {
      try {
        if (searchMode === "semantic") {
          // Semantic search using RAG
          const response = await VaultBridge.searchMemoriesSemantic(
            userId,
            query,
            20, // Get more results for better ranking
            0.3, // Lower threshold to include more matches
          );

          if (!response.success) {
            throw new Error(response.error);
          }

          // Filter out very low similarity scores and limit to 10
          const filtered = response.data
            .filter(
              (r: MemorySliceWithScore) => (r.similarity_score || 0) > 0.3,
            )
            .sort(
              (a: MemorySliceWithScore, b: MemorySliceWithScore) =>
                (b.similarity_score || 0) - (a.similarity_score || 0),
            )
            .slice(0, 10);

          setResults(filtered);
          setSelectedIndex(0);
        } else {
          // Keyword search (original implementation)
          const response = await VaultBridge.getMemories(userId, {
            status: "active",
            limit: 100,
          });

          if (!response.success) {
            throw new Error(response.error);
          }

          // Parse tags from JSON string if needed
          const parseTags = (tags: string | string[] | null): string[] => {
            if (!tags) return [];
            if (Array.isArray(tags)) return tags;
            try {
              return JSON.parse(tags);
            } catch {
              return [];
            }
          };

          // Filter results by matching the query against content and tags
          const filtered = response.data.filter((memory: MemorySlice) => {
            const matchesContent = memory.content
              .toLowerCase()
              .includes(query.toLowerCase());
            const memoryTags = parseTags(memory.tags);
            const matchesTags = memoryTags.some((tag: string) =>
              tag.toLowerCase().includes(query.toLowerCase()),
            );
            return matchesContent || matchesTags;
          });

          // Sort by relevance and limit to 10 results
          const sorted = filtered
            .sort((a: MemorySlice, b: MemorySlice) => {
              // Prioritize exact word matches in content
              const aContentMatch = a.content
                .split(/\s+/)
                .some((word: string) =>
                  word.toLowerCase().includes(query.toLowerCase()),
                );
              const bContentMatch = b.content
                .split(/\s+/)
                .some((word: string) =>
                  word.toLowerCase().includes(query.toLowerCase()),
                );
              return (bContentMatch ? 1 : 0) - (aContentMatch ? 1 : 0);
            })
            .slice(0, 10);

          setResults(sorted);
          setSelectedIndex(0);
        }
      } catch (err) {
        console.error("[MemorySearchBar] Search failed:", err);
        setSearchError("Failed to search memories");
        setResults([]);
      } finally {
        setIsLoading(false);
      }
    }, 300); // 300ms debounce

    return () => {
      if (debounceTimerRef.current) {
        clearTimeout(debounceTimerRef.current);
      }
    };
  }, [query, userId, searchMode]);

  const handleSelectMemory = useCallback(
    (memory: MemorySlice) => {
      onMemorySelect?.(memory);
      setIsOpen(false);
      setQuery("");
      setResults([]);
      setSelectedIndex(0);
    },
    [onMemorySelect],
  );

  // Keyboard shortcuts and navigation
  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      // Cmd+K or Ctrl+K to focus search
      if ((e.metaKey || e.ctrlKey) && e.key === "k") {
        e.preventDefault();
        setIsOpen(true);
        inputRef.current?.focus();
      }

      // Only handle navigation keys if search is open
      if (!isOpen) return;

      // Escape to close
      if (e.key === "Escape") {
        e.preventDefault();
        setIsOpen(false);
        setQuery("");
        setResults([]);
        setSelectedIndex(0);
      }

      // Arrow Down - navigate down
      if (e.key === "ArrowDown" && results.length > 0) {
        e.preventDefault();
        setSelectedIndex((prev) => Math.min(prev + 1, results.length - 1));
      }

      // Arrow Up - navigate up
      if (e.key === "ArrowUp" && results.length > 0) {
        e.preventDefault();
        setSelectedIndex((prev) => Math.max(prev - 1, 0));
      }

      // Enter to select
      if (e.key === "Enter" && results[selectedIndex]) {
        e.preventDefault();
        handleSelectMemory(results[selectedIndex]);
      }
    };

