const FRAME_PADDING = 16;
const TILE_GAP = 12;
const MIN_Y_BUFFER = 120;
const ROW_HEIGHT = 180;
const ROW_GAP = 24;
const ROW0_Y = FRAME_PADDING + MIN_Y_BUFFER;
const ROW1_Y = ROW0_Y + ROW_HEIGHT + ROW_GAP;

interface DashboardLayoutItem {
  id: DashboardTileId;
  x: number;
  y: number;
  width: number;
  height: number;
  locked?: boolean;
}

const DEFAULT_DASHBOARD_LAYOUT: DashboardLayoutItem[] = [
  {
    id: "counter-tasks",
    x: FRAME_PADDING,
    y: ROW0_Y,
    width: 240,
    height: 180,
    locked: true,
  },
  {
    id: "counter-schedule",
    x: 272,
    y: ROW0_Y,
    width: 240,
    height: 180,
    locked: true,
  },
  {
