              className="px-3 py-1.5 text-xs uppercase tracking-[0.14em] bg-[#0ef]/10 text-[#0ef] border border-[#0ef]/40 rounded shadow-[0_0_10px_rgba(0,240,255,0.14)] hover:bg-[#0ef]/16 transition-all"
            >
              New Task
            </button>
          </div>
        </div>
        <div className="flex-1 min-h-0 overflow-hidden">
          <EnhancedTaskManager />
        </div>
      </div>
    ),
    [frameStyle],
  );

  const renderCalendarView = useCallback(
    () => (
      <div
        className="h-full bg-alden-panel ring-1 ring-[rgba(255,255,255,0.2)] rounded-xl p-5 w-full"
        style={frameStyle}
      >
        <div className="flex items-center justify-between mb-3">
          <div className="flex items-center gap-4">
            <h2 className="text-sm font-semibold text-alden-gold uppercase tracking-widest font-orbitron-force">
              Calendars
            </h2>
            <div className="flex items-center gap-2 bg-alden-bg/30 p-1 rounded-lg border border-white/5">
              {["Personal", "Work", "Family"].map((cal, i) => (
                <button
                  key={cal}
                  className={`px-3 py-1 text-[10px] uppercase tracking-wider rounded ${i === 0 ? "bg-alden-cyan/20 text-alden-cyan border border-alden-cyan/30" : "text-[#9fb4d6] hover:bg-white/5"}`}
                >
                  {cal}
                </button>
              ))}
              <button
                className="w-6 h-6 flex items-center justify-center text-[#9fb4d6] hover:text-white"
                aria-label="Add new calendar"
              >
                <Plus size={12} />
              </button>
            </div>
          </div>
          <button
            onClick={() => setIsEventModalOpen(true)}
            className="px-3 py-1.5 text-xs uppercase tracking-[0.14em] bg-[#0ef]/10 text-[#0ef] border border-[#0ef]/40 rounded shadow-[0_0_10px_rgba(0,240,255,0.14)] hover:bg-[#0ef]/16 transition-all"
          >
            New Event
          </button>
        </div>
        <CalendarView events={events} onEventClick={setSelectedEvent} />
      </div>
    ),
    [events, frameStyle],
  );

  const renderTerminalView = useCallback(
    () => (
      <div
        className="h-full flex flex-col bg-alden-panel ring-1 ring-[rgba(255,255,255,0.2)] rounded-xl overflow-hidden w-full p-4"
        style={frameStyle}
      >
        <AldenTerminal
          userId={userId}
          className="flex-1 min-h-0 flex flex-col"
        />
      </div>
    ),
    [frameStyle, userId],
  );

  const renderSkillsView = useCallback(
    () => (
      <div
        className="h-full bg-alden-panel ring-1 ring-[rgba(255,255,255,0.2)] rounded-xl p-3 flex flex-col w-full"
        style={frameStyle}
      >
        <div className="flex items-center justify-between mb-4 shrink-0">
          <h2 className="text-base text-alden-gold uppercase tracking-widest flex items-center gap-3 font-orbitron-force">
            <Brain size={16} />
            Skill System
          </h2>
          <span className="text-[10px] text-slate-500 uppercase tracking-widest">
            Library & Execution
          </span>
        </div>
        <div className="grid grid-cols-1 xl:grid-cols-2 gap-3 flex-1 min-h-0 overflow-hidden">
          <div className="flex flex-col gap-4 min-h-0 overflow-hidden">
            <div className="bg-black/20 border border-white/10 rounded-xl p-4 shrink-0">
              <SkillSystemPanel />
            </div>
            <div className="bg-black/20 border border-white/10 rounded-xl p-4 flex-1 min-h-0 overflow-auto">
              <SkillMarketplace
                mode="library"
                onCheckout={(id) => console.log("Checking out:", id)}
                initialSearch={skillSearch}
              />
            </div>
          </div>
          <div className="bg-black/20 border border-white/10 rounded-xl p-4 min-h-0 overflow-auto">
            <SkillExecutionMonitor />
          </div>
        </div>
      </div>
    ),
    [frameStyle, skillSearch],
  );

  const handleEmailAccountClick = useCallback((accountId: string) => {
    setSelectedEmailAccount(accountId);
    setActiveView("email");
  }, []);

  const handleSkillClick = useCallback((skillId: string) => {
    setSkillSearch(skillId);
    setActiveView("skills");
  }, []);

  const handleSmartTaskApply = useCallback(
    (task: { taskId: string; title: string; reason: string }) => {
      console.log("Applying smart task:", task);
      setNewTaskData({
        title: task.title,
        description: `Suggested because: ${task.reason}`,
      });
      setIsTaskModalOpen(true);
    },
    [],
  );

  const renderOverview = () => (
    <AldenDashboardGrid
      stats={stats}
      tasks={tasks}
      events={events}
      onTaskClick={setSelectedTask}
      onEventClick={setSelectedEvent}
      onNewTask={() => {
        setNewTaskData({});
        setIsTaskModalOpen(true);
      }}
      onNewEvent={() => setIsEventModalOpen(true)}
      onEmailAccountClick={handleEmailAccountClick}
      onSkillClick={handleSkillClick}
      onSmartTaskApply={handleSmartTaskApply}
      pendingApprovalsCount={pendingApprovals.length}
      onOpenApprovals={() => setShowApprovalsModal(true)}
    />
  );

  const renderEmailView = useCallback(
    () => (
      <div
        className="h-full flex flex-col bg-alden-panel ring-1 ring-[rgba(255,255,255,0.2)] rounded-xl overflow-hidden w-full p-5"
        style={frameStyle}
        data-testid="email-view-container"
      >
        <div className="flex items-center justify-between mb-3 shrink-0">
          <h2 className="text-sm font-semibold text-alden-gold uppercase tracking-widest font-orbitron-force flex items-center gap-3">
            <Mail size={16} />
            Email System
          </h2>
        </div>
        <EmailManager
          userId={userId}
          className="flex-1 min-h-0"
          activeAccountId={selectedEmailAccount}
        />
      </div>
    ),
    [frameStyle, userId, selectedEmailAccount],
  );

  const renderContent = useCallback(() => {
    if (activeView === "chat") {
      return (
        <UnifiedChatContainer
          userId={userId}
          moduleId="alden"
          residentPersonaId={residentPersonaId}
          onClose={() => setActiveView("overview")}
          className="h-full w-full chat-container"
        />
      );
    }

    if (activeView === "tasks") return renderTasksView();
    if (activeView === "calendar") return renderCalendarView();
    if (activeView === "email") return renderEmailView();
    if (activeView === "terminal") return renderTerminalView();
    if (activeView === "skills") return renderSkillsView();
    return renderOverview();
  }, [
    activeView,
    userId,
    residentPersonaId,
    renderTasksView,
    renderCalendarView,
    renderEmailView,
    renderTerminalView,
    renderSkillsView,
    renderOverview,
  ]);

  const renderApprovalsView = useCallback(
    () => (
      <div
        className="h-full bg-alden-panel ring-1 ring-[rgba(255,255,255,0.2)] rounded-xl p-5 w-full overflow-auto"
        style={frameStyle}
      >
        <ApprovalQueue
          pendingActions={pendingApprovals}
          onApprove={handleApproveAction}
          onReject={handleRejectAction}
          className="max-w-4xl mx-auto"
        />
      </div>
    ),
    [frameStyle, pendingApprovals, handleApproveAction, handleRejectAction],
  );

  const tabs: UnifiedTabItem[] = useMemo(
    () => [
      { id: "overview", label: "Overview" },
      { id: "tasks", label: "Tasks" },
      { id: "calendar", label: "Calendar" },
      { id: "email", label: "Email" },
      { id: "skills", label: "Skills" },
      { id: "terminal", label: "Terminal" },
      { id: "chat", label: "Chat" },
    ],
    [],
  );
  const tabSelection = useMemo(
    () => tabs.find((tab) => tab.id === activeView)?.id || tabs[0]?.id,
    [tabs, activeView],
  );

  const handleNavigation = useCallback(
    (moduleName: string) => {
      const lower = moduleName.toLowerCase();
      if (lower === "alden") {
        setActiveView("overview");
        return true;
      }
      if (lower === "chat") {
        setActiveView("chat");
        return true;
      }
      if (lower === "settings" || moduleName === "/settings") {
        setShowSettings(true);
        return true;
      }

      if (onNavigateToModule) {
        onNavigateToModule(moduleName);
        return true;
      }

      if (onBackToLauncher) {
        onBackToLauncher();
        return true;
      }

      return false;
    },
    [onNavigateToModule, onBackToLauncher],
  );

  const sidebar = useMemo(() => {
    if (activeView === "chat" || !residentPersona) return null;

    return (
      <PrivateChannel
        residentPersona={residentPersona}
        messages={privateMessages}
        isLoading={privateLoading}
        error={privateError}
        isTyping={privateTyping}
        onSendMessage={sendPrivateMessage}
        isCollapsed={isSidebarCollapsed}
        onToggleCollapse={() => setIsSidebarCollapsed((prev) => !prev)}
        onStandardView={() => setActiveView("chat")}
        className="h-full border-l border-alden-gold/20"
        onFocusAssist={onFocusAssist}
      />
    );
  }, [
    activeView,
    residentPersona,
    isSidebarCollapsed,
    privateMessages,
    privateLoading,
    privateError,
    privateTyping,
    sendPrivateMessage,
  ]);

  return (
    <>
      <UnifiedCommandCenter
        title="Alden"
        subtitle="Command Center"
        iconPath="/assets/Alden.png"
        tabs={tabs}
        activeTab={tabSelection}
        onTabChange={handleTabChange}
        onBackToLauncher={onBackToLauncher}
        onNavigateToModule={onNavigateToModule}
        onNavigate={handleNavigation}
        statusLabel={isConnected ? "System Online" : "Offline"}
        statusTone={isConnected ? "success" : "error"}
        sidebar={sidebar}
        withFrame={false}
        titleColor="var(--color-amber-600, #d6b369)"
        subtitleColor="var(--color-white, #ffffff)"
        headerStyle={{
          background:
            "linear-gradient(90deg, var(--color-dark-900, #040912) 0%, var(--color-dark-800, #0b1628) 50%, var(--color-dark-900, #040912) 100%)",
          borderBottom: "3px solid var(--color-amber-600, #d6b369)",
          boxShadow:
            "0 8px 30px rgba(0,0,0,0.8), inset 0 -2px 0 var(--color-cyan-500-alpha, rgba(0,230,255,0.6)), inset 0 2px 0 var(--shadow-glow-gold, rgba(197,160,89,0.5))",
        }}
        fullWidthContent={true}
        sidebarCollapsed={isSidebarCollapsed}
      >
        {renderContent()}
      </UnifiedCommandCenter>

      <style>
        {`
          .alden-slate, .alden-slate * { color: #c5d2e8 !important; }
          .alden-slate .text-muted, .alden-slate .text-secondary { color: #9fb4d6 !important; }
          .alden-slate .alden-header-title { color: #d6b369 !important; }
          .alden-slate .alden-header-subtitle { color: #00e6ff !important; }
        `}
      </style>

      <CreateTaskModal
        isOpen={isTaskModalOpen}
        onClose={() => setIsTaskModalOpen(false)}
        onCreate={handleTaskCreated}
        initialTitle={newTaskData.title}
        initialDescription={newTaskData.description}
      />
      <CreateEventModal
        isOpen={isEventModalOpen}
        onClose={() => setIsEventModalOpen(false)}
        onCreate={handleEventCreated}
      />

      {selectedTask && (
        <TaskDetailModal
          task={selectedTask}
          isOpen={!!selectedTask}
          onClose={() => setSelectedTask(null)}
          onUpdateTask={handleTaskUpdate}
          onDeleteTask={handleDeleteTask}
        />
      )}

      {selectedEvent && (
        <EventDetailModal
          event={selectedEvent}
          isOpen={!!selectedEvent}
          onClose={() => setSelectedEvent(null)}
          onUpdateEvent={handleEventUpdate}
          onDeleteEvent={handleDeleteEvent}
        />
      )}

      {showSettings && (
        <div className="fixed inset-0 z-[10000] flex items-center justify-center bg-black/80 backdrop-blur-sm animate-fadeIn">
          <div className="relative w-full max-w-6xl max-h-[90vh] overflow-auto rounded-xl border border-alden-cyan/30 bg-alden-panel/95 shadow-2xl ring-1 ring-white/5">
            <SettingsPanelEnhanced onClose={() => setShowSettings(false)} />
          </div>
        </div>
      )}

      {/* HITL Approvals Modal */}
      {showApprovalsModal && (
        <div className="fixed inset-0 z-[10000] flex items-center justify-center bg-black/80 backdrop-blur-sm animate-fadeIn">
          <div className="relative w-full max-w-2xl max-h-[80vh] overflow-auto rounded-xl border border-amber-500/30 bg-slate-900/95 shadow-2xl ring-1 ring-white/5 p-6">
            <button
              onClick={() => setShowApprovalsModal(false)}
              className="absolute top-4 right-4 z-10 p-2 rounded-lg bg-slate-800 border border-slate-700 hover:text-red-400 transition-colors"
              aria-label="Close approvals"
            >
              <X className="w-5 h-5" />
            </button>
            <h2 className="text-lg font-semibold text-amber-400 mb-4 font-orbitron-force">
              Pending Approvals
            </h2>
            <ApprovalQueue
              pendingActions={pendingApprovals}
              onApprove={handleApproveAction}
              onReject={handleRejectAction}
            />
          </div>
        </div>
      )}
    </>
  );
};

