}

impl SecurityBoundary {
    pub fn new(db: Arc<RwLock<Option<DatabaseManager>>>) -> Self {
        Self {
            db,
            rate_limiter: Arc::new(RwLock::new(RateLimiter::new())),
        }
    }

    /// Initialize database schema for security logs
    pub async fn initialize_schema(&self) -> Result<(), String> {
        let db = self.db.read().await;
        let db = db.as_ref().ok_or("Database not initialized")?;

        let query = r#"
            CREATE TABLE IF NOT EXISTS synapse_security_logs (
                log_id TEXT PRIMARY KEY NOT NULL,
                plugin_id TEXT,
                connection_id TEXT,
                event_type TEXT NOT NULL,
                severity TEXT NOT NULL,
                timestamp TEXT NOT NULL,
                details TEXT NOT NULL,
                FOREIGN KEY (plugin_id) REFERENCES synapse_plugins(plugin_id),
                FOREIGN KEY (connection_id) REFERENCES synapse_connections(connection_id)
            )
        "#;

        sqlx::query(query)
            .execute(&db.write_pool)
            .await
            .map_err(|e| format!("Failed to create synapse_security_logs table: {e}"))?;

        // Create index on timestamp for efficient log queries
        let index_query = r#"
            CREATE INDEX IF NOT EXISTS idx_synapse_security_logs_timestamp
            ON synapse_security_logs(timestamp DESC)
        "#;

        sqlx::query(index_query)
            .execute(&db.write_pool)
            .await
            .map_err(|e| format!("Failed to create security logs index: {e}"))?;

        Ok(())
    }

    /// Check if a plugin has permission for a specific resource
    pub async fn check_permission(
        &self,
        plugin_id: &str,
        resource: &str,
        action: &str, // "read" or "write"
    ) -> Result<bool, String> {
        // System-level operations always have full permission
        if plugin_id == "system" {
            return Ok(true);
        }

        let db = self.db.read().await;
        let db = db.as_ref().ok_or("Database not initialized")?;

        // Get plugin permissions
        let query = r#"
            SELECT permissions
            FROM synapse_plugins
            WHERE plugin_id = ? AND enabled = 1
        "#;

        let (permissions_json,): (String,) = sqlx::query_as(query)
            .bind(plugin_id)
            .fetch_one(&db.read_pool)
            .await
            .map_err(|_| format!("Plugin {plugin_id} not found or not enabled"))?;

        let permissions: Vec<String> = serde_json::from_str(&permissions_json)
            .map_err(|e| format!("Failed to parse permissions: {e}"))?;

        // Check if permission exists
        let required_perm = format!("{action}:{resource}");
        let has_permission = permissions.iter().any(|p| p == &required_perm || p == "*");

        if !has_permission {
            // Log permission denial
            self.log_event(
                Some(plugin_id.to_string()),
                None,
                SecurityEventType::PermissionDenied,
                SecuritySeverity::Warning,
                format!("Permission denied: {action} on {resource}"),
            )
            .await?;
        }

        Ok(has_permission)
    }

    /// Check if a plugin is verified and allowed to run
    pub async fn check_verification(&self, plugin_id: &str) -> Result<bool, String> {
        let db = self.db.read().await;
        let db = db.as_ref().ok_or("Database not initialized")?;

        let query = r#"
            SELECT manifest
            FROM synapse_plugins
            WHERE plugin_id = ? AND enabled = 1
        "#;

        let (manifest_json,): (String,) = sqlx::query_as(query)
            .bind(plugin_id)
            .fetch_one(&db.read_pool)
            .await
            .map_err(|_| format!("Plugin {plugin_id} not found or not enabled"))?;

        let manifest: PluginManifest = serde_json::from_str(&manifest_json)
            .map_err(|e| format!("Failed to parse manifest: {e}"))?;

        if !manifest.verification.verified {
            self.log_event(
                Some(plugin_id.to_string()),
                None,
                SecurityEventType::OperationModeViolation, // Using this for verification failure
                SecuritySeverity::Critical,
                "Unverified vendor plugin matched fail-closed policy".to_string(),
            )
            .await?;
            return Ok(false);
        }

        Ok(true)
    }

    /// Check if network access is allowed for a URL
    pub async fn check_network_permission(
        &self,
        plugin_id: &str,
        url: &str,
    ) -> Result<bool, String> {
        // Enforce vendor verification for external network access
        if !self.check_verification(plugin_id).await? {
            return Ok(false);
        }

        let db = self.db.read().await;
        let db = db.as_ref().ok_or("Database not initialized")?;

        // Get plugin manifest
        let query = r#"
            SELECT manifest
            FROM synapse_plugins
            WHERE plugin_id = ? AND enabled = 1
        "#;

        let (manifest_json,): (String,) = sqlx::query_as(query)
            .bind(plugin_id)
            .fetch_one(&db.read_pool)
            .await
            .map_err(|_| format!("Plugin {plugin_id} not found or not enabled"))?;

        let manifest: PluginManifest = serde_json::from_str(&manifest_json)
            .map_err(|e| format!("Failed to parse manifest: {e}"))?;

        // Check network permissions
        let has_permission = manifest.permissions.network.iter().any(|pattern| {
            // Simple wildcard matching (* at end of pattern)
            if pattern.ends_with('*') {
                let prefix = &pattern[..pattern.len() - 1];
                url.starts_with(prefix)
            } else {
                url == pattern
            }
        });

        if !has_permission {
            // Log denied network access
            self.log_event(
                Some(plugin_id.to_string()),
                None,
                SecurityEventType::PermissionDenied,
                SecuritySeverity::Warning,
                format!("Network access denied: {url}"),
            )
            .await?;
        }

        Ok(has_permission)
    }

    /// Check rate limit for a plugin
    pub async fn check_rate_limit(&self, plugin_id: &str) -> Result<bool, String> {
        let mut limiter = self.rate_limiter.write().await;
        let allowed = limiter.check_limit(plugin_id);

        if !allowed {
            // Log rate limit exceeded
            self.log_event(
                Some(plugin_id.to_string()),
                None,
                SecurityEventType::RateLimitExceeded,
                SecuritySeverity::Warning,
                "Rate limit exceeded".to_string(),
            )
            .await?;
        }

        Ok(allowed)
    }

