        let query = r#"
            SELECT requests_last_minute
            FROM v_rate_limit_last_minute
            WHERE plugin_id = ? AND vendor_name = ?
        "#;

        let usage: Option<(i64,)> = sqlx::query_as(query)
            .bind(plugin_id)
            .bind(vendor_name)
            .fetch_optional(&db.read_pool)
            .await
            .map_err(|e| format!("Failed to fetch rate limit usage: {e}"))?;

        let current_usage = usage.map(|(c,)| c).unwrap_or(0) as i32;

        if current_usage >= limit_per_minute {
            self.log_rate_limit_violation(
                plugin_id,
                vendor_name,
                "per_minute",
                current_usage as i64,
                limit_per_minute as i64,
                "blocked",
            )
            .await?;
            return Ok(false);
        }

        Ok(true)
    }

