pub struct MimicChatState {
    pub chat_manager: Arc<MimicChatManager>,
    pub history: Arc<RwLock<HashMap<String, Vec<ChatMessageEntry>>>>,
    pub db: Arc<RwLock<Option<DatabaseManager>>>,
}

impl MimicChatState {
    async fn append_history(&self, user_id: &str, entry: ChatMessageEntry) -> Result<(), String> {
        let mut history = self.history.write().await;
        let entries = history.entry(user_id.to_string()).or_default();
        entries.push(entry.clone());
        if entries.len() > MAX_CHAT_HISTORY {
            let overflow = entries.len() - MAX_CHAT_HISTORY;
            entries.drain(0..overflow);
        }

        self.persist_message(&entry).await
    }

    async fn persist_message(&self, entry: &ChatMessageEntry) -> Result<(), String> {
        let db_lock = self.db.read().await;
        let db = db_lock.as_ref().ok_or("Database not initialized")?;

        let metadata = entry
            .metadata
            .as_ref()
            .map(|m| serde_json::to_string(m).unwrap_or_default());

        // Store in shared alden_chat_messages table with role='mimic'
        sqlx::query(
            "INSERT OR REPLACE INTO alden_chat_messages (id, user_id, role, content, created_at, session_id, metadata) VALUES (?, ?, ?, ?, ?, ?, ?)",
        )
        .bind(&entry.message_id)
        .bind(&entry.user_id)
        .bind(&entry.role)
        .bind(&entry.content)
        .bind(&entry.created_at)
        .bind(&entry.session_id)
        .bind(metadata)
        .execute(&db.write_pool)
        .await
        .map_err(|e| format!("Failed to persist chat message: {e}"))?;

        Ok(())
    }

    async fn load_history(
        &self,
        user_id: &str,
        limit: Option<usize>,
    ) -> Result<Vec<ChatMessageEntry>, String> {
        // Try memory cache first
        if let Some(cached) = self.history.read().await.get(user_id).cloned() {
            if limit.is_none() || cached.len() >= limit.unwrap_or(0) {
                return Ok(if let Some(limit) = limit {
                    let len = cached.len();
                    let start = len.saturating_sub(limit);
                    cached[start..].to_vec()
                } else {
                    cached
                });
            }
        }

        // Load from shared table - get all messages (Alden, Alice, Mimic, user)
        let mut query = String::from("SELECT id, user_id, role, content, created_at, session_id, metadata FROM alden_chat_messages WHERE user_id = ? ORDER BY created_at DESC");
        if limit.is_some() {
            query.push_str(" LIMIT ?");
        }

        let db_lock = self.db.read().await;
        let db = db_lock.as_ref().ok_or("Database not initialized")?;

        let mut sql_query = sqlx::query(&query).bind(user_id);
        if let Some(limit) = limit {
            sql_query = sql_query.bind(limit as i64);
        }

        let rows = sql_query
            .fetch_all(&db.read_pool)
            .await
            .map_err(|e| format!("Failed to fetch chat history: {e}"))?;

        let mut entries: Vec<ChatMessageEntry> = rows.into_iter().map(row_to_chat_entry).collect();
        entries.reverse(); // chronological order

        // Update cache
        self.history
            .write()
            .await
            .insert(user_id.to_string(), entries.clone());

        Ok(entries)
    }

    async fn clear_history(&self, user_id: &str) -> Result<(), String> {
        {
            let mut history = self.history.write().await;
            history.remove(user_id);
        }

        let db_lock = self.db.read().await;
        let db = db_lock.as_ref().ok_or("Database not initialized")?;

        // Only clear Mimic's messages, preserve Alden/Alice
        sqlx::query("DELETE FROM alden_chat_messages WHERE user_id = ? AND role = 'mimic'")
            .bind(user_id)
            .execute(&db.write_pool)
            .await
            .map_err(|e| format!("Failed to clear chat history: {e}"))?;

        Ok(())
    }
}

