/// Send a chat message to Mimic
#[tauri::command]
pub async fn mimic_chat_send(
    auth: State<'_, Arc<SecureAuthenticator>>,
    request: AuthenticatedRequest<SendChatMessageRequest>,
    state: State<'_, MimicChatState>,
    settings_state: State<'_, SettingsState>,
) -> Result<ChatMessageEntry, String> {
    let authenticated_user = auth
        .verify_token(&request.auth_token)
        .await
        .map_err(|e| format!("Authentication failed: {e}"))?;

    if authenticated_user != request.payload.user_id {
        return Err("Unauthorized: Cannot send messages for other users".to_string());
    }

    let trimmed = request.payload.message.trim();
    if trimmed.is_empty() {
        return Err("Message cannot be empty".to_string());
    }

    let user_message = ChatMessageEntry::new(
        &request.payload.user_id,
        "user",
        trimmed,
        &request.payload.session_id,
    );
    state
        .append_history(&request.payload.user_id, user_message)
        .await?;

    // Load recent history for context (includes all agents)
    let history = state
        .load_history(&request.payload.user_id, Some(10))
        .await
        .unwrap_or_default();

    // Get current persona mode
    let persona_mode = state
        .chat_manager
        .get_persona_mode(&request.payload.user_id)
        .await;

    let settings = {
        let manager = settings_state
            .manager
            .lock()
            .map_err(|_| "Failed to lock settings manager".to_string())?;
        manager.load()
    };
    let model_override = settings
        .personality
        .mimic_model
        .clone()
        .filter(|value| !value.trim().is_empty())
        .or_else(|| {
            let value = settings.models_runtime.default_model.clone();
            if value.trim().is_empty() {
                None
            } else {
                Some(value)
            }
        });

    let response_text = state
        .chat_manager
        .handle_message(
            HandleMessageRequest {
                user_id: request.payload.user_id.clone(),
                message: trimmed.to_string(),
                session_id: request.payload.session_id.clone(),
            },
            history,
            persona_mode,
            model_override,
        )
        .await?;

    let response_entry = ChatMessageEntry::new(
        &request.payload.user_id,
        "mimic",
        &response_text,
        &request.payload.session_id,
    );
    state
        .append_history(&request.payload.user_id, response_entry.clone())
        .await?;

    Ok(response_entry)
}

