"""Utilities for executing Model Context Protocol helpers.

This lightweight implementation intentionally keeps behaviour minimal â€“ the
unit tests exercise the orchestration layer rather than real MCP calls â€“ so we
focus on deterministic, well-scoped filesystem helpers while clearly
communicating that GitHub/Gmail actions are placeholders.  The executor mirrors
the interface expected by :mod:`synapse.plugin_manager`.
"""

from __future__ import annotations

from pathlib import Path
from typing import Any, Dict, Iterable, List, Optional

import logging


LOGGER = logging.getLogger(__name__)


class MCPExecutor:
    """Execute MCP helper tools with strict sandboxing."""

    def __init__(
        self,
        allowed_paths: Optional[Iterable[Path | str]] = None,
        workspace_root: Optional[Path | str] = None,
    ) -> None:
        root = Path(workspace_root or Path.cwd()).resolve()

        base_paths: List[Path] = [root]
        if allowed_paths:
            base_paths.extend(Path(p).resolve() for p in allowed_paths)

        # Deduplicate while preserving order
        seen: set[Path] = set()
        self.allowed_paths: List[Path] = []
        for path in base_paths:
            if path not in seen:
                seen.add(path)
                self.allowed_paths.append(path)

    # ------------------------------------------------------------------
    # Filesystem helpers
    # ------------------------------------------------------------------
    def execute_filesystem_mcp(self, tool: str, parameters: Dict[str, Any]) -> Dict[str, Any]:
        """Run basic filesystem operations within the allowed sandbox."""

        try:
            if tool == "list_directory":
                return self._list_directory(parameters.get("path", "."))
            if tool == "read_file":
                return self._read_file(parameters.get("path"))
            if tool == "write_file":
                return self._write_file(
                    parameters.get("path"), parameters.get("content", "")
                )
            if tool == "create_directory":
                return self._create_directory(parameters.get("path"))
            if tool == "get_file_info":
                return self._get_file_info(parameters.get("path"))
            if tool == "search_files":
                return self._search_files(
                    parameters.get("path", "."), parameters.get("pattern", "*")
                )
            if tool == "list_allowed_directories":
                return {
                    "success": True,
                    "directories": [str(path) for path in self.allowed_paths],
                }

            return {"success": False, "error": f"Unknown filesystem tool: {tool}"}

        except Exception as exc:  # pragma: no cover - defensive logging
            LOGGER.exception("Filesystem MCP execution failed")
            return {"success": False, "error": str(exc)}

    def _list_directory(self, path: Optional[str]) -> Dict[str, Any]:
        target = self._validate_path(path)
        if not target.exists():
            return {"success": False, "error": f"Path does not exist: {target}"}
        if not target.is_dir():
            return {"success": False, "error": f"Not a directory: {target}"}

        entries = []
        for child in sorted(target.iterdir(), key=lambda p: p.name.lower()):
            info = {
                "name": child.name,
                "type": "directory" if child.is_dir() else "file",
            }
            if child.is_file():
                try:
                    info["size"] = child.stat().st_size
                except OSError:
                    info["size"] = None
            entries.append(info)

        return {"success": True, "path": str(target), "items": entries, "count": len(entries)}

    def _read_file(self, path: Optional[str]) -> Dict[str, Any]:
        target = self._validate_path(path)
        if not target.exists() or not target.is_file():
            return {"success": False, "error": f"File not found: {target}"}
        return {"success": True, "path": str(target), "content": target.read_text(encoding="utf-8")}

    def _write_file(self, path: Optional[str], content: str) -> Dict[str, Any]:
        target = self._validate_path(path)
        if target.is_dir():
            return {"success": False, "error": f"Path is a directory: {target}"}
        target.parent.mkdir(parents=True, exist_ok=True)
        target.write_text(content, encoding="utf-8")
        return {"success": True, "path": str(target), "bytes_written": len(content.encode("utf-8"))}

    def _create_directory(self, path: Optional[str]) -> Dict[str, Any]:
        target = self._validate_path(path)
        target.mkdir(parents=True, exist_ok=True)
        return {"success": True, "path": str(target)}

    def _get_file_info(self, path: Optional[str]) -> Dict[str, Any]:
        target = self._validate_path(path)
        if not target.exists():
            return {"success": False, "error": f"Path does not exist: {target}"}

        info = {
            "success": True,
            "path": str(target),
            "type": "directory" if target.is_dir() else "file",
        }
        try:
            stat = target.stat()
            info["size"] = stat.st_size
            info["modified"] = stat.st_mtime
        except OSError:
            info["size"] = None
            info["modified"] = None
        return info

    def _search_files(self, path: Optional[str], pattern: str) -> Dict[str, Any]:
        root = self._validate_path(path)
        if not root.exists() or not root.is_dir():
            return {"success": False, "error": f"Search root invalid: {root}"}

        matches = [str(p) for p in root.rglob(pattern)]
        return {"success": True, "path": str(root), "pattern": pattern, "results": matches}

