    def _validate_path(self, path: Optional[str]) -> Path:
        if not path:
            raise ValueError("Path parameter is required")

        candidate = Path(path).expanduser()
        if not candidate.is_absolute():
            candidate = (self.allowed_paths[0] / candidate).resolve()
        else:
            candidate = candidate.resolve()

        if not any(self._is_within(candidate, allowed) for allowed in self.allowed_paths):
            raise PermissionError(f"Access to path is not permitted: {candidate}")

        return candidate

    @staticmethod
    def _is_within(path: Path, allowed: Path) -> bool:
        try:
            path.relative_to(allowed)
            return True
        except ValueError:
            return False
