#[tauri::command]
pub async fn synapse_marketplace_list(
    auth: State<'_, Arc<SecureAuthenticator>>,
    request: AuthenticatedRequest<EmptyPayload>,
) -> Result<Vec<MarketplaceEntry>, String> {
    let _authenticated_user = require_auth!(auth, &request.auth_token);

    println!("[Marketplace] User authenticated, listing plugins");
    Ok(catalog_with_status())
}

/// Install a plugin from the marketplace
/// Requires authentication (write operation) and permission consent validation
#[tauri::command]
pub async fn synapse_marketplace_install(
    auth: State<'_, Arc<SecureAuthenticator>>,
    vault: State<'_, SecureVault>,
    request: AuthenticatedRequest<InstallPluginPayload>,
) -> Result<(), String> {
    let authenticated_user = require_auth!(auth, &request.auth_token);

    let plugin_id = &request.payload.id;
    let consented_permissions = &request.payload.consented_permissions;

    println!(
        "[Marketplace] User {} installing plugin: {}",
        authenticated_user, plugin_id
    );

    // Validate that user has consented to all required permissions
    let catalog = get_catalog();
    let plugin = catalog
        .iter()
        .find(|entry| entry.id == *plugin_id)
        .ok_or_else(|| format!("Plugin {} not found in catalog", plugin_id))?;

    // Check that all required permissions are consented
    for required_perm in &plugin.permissions {
        if !consented_permissions.contains(required_perm) {
            eprintln!(
                "[SECURITY] Permission consent missing for {:?} during install of {}",
                required_perm, plugin_id
            );
            return Err(format!(
                "Permission consent required: {:?}. Please review and approve all permissions.",
                required_perm
            ));
        }
    }

    // Special validation for SensitiveData permission
    if plugin.permissions.contains(&Permission::SensitiveData) {
        if !consented_permissions.contains(&Permission::SensitiveData) {
            eprintln!(
                "[SECURITY] SensitiveData permission not explicitly consented for plugin {}",
                plugin_id
            );
            return Err(
                "This plugin requires access to sensitive Vault data. Explicit consent is required."
                    .to_string(),
            );
        }
        println!(
            "[Marketplace] User {} explicitly consented to SensitiveData access for {}",
            authenticated_user, plugin_id
        );
    }

    install_plugin(plugin_id).map_err(|err| err.to_string())?;

    // Log the permission grant to the persistent audit log for compliance
    let metadata = serde_json::json!({
        "plugin_id": plugin_id,
        "consented_permissions": consented_permissions,
    });

    vault
        .log_audit_event(
            &authenticated_user,
            "permission_grant",
            Some(plugin_id),
            "success",
            None,
            Some(metadata),
        )
        .await
        .map_err(|e| format!("Failed to log audit event: {}", e))?;

    Ok(())
}

/// Uninstall a plugin
/// Requires authentication (write operation)
#[tauri::command]
pub async fn synapse_marketplace_uninstall(
    auth: State<'_, Arc<RwLock<SecureAuthenticator>>>,
    request: AuthenticatedRequest<PluginIdPayload>,
) -> Result<(), String> {
    let auth_guard = auth.read().await;
    let authenticated_user = require_auth!(auth_guard, &request.auth_token);

    let plugin_id = &request.payload.id;
    println!(
        "[Marketplace] User {} uninstalling plugin: {}",
        authenticated_user, plugin_id
    );

    uninstall_plugin(plugin_id).map_err(|err| err.to_string())
}

/// Get details for a specific plugin
/// Requires authentication (read operation)
#[tauri::command]
pub async fn synapse_marketplace_get_details(
    auth: State<'_, Arc<RwLock<SecureAuthenticator>>>,
    request: AuthenticatedRequest<PluginIdPayload>,
) -> Result<PluginMetadata, String> {
    let auth_guard = auth.read().await;
    let _authenticated_user = require_auth!(auth_guard, &request.auth_token);

    let plugin_id = &request.payload.id;

    let catalog = get_catalog();
    if let Some(metadata) = catalog.into_iter().find(|entry| entry.id == *plugin_id) {
        return Ok(metadata);
    }

    let installed = scan_local_plugins(&default_plugin_dir());
    installed
        .into_iter()
        .find(|entry| entry.id == *plugin_id)
        .ok_or_else(|| format!("Plugin {} not found", plugin_id))
}

