const fs = require("fs");
const path = require("path");

const catalogPath = path.join(__dirname, "..", "src-tauri", "src", "synapse", "marketplace_catalog.json");

const REQUIRED_FIELDS = [
  "id",
  "name",
  "version",
  "description",
  "author",
  "category",
  "trust_level",
  "dependencies",
  "permissions",
];

const ALLOWED_CATEGORIES = new Set([
  "productivity",
  "health",
  "iot",
  "developer",
  "security",
  "custom",
]);

const ALLOWED_TRUST_LEVELS = new Set([
  "verified",
  "community",
  "experimental",
]);

const ALLOWED_PERMISSIONS = new Set([
  "network_access",
  "file_system_read",
  "file_system_write",
  "system_events",
  "sensitive_data",
]);

const isNonEmptyString = (value) => typeof value === "string" && value.trim().length > 0;

const errors = [];

const raw = fs.readFileSync(catalogPath, "utf-8");
let catalog;
try {
  catalog = JSON.parse(raw);
} catch (err) {
  console.error("[marketplace-catalog] Invalid JSON:", err.message);
  process.exit(1);
}

if (!Array.isArray(catalog)) {
  console.error("[marketplace-catalog] Catalog root must be an array.");
  process.exit(1);
}

const seenIds = new Set();

catalog.forEach((entry, index) => {
  const label = `entry ${index}`;
  if (typeof entry !== "object" || entry === null || Array.isArray(entry)) {
    errors.push(`${label}: must be an object.`);
    return;
  }

  for (const field of REQUIRED_FIELDS) {
    if (!(field in entry)) {
      errors.push(`${label}: missing required field \"${field}\".`);
    }
  }

  if ("id" in entry) {
    if (!isNonEmptyString(entry.id)) {
      errors.push(`${label}: id must be a non-empty string.`);
    } else if (seenIds.has(entry.id)) {
      errors.push(`${label}: duplicate id \"${entry.id}\".`);
    } else {
      seenIds.add(entry.id);
    }
  }

  if ("name" in entry && !isNonEmptyString(entry.name)) {
    errors.push(`${label}: name must be a non-empty string.`);
  }

  if ("version" in entry && !isNonEmptyString(entry.version)) {
    errors.push(`${label}: version must be a non-empty string.`);
  }

  if ("description" in entry && !isNonEmptyString(entry.description)) {
    errors.push(`${label}: description must be a non-empty string.`);
  }

  if ("author" in entry && !isNonEmptyString(entry.author)) {
    errors.push(`${label}: author must be a non-empty string.`);
  }

  if ("category" in entry) {
    if (!isNonEmptyString(entry.category)) {
      errors.push(`${label}: category must be a non-empty string.`);
    } else if (!ALLOWED_CATEGORIES.has(entry.category)) {
      errors.push(`${label}: unknown category \"${entry.category}\".`);
    }
  }

  if ("trust_level" in entry) {
    if (!isNonEmptyString(entry.trust_level)) {
      errors.push(`${label}: trust_level must be a non-empty string.`);
    } else if (!ALLOWED_TRUST_LEVELS.has(entry.trust_level)) {
      errors.push(`${label}: unknown trust_level \"${entry.trust_level}\".`);
    }
  }

  if ("dependencies" in entry) {
    if (!Array.isArray(entry.dependencies)) {
      errors.push(`${label}: dependencies must be an array.`);
    } else if (!entry.dependencies.every(isNonEmptyString)) {
      errors.push(`${label}: dependencies must be an array of non-empty strings.`);
    }
  }

  if ("permissions" in entry) {
    if (!Array.isArray(entry.permissions)) {
      errors.push(`${label}: permissions must be an array.`);
    } else {
      entry.permissions.forEach((permission) => {
        if (!isNonEmptyString(permission)) {
          errors.push(`${label}: permissions must be non-empty strings.`);
        } else if (!ALLOWED_PERMISSIONS.has(permission)) {
          errors.push(`${label}: unknown permission \"${permission}\".`);
        }
      });
    }
  }
});

if (errors.length > 0) {
  console.error("[marketplace-catalog] Validation failed:");
  errors.forEach((err) => console.error(`- ${err}`));
  process.exit(1);
}

console.log(`[marketplace-catalog] OK (${catalog.length} entries).`);
