import { AuthClient } from "../lib/tauri-client";
import { invoke } from "@tauri-apps/api/tauri";
import { getStaticUserId } from "./alden/ipc";

export interface PluginMetadata {
  id: string;
  name: string;
  version: string;
  description: string;
  author: string;
  category: PluginCategory;
  trust_level: TrustLevel;
  dependencies: string[];
  permissions: Permission[];
  long_description?: string;
  tags?: string[];
}

export type PluginCategory =
  | "productivity"
  | "health"
  | "iot"
  | "developer"
  | "security"
  | "custom";
export type TrustLevel = "verified" | "community" | "experimental";
export type Permission =
  | "network_access"
  | "file_system_read"
  | "file_system_write"
  | "system_events"
  | "sensitive_data";

export interface MarketplaceEntry {
  metadata: PluginMetadata;
  status: InstallStatus;
}

export type InstallStatus =
  | "available"
  | "installed"
  | "update_available"
  | "installing"
  | { failed: string };

// Centralized auth helper that uses the real AuthClient
// Matches BaseBridge's logic but for this standalone object
const createRealAuthenticatedRequest = <T>(
  payload: T,
  _userId?: string, // Legacy param ignored in favor of real client
): { request: { auth_token: string } & T } => {
  const token = AuthClient.getAuthToken();
  if (!token) {
    throw new Error("Authentication required for Marketplace operations");
  }
  return {
    request: {
      auth_token: token,
      ...(payload as any),
    },
  };
};

export const MarketplaceBridge = {
  list: (userId: string = getStaticUserId()) =>
    invoke<MarketplaceEntry[]>(
      "synapse_marketplace_list",
      createRealAuthenticatedRequest({}, userId),
    ),
  install: (
    id: string,
    consentedPermissions: Permission[],
    userId: string = getStaticUserId(),
  ) =>
    invoke<void>(
      "synapse_marketplace_install",
      createRealAuthenticatedRequest(
        { id, consented_permissions: consentedPermissions },
        userId,
      ),
    ),
  uninstall: (id: string, userId: string = getStaticUserId()) =>
    invoke<void>(
      "synapse_marketplace_uninstall",
      createRealAuthenticatedRequest({ id }, userId),
    ),
  getDetails: (id: string, userId: string = getStaticUserId()) =>
    invoke<PluginMetadata>(
      "synapse_marketplace_get_details",
      createRealAuthenticatedRequest({ id }, userId),
    ),
  sync: (userId: string = getStaticUserId()) =>
    invoke<MarketplaceEntry[]>(
      "synapse_marketplace_sync",
      createRealAuthenticatedRequest({}, userId),
    ),
};

