/**
 * Tauri IPC Client for Secure Vault Operations
 * Fixed parameter mapping for Rust function signatures
 */

// import { invoke } from '@tauri-apps/api/tauri';

// Safe invoke wrapper for browser compatibility
const invoke = async <T>(cmd: string, args?: unknown): Promise<T> => {
  // Check for explicit test mocks (Playwright)
  if (typeof window !== "undefined" && (window as any).__TAURI_MOCKS__) {
    const mocks = (window as any).__TAURI_MOCKS__;
    if (mocks[cmd] !== undefined) {
      console.log(`[Tauri Mock] Returning mocked response for "${cmd}"`);
      return mocks[cmd] as T;
    }
  }

  if (typeof window !== "undefined" && "__TAURI_IPC__" in window) {
    const { invoke: tauriInvoke } = await import("@tauri-apps/api/tauri");
    return tauriInvoke(cmd, args as any);
  }

  console.warn(`[Tauri Mock] invoking "${cmd}" in browser mode`, args);

  // Mock responses for UI testing
  if (cmd === "vault_store")
    return { success: true, message: "Mock stored" } as any;
  if (cmd === "vault_retrieve")
    return { success: true, data: { content: "Mock data" } } as any;
  if (cmd === "send_message")
    return { success: true, data: "Message sent" } as any;

  // Calendar Sync Mocks
  if (
    cmd === "alden_calendar_sync_google" ||
    cmd === "alden_calendar_sync_outlook"
  ) {
    return {
      status: "success",
      eventsSynced: 12,
      lastSyncAt: new Date().toISOString(),
      provider: cmd.includes("google") ? "google" : "outlook",
    } as any;
  }

  return { success: true } as any;
};

// --- Data Types ---

export interface VaultRequest {
  userId: string;
  personaId: string;
  data: unknown;
  authToken: string;
}

export interface VaultResponse {
  success: boolean;
  message: string;
  data?: unknown;
  entry_id?: string;
}

export interface AuditEvent {
  id: string;
  user_id: string;
  action: string;
  resource_id?: string;
  timestamp: string;
  metadata?: Record<string, unknown>;
}

// --- Secure Vault Client ---

export class SecureVaultClient {
  /**
   * Store data in the secure vault
   * Fixed to match Rust function signature with individual parameters
   */
  static async store(request: VaultRequest): Promise<VaultResponse> {
    try {
      console.log(
        "[SecureVaultClient] About to store with request:",
        JSON.stringify(request, null, 2),
      );
      console.log(
        "[SecureVaultClient] Mapping to individual parameters for Rust function signature",
      );
      return await invoke("vault_store", {
        userId: request.userId,
        personaId: request.personaId,
        data: request.data,
        authToken: request.authToken,
      });
    } catch (error) {
      console.error("[SecureVaultClient] Store error:", error);
      throw new Error(`Failed to store data: ${error}`);
    }
  }

  /**
   * Retrieve data from the secure vault
   */
  static async retrieve(
    userId: string,
    personaId: string,
    authToken: string,
  ): Promise<VaultResponse> {
    try {
      const params = {
        userId,
        personaId,
        authToken,
      };
      console.log(
        "[SecureVaultClient] About to retrieve with params:",
        JSON.stringify(params, null, 2),
      );
      return await invoke("vault_retrieve", params);
    } catch (error) {
      console.error("[SecureVaultClient] Retrieve error:", error);
      throw new Error(`Failed to retrieve data: ${error}`);
    }
  }

  /**
   * Delete data from the secure vault
   */
  static async delete(
    userId: string,
    personaId: string,
    authToken: string,
  ): Promise<VaultResponse> {
    try {
      const params = {
        userId,
        personaId,
        authToken,
      };
      console.log(
        "[SecureVaultClient] About to delete with params:",
        JSON.stringify(params, null, 2),
      );
      return await invoke("vault_delete", params);
    } catch (error) {
      console.error("[SecureVaultClient] Delete error:", error);
      throw new Error(`Failed to delete data: ${error}`);
    }
  }

  /**
   * Get audit log for a user
   */
  static async getAuditLog(
    userId: string,
    authToken: string,
  ): Promise<AuditEvent[]> {
    try {
      const params = {
        userId,
        authToken,
      };
      console.log(
        "[SecureVaultClient] About to get audit log with params:",
        JSON.stringify(params, null, 2),
      );
      return await invoke("vault_get_audit_log", params);
    } catch (error) {
      console.error("[SecureVaultClient] Audit log error:", error);
      throw new Error(`Failed to get audit log: ${error}`);
    }
  }
}

// --- App Status Client ---

export class AppStatusClient {
  /**
   * Get basic app status
   */
  static async getAppStatus(): Promise<unknown> {
    try {
      return await invoke("get_app_status");
    } catch (error) {
      console.error("[AppStatusClient] Get app status error:", error);
      throw new Error(`Failed to get app status: ${error}`);
    }
  }

  /**
   * Basic greeting command
   */
  static async greet(name: string): Promise<string> {
    try {
      return await invoke("greet", { name });
    } catch (error) {
      console.error("[AppStatusClient] Greet error:", error);
      throw new Error(`Failed to greet: ${error}`);
    }
  }
}

// --- Authentication Client ---
export class AuthClient {
  private static userId: string | null = null;
  private static authToken: string | null = null;

  /**
   * Set authentication credentials
   */
  static setCredentials(userId: string, authToken: string): void {
    this.userId = userId;
    this.authToken = authToken;

    // Persist to localStorage for cross-module reliability
    try {
      localStorage.setItem("hearthlink_user_id", userId);
      localStorage.setItem("hearthlink_token", authToken);
    } catch (e) {
      console.warn(
        "[AuthClient] Failed to persist credentials to localStorage:",
        e,
      );
    }

    console.log("[AuthClient] Credentials set for user:", userId);
  }

  /**
   * Get current user ID
   */
  static getUserId(): string | null {
    if (this.userId) return this.userId;

    // Fallback to localStorage
    try {
      this.userId = localStorage.getItem("hearthlink_user_id");
    } catch (e) {
      return null;
    }
    return this.userId;
  }

  /**
   * Get current auth token
   */
  static getAuthToken(): string | null {
    if (this.authToken) return this.authToken;

    // Fallback to localStorage
    try {
      this.authToken = localStorage.getItem("hearthlink_token");
    } catch (e) {
      return null;
    }
    return this.authToken;
  }

  /**
   * Refresh credentials for development (Auto-Generating Demo Token)
   * This handles the case where the backend rejects a stale "demo_token_..."
   */
  static refreshDemoCredentials(): { userId: string; authToken: string } {
    if (process.env.NODE_ENV === "development") {
      console.log(
        "[AuthClient] Refreshing demo credentials due to Auth Failure...",
      );
      this.clearCredentials();
      return this.generateDemoCredentials();
    }
    throw new Error("Cannot auto-refresh credentials in production.");
  }

  /**
   * Check if user is authenticated
   */
  static isAuthenticated(): boolean {
    return this.getUserId() !== null && this.getAuthToken() !== null;
  }

  /**
   * Clear current credentials
   */
  static clearCredentials(): void {
    this.userId = null;
    this.authToken = null;
    try {
      localStorage.removeItem("hearthlink_user_id");
      localStorage.removeItem("hearthlink_token");
    } catch (e) {}
    console.log("[AuthClient] Credentials cleared");
  }

  /**
   * Generate demo credentials for testing
   * Uses a consistent userId to avoid "user mismatch" errors
   */
  static generateDemoCredentials(): { userId: string; authToken: string } {
    if (process.env.NODE_ENV !== "development") {
      throw new Error(
        "[AuthClient] Demo credentials are disabled in production builds",
      );
    }
    const userId = "default-user"; // Consistent userId for demo/testing

    // Use the demo_token format that auth.rs accepts without signature verification
    // Format: demo_token_USERID ensures verify_token returns USERID
    const authToken = `demo_token_${userId}`;

    this.setCredentials(userId, authToken);

    console.log("[AuthClient] Generated demo credentials:", {
      userId,
      tokenLength: authToken.length,
      tokenFormat: "demo_token",
    });

    return { userId, authToken };
  }
}

// --- Hearthlink Client (Main Interface) ---

export class HearthlinkClient {
  /**
   * Initialize the client with authentication
   */
  static async initialize(): Promise<{ userId: string; authToken: string }> {
    console.log("[HearthlinkClient] Initializing...");

    if (!AuthClient.isAuthenticated()) {
      if (process.env.NODE_ENV !== "development") {
        throw new Error(
          "[HearthlinkClient] Authentication required before initialization",
        );
      }
      const credentials = AuthClient.generateDemoCredentials();
      console.log(
        "[HearthlinkClient] Generated demo credentials:",
        credentials,
      );
      const result = {
        userId: credentials.userId,
        authToken: credentials.authToken,
      };
      console.log(
        "[HearthlinkClient] Returning initialized credentials:",
        JSON.stringify(result, null, 2),
      );
      return result;
    }

    const userId = AuthClient.getUserId();
    const authToken = AuthClient.getAuthToken();

    if (!userId || !authToken) {
      throw new Error(
        "[HearthlinkClient] Authenticated but missing credentials",
      );
    }

    const result = { userId, authToken };
    console.log(
      "[HearthlinkClient] Returning existing credentials:",
      JSON.stringify(result, null, 2),
    );
    return result;
  }

  /**
   * Send a voice message (simplified for vault demo)
   */
  static async sendVoiceMessage(
    message: string,
    agent: string,
  ): Promise<unknown> {
    const { userId, authToken } = await this.initialize();

    try {
      console.log("[HearthlinkClient] Storing voice message in vault...");

      // Store the user message in vault first
      await SecureVaultClient.store({
        userId,
        personaId: agent,
        data: {
          type: "user_message",
          message,
          timestamp: Date.now(),
          agent,
        },
        authToken,
      });

      console.log("[HearthlinkClient] Voice message stored successfully");

      // Return a demo response for testing
      return {
        success: true,
        message: "Voice message processed and stored securely",
        agent_response: `Demo response from ${agent}: I received your message "${message}"`,
      };
    } catch (error) {
      console.error("[HearthlinkClient] Voice message error:", error);
      throw new Error(`Failed to send voice message: ${error}`);
    }
  }
}

