/**
 * Check if error is due to missing Python and provide helpful message
 */
function handlePythonError(error: unknown, featureName: string): Error {
  const errorStr = String(error);

  if (errorStr.includes("Python") || errorStr.includes("python")) {
    return new Error(
      `${featureName} requires Python 3.8+. This is a legacy feature - please install Python or use the modern Rust implementation (Alden tasks, calendar, email).`,
    );
  }

  return new Error(`Failed to ${featureName.toLowerCase()}: ${error}`);
}

