/**
 * Authentication Context Provider
 *
 * Provides authentication state and user session management across the application.
 * Integrates with Arbiter auth system for secure user identification.
 *
 * @version 2.0.0
 * @date 2026-01-01
 */

import React, {
  createContext,
  useContext,
  useState,
  useEffect,
  useCallback,
  useMemo,
} from "react";
import { ArbiterBridge } from "../bridges/ArbiterBridge";
import { AuthClient } from "../lib/tauri-client";

interface AuthContextType {
  userId: string | null;
  authToken: string | null;
  isAuthenticated: boolean;
  isLoading: boolean;
  isDemoMode: boolean;
  login: (credentials: {
    username: string;
    password: string;
  }) => Promise<boolean>;
  logout: () => Promise<void>;
  autoLogin: () => Promise<boolean>;
  verifySession: () => Promise<boolean>;
  enableDemoMode: () => void;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

let lastKnownUserId = "default-user";

export const AuthProvider: React.FC<{ children: React.ReactNode }> = ({
  children,
}) => {
  const [userId, setUserId] = useState<string | null>(null);
  const [authToken, setAuthToken] = useState<string | null>(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [isDemoMode, setIsDemoMode] = useState(false);

  // Initialize Arbiter bridge in unauthenticated mode
  // This allows it to be created without credentials, which are obtained during login/autoLogin
  const arbiterBridge = useMemo(() => new ArbiterBridge({ requireAuth: false }), []);

  /**
   * Enable demo mode for development/testing
   * Bypasses authentication with a demo user
   */
  const enableDemoMode = useCallback(() => {
    const demoAllowed =
      process.env.NODE_ENV === "development" ||
      ["1", "true", "yes", "on"].includes(
        (process.env.REACT_APP_HEARTHLINK_DEMO_MODE || "").toLowerCase(),
      );

    if (!demoAllowed) {
      console.warn("[AuthContext] Demo mode is disabled in production");
      return;
    }
    console.log("[AuthContext] Enabling demo mode");
    const demoCredentials = AuthClient.generateDemoCredentials();
    setUserId(demoCredentials.userId);
    setAuthToken(demoCredentials.authToken);
    setIsAuthenticated(true);
    setIsDemoMode(true);
    lastKnownUserId = demoCredentials.userId;
  }, []);

  /**
   * Auto-login on app startup
   * Attempts to restore previous session via ArbiterBridge
   */
  useEffect(() => {
    const initializeAuth = async () => {
      setIsLoading(true);
      try {
        // Try auto-login first (biometric or cached credentials)
        const autoLoginResult = await arbiterBridge.autoLogin();

        if (autoLoginResult.success && autoLoginResult.data) {
          setUserId(autoLoginResult.data.userId);
          setAuthToken(autoLoginResult.data.authToken);
          setIsAuthenticated(true);
          lastKnownUserId = autoLoginResult.data.userId;
          AuthClient.setCredentials(
            autoLoginResult.data.userId,
            autoLoginResult.data.authToken,
          );
        } else {
          // Fallback: Check for existing session
          const sessionResult = await arbiterBridge.getSession();

          if (sessionResult.success && sessionResult.data) {
            setUserId(sessionResult.data.userId);
            setAuthToken(sessionResult.data.authToken);
            setIsAuthenticated(true);
            lastKnownUserId = sessionResult.data.userId;
            AuthClient.setCredentials(
              sessionResult.data.userId,
              sessionResult.data.authToken,
            );
          } else {
            // No session found - require authentication
            // User must either login or use demo mode
            console.log(
              "[AuthContext] No session found, authentication required",
            );
            setUserId(null);
            setAuthToken(null);
            setIsAuthenticated(false);
            AuthClient.clearCredentials();
          }
        }
      } catch (error) {
        console.error("[AuthContext] Failed to initialize auth:", error);
        // Authentication system unavailable - require login
        setUserId(null);
        setAuthToken(null);
        setIsAuthenticated(false);
        AuthClient.clearCredentials();
      } finally {
        setIsLoading(false);
      }
    };

    initializeAuth();
  }, [arbiterBridge]);

  useEffect(() => {
    if (userId) {
      lastKnownUserId = userId;
    }
  }, [userId]);

  useEffect(() => {
    if (authToken) {
      localStorage.setItem("hearthlink_token", authToken);
    } else {
      localStorage.removeItem("hearthlink_token");
    }
  }, [authToken]);

  /**
   * Login with credentials
   */
  const login = useCallback(
    async (credentials: {
      username: string;
      password: string;
    }): Promise<boolean> => {
      try {
        const result = await arbiterBridge.login(credentials);

        if (!result.success) {
          console.error("[AuthContext] Login failed:", result.error);
          return false;
        }

        setUserId(result.data.userId);
        setAuthToken(result.data.authToken);
        setIsAuthenticated(true);
        setIsDemoMode(false);
        lastKnownUserId = result.data.userId;
        AuthClient.setCredentials(result.data.userId, result.data.authToken);
        return true;
      } catch (error) {
        console.error("[AuthContext] Login error:", error);
        return false;
      }
    },
    [arbiterBridge],
  );

  /**
   * Logout and clear session
   */
  const logout = useCallback(async (): Promise<void> => {
    try {
      if (!isDemoMode) {
        await arbiterBridge.logout();
      }
      setUserId(null);
      setAuthToken(null);
      setIsAuthenticated(false);
      setIsDemoMode(false);
      AuthClient.clearCredentials();
    } catch (error) {
      console.error("[AuthContext] Logout error:", error);
    }
  }, [arbiterBridge, isDemoMode]);

  /**
   * Auto-login (called after app restart or biometric prompt)
   */
  const autoLogin = useCallback(async (): Promise<boolean> => {
    try {
      const result = await arbiterBridge.autoLogin();

      if (!result.success) {
        console.warn("[AuthContext] Auto-login failed:", result.error);
        return false;
      }

      setUserId(result.data.userId);
      setAuthToken(result.data.authToken);
      setIsAuthenticated(true);
      setIsDemoMode(false);
      lastKnownUserId = result.data.userId;
      AuthClient.setCredentials(result.data.userId, result.data.authToken);
      return true;
    } catch (error) {
      console.error("[AuthContext] Auto-login error:", error);
      return false;
    }
  }, [arbiterBridge]);

  /**
   * Verify current session is still valid
   */
  const verifySession = useCallback(async (): Promise<boolean> => {
    // Demo mode sessions are always valid
    if (isDemoMode) return true;
    if (!authToken) return false;

    try {
      const result = await arbiterBridge.verifySession(authToken);

      if (result.success && result.data) {
        setUserId(result.data.userId);
        setAuthToken(result.data.authToken);
        setIsAuthenticated(true);
        setIsDemoMode(false);
        AuthClient.setCredentials(result.data.userId, result.data.authToken);
        return true;
      } else {
        // Session invalid - clear auth state
        setUserId(null);
        setAuthToken(null);
        setIsAuthenticated(false);
        AuthClient.clearCredentials();
        return false;
      }
    } catch (error) {
      console.error("[AuthContext] Session verification error:", error);
      return false;
    }
  }, [authToken, arbiterBridge, isDemoMode]);

  const value: AuthContextType = {
    userId,
    authToken,
    isAuthenticated,
    isLoading,
    isDemoMode,
    login,
    logout,
    autoLogin,
    verifySession,
    enableDemoMode,
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};

/**
 * Custom hook to use authentication context
 * @throws Error if used outside AuthProvider
 */
export const useAuth = (): AuthContextType => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error("useAuth must be used within an AuthProvider");
  }
  return context;
};

/**
 * Helper function to get current user ID
 * Provides fallback for backwards compatibility
 */
export const getCurrentUserId = (): string => {
  return lastKnownUserId;
};

