/**
 * Base Agent Bridge Implementation
 *
 * Provides common functionality for all agent bridges including:
 * - API communication
 * - Event subscription
 * - Error handling
 * - State management
 *
 * @version 1.3.0
 * @date 2025-10-09
 */

import { invoke } from "@tauri-apps/api/tauri";
import {
  AgentId,
  AgentBridge,
  BridgeResponse,
  AgentCommand,
  CommandRequest,
} from "../types/agents";
import { AuthClient } from "../lib/tauri-client";
import { isBrowserMode, warnBrowserMode } from "../utils/tauri";
import {
  AuthError,
  BridgeOperationError,
  ValidationError,
  isAuthError,
  isBridgeError,
  createFailedResponse,
} from "../types/bridge-errors";

type JsonObject = Record<string, unknown>;

export interface BridgeConfig {
  agentId: AgentId;
  baseUrl?: string;
  timeout?: number;
  retryAttempts?: number;
  userId?: string;
  authToken?: string;
  /**
   * If false, allows bridge to be created without userId/authToken.
   * Useful for bridges used during authentication flows (e.g., ArbiterBridge).
   * Default: true (requires auth credentials for backward compatibility)
   */
  requireAuth?: boolean;
}

export abstract class BaseBridge<TProps = unknown>
  implements AgentBridge<TProps>
{
  public readonly agentId: AgentId;
  protected baseUrl: string;
  protected timeout: number;
  protected retryAttempts: number;
  protected subscribers: Set<(data: unknown) => void> = new Set();
  protected initialized = false;
  protected userId: string;
  protected authToken: string;
  protected requireAuth: boolean;

  constructor(config: BridgeConfig) {
    this.agentId = config.agentId;
    this.baseUrl = config.baseUrl || "/api";
    this.timeout = config.timeout || 30000;
    this.retryAttempts = config.retryAttempts || 3;
    this.requireAuth = config.requireAuth !== false; // Default to true for backward compatibility

    // Credentials must be explicitly provided - no fallback to demo tokens
    this.userId = config.userId ?? AuthClient.getUserId() ?? "";
    this.authToken = config.authToken ?? AuthClient.getAuthToken() ?? "";

    // Only enforce auth if requireAuth is true (default behavior)
    if (this.requireAuth) {
      // Credentials will be validated in initialize() to safe-guard React lifecycles
      if (this.userId && this.authToken) {
        console.log(
          `[Bridge:${this.agentId}] Initialized with validated credentials for user: ${this.userId}`,
        );
      } else {
        console.log(
          `[Bridge:${this.agentId}] Credentials missing in constructor; deferring validation to initialize().`,
        );
      }
    } else {
      // Unauthenticated mode: allow bridge to exist without credentials for auth flows
      console.log(
        `[Bridge:${this.agentId}:UNAUTHENTICATED_MODE] Bridge created in unauthenticated mode (for auth operations)`,
      );
    }
  }

  /**
   * Initialize the bridge connection
   */
  async initialize(): Promise<void> {
    // Attempt to hydrate auth from AuthClient if missing (fix for race condition)
    if (this.requireAuth && (!this.userId || !this.authToken)) {
      const freshUserId = AuthClient.getUserId();
      const freshToken = AuthClient.getAuthToken();
      if (freshUserId) this.userId = freshUserId;
      if (freshToken) this.authToken = freshToken;

      if (this.userId && this.authToken) {
        console.log(
          `[Bridge:${this.agentId}] Late hydration of credentials successful`,
        );
      }
    }

    // Enforce strict auth requirements before proceeding with initialization
    if (this.requireAuth) {
      if (!this.userId) throw AuthError.missingUserId();
      if (!this.authToken) throw AuthError.missingToken();
    }

    if (this.initialized) {
      return;
    }

    try {
      // Perform agent-specific initialization
      await this.onInitialize();
      this.initialized = true;
      console.log(`[Bridge:${this.agentId}] Initialized successfully`);
    } catch (error) {
      console.error(`[Bridge:${this.agentId}] Initialization failed:`, error);
      throw error;
    }
  }

  /**
   * Get props for the agent component
   */
  abstract getProps(): Promise<TProps>;

  /**
   * Send a message to the agent
   */
  async sendMessage(message: unknown): Promise<BridgeResponse<unknown>> {
    try {
      this.assertAuthContext();

      if (!this.initialized) {
        throw BridgeOperationError.notInitialized(this.agentId);
      }

      const command = `${this.agentId}_send_message`;
      const request: CommandRequest = {
        user_id: this.userId,
        message,
      };

      const response = await this.invokeCommand(command as AgentCommand, {
        request,
      });

      this.notifySubscribers({ type: "message_sent", data: message });
      return { success: true, data: response };
    } catch (error) {
      // Type-safe error handling with proper error codes
      return createFailedResponse(error, "Failed to send message");
    }
  }

  /**
   * Subscribe to bridge events
   */
  subscribe(callback: (data: unknown) => void): () => void {
    this.subscribers.add(callback);
    return () => {
      this.subscribers.delete(callback);
    };
  }

  /**
   * Destroy the bridge and cleanup resources
   */
  async destroy(): Promise<void> {
    try {
      await this.onDestroy();
      this.subscribers.clear();
      this.initialized = false;
      console.log(`[Bridge:${this.agentId}] Destroyed successfully`);
    } catch (error) {
      console.error(`[Bridge:${this.agentId}] Destruction failed:`, error);
      throw error;
    }
  }

  // Protected methods for subclasses

  /**
   * Hook for agent-specific initialization
   */
  protected abstract onInitialize(): Promise<void>;

  /**
   * Hook for agent-specific cleanup
   */
  protected abstract onDestroy(): Promise<void>;

  /**
   * Invoke a Tauri IPC command
   * @param command - The Tauri command name (e.g., 'alden_tasks_list')
   * @param params - The parameters to pass to the command
   * @param attempt - Current retry attempt (for internal use)
   * @returns The command result
   */
  protected async invokeCommand<T = unknown>(
    command: AgentCommand,
    params?: JsonObject,
    attempt = 1,
  ): Promise<T> {
    const callId = `${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;

    try {
      this.assertAuthContext();

      // Browser mode is not supported for real API usage
      // Tauri IPC is required for proper authentication and security
      if (isBrowserMode()) {
        throw BridgeOperationError.browserModeNotSupported(this.agentId);
      }

      console.log(
        `[BaseBridge.invokeCommand][${callId}] START command: ${command}`,
        JSON.stringify(params),
      );

      // Prepare payload with authenticated context
      const payload = { ...(params || {}) } as JsonObject;

      // Smart auth injection: If there is a 'request' key, inject token inside it
      // This ensures compatibility with Rust's AuthenticatedRequest<T> guard
      if (
        payload.request &&
        typeof payload.request === "object" &&
        !Array.isArray(payload.request)
      ) {
        (payload.request as JsonObject).auth_token = this.authToken;
      } else if (!("auth_token" in payload)) {
        payload.auth_token = this.authToken;
      }

      console.log(
        `[BaseBridge.invokeCommand][${callId}] About to call Tauri invoke() with command: ${command}`,
      );
      const startTime = performance.now();

      const result = await invoke<T>(command, payload);

      const elapsed = performance.now() - startTime;
      console.log(
        `[BaseBridge.invokeCommand][${callId}] SUCCESS after ${elapsed.toFixed(2)}ms for command: ${command}`,
      );
      console.log(
        `[BaseBridge.invokeCommand][${callId}] Result type:`,
        typeof result,
      );
      console.log(
        `[BaseBridge.invokeCommand][${callId}] Result is array:`,
        Array.isArray(result),
      );

      return result;
    } catch (error) {
      const safeNow = () => {
        try {
          return (
            (typeof performance !== "undefined" && performance?.now?.()) ||
            Date.now()
          );
        } catch {
          return Date.now();
        }
      };
      const elapsed = safeNow();
      console.error(
        `[BaseBridge.invokeCommand][${callId}] ERROR after ${elapsed.toFixed(2)}ms for command: ${command}`,
      );
      console.error(
        `[BaseBridge.invokeCommand][${callId}] Error details:`,
        error,
      );
      console.error(
        `[BaseBridge.invokeCommand][${callId}] Error type:`,
        typeof error,
      );
      if (error instanceof Error) {
        console.error(
          `[BaseBridge.invokeCommand][${callId}] Error message:`,
          error.message,
        );
        console.error(
          `[BaseBridge.invokeCommand][${callId}] Error stack:`,
          error.stack,
        );
      }

      // Retry on transient errors
      if (attempt <= this.retryAttempts && this.isRetryableError(error)) {
        console.log(
          `[BaseBridge.invokeCommand][${callId}] Retrying command ${command} (attempt ${attempt + 1}/${this.retryAttempts})`,
        );
        await this.delay(1000 * attempt); // Exponential backoff
        return this.invokeCommand(command, params, attempt + 1);
      }

      // Catch invalid token errors and attempt auto-refresh in DEV
      const errorMessage = String(error);
      if (
        (errorMessage.includes("InvalidToken") ||
          errorMessage.includes("Authentication failed")) &&
        process.env.NODE_ENV === "development" &&
        attempt === 1 // Only try refreshing once
      ) {
        console.warn(
          `[BaseBridge] Auth failed for ${command}. Attempting credential refresh...`,
        );
        try {
          // Dynamically import to avoid circular dependency issues if they exist,
          // but since they are in different trees it should be fine.
          // However, BaseBridge imports AuthClient already.
          const newCreds = AuthClient.refreshDemoCredentials();
          this.userId = newCreds.userId;
          this.authToken = newCreds.authToken;

          // Recursive call will rebuild payload with new this.authToken
          console.log(
            `[BaseBridge] Retrying ${command} with fresh credentials...`,
          );
          return this.invokeCommand(command, params, attempt + 1);
        } catch (refreshErr) {
          console.error(
            "[BaseBridge] Failed to refresh credentials during retry:",
            refreshErr,
          );
        }
      }

      console.error(
        `[BaseBridge.invokeCommand][${callId}] Throwing error for command: ${command}`,
      );
      throw error;
    }
  }

  /**
   * Get mock response for browser mode development
   * Override in subclasses to provide agent-specific mock data
   */
  protected getMockResponse<T>(command: AgentCommand, params?: JsonObject): T {
    console.log(`[BaseBridge] Mock response for ${command}`, params);

    // Default mock responses for common patterns
    if (command.includes("list") || command.includes("get_all")) {
      return [] as T;
    }

    if (command.includes("get") || command.includes("fetch")) {
      return { success: true, data: null } as T;
    }

    if (
      command.includes("create") ||
      command.includes("update") ||
      command.includes("delete")
    ) {
      return { success: true, id: "mock-id-" + Date.now() } as T;
    }

    // Generic success response
    return { success: true } as T;
  }

  /**
   * Check if an error is retryable
   */
  private isRetryableError(error: unknown): boolean {
    const message =
      error instanceof Error
        ? error.message
        : typeof error === "string"
          ? error
          : error !== null && typeof error === "object"
            ? (error as { message?: string }).message || ""
            : "";

    return (
      message.includes("timeout") ||
      message.includes("network") ||
      message.includes("connection")
    );
  }

  /**
   * @deprecated Use invokeCommand() instead. HTTP requests are no longer supported.
   * This method is kept for backward compatibility but will be removed in future versions.
   */
  protected async makeRequest<T = unknown>(
    method: "GET" | "POST" | "PATCH" | "DELETE",
    endpoint: string,
    body?: JsonObject | string,
    _attempt = 1,
  ): Promise<T> {
    console.warn(
      `[Bridge:${this.agentId}] makeRequest() is deprecated. Use invokeCommand() instead.`,
    );
    throw new Error(
      "HTTP requests are no longer supported. Bridge must use Tauri IPC commands.",
    );
  }

  /**
   * Notify all subscribers of an event
   */
  protected notifySubscribers(data: unknown): void {
    this.subscribers.forEach((callback) => {
      try {
        callback(data);
      } catch (error) {
        console.error(
          `[Bridge:${this.agentId}] Subscriber callback error:`,
          error,
        );
      }
    });
  }

  /**
   * Delay helper for retry logic
   */
  protected delay(ms: number): Promise<void> {
    return new Promise((resolve) => setTimeout(resolve, ms));
  }

  /**
   * Validate response data against expected schema
   */
  protected validateResponse<T>(data: unknown, requiredFields: string[]): T {
    if (typeof data !== "object" || data === null) {
      throw new Error("Invalid response type");
    }
    for (const field of requiredFields) {
      if (!(field in data)) {
        throw new Error(`Missing required field: ${field}`);
      }
    }
    return data as T;
  }

  /**
   * Create a standardized error response
   */
  protected createErrorResponse<T>(
    error: unknown,
    fallback?: string,
    code?: string,
  ): BridgeResponse<T> {
    let message: string;
    if (error instanceof Error) {
      message = error.message;
    } else if (typeof error === "string") {
      message = error;
    } else if (
      typeof error === "object" &&
      error !== null &&
      "message" in error
    ) {
      const maybeMessage = (error as { message?: unknown }).message;
      message =
        typeof maybeMessage === "string"
          ? maybeMessage
          : fallback || "Unknown error";
    } else {
      message = fallback || "Unknown error";
    }

    return {
      success: false,
      error: message,
      code: code || "BRIDGE_ERROR",
    };
  }

  /**
   * Check if the bridge has valid authentication credentials
   * Returns true if credentials are available (either from config or AuthClient)
   */
  protected hasAuthCredentials(): boolean {
    return (
      !!(this.userId || AuthClient.getUserId()) &&
      !!(this.authToken || AuthClient.getAuthToken())
    );
  }

  /**
   * Ensure auth context is available before IPC calls
   *
   * Fail-Closed Auth Logic
   * - No fallback to demo credentials
   * - Errors immediately if credentials are missing (when requireAuth=true)
   * - Applies to all environments (dev, test, prod)
   * - For unauthenticated bridges (requireAuth=false), validates before secure calls
   */
  protected assertAuthContext(): void {
    // ALWAYS try to refresh credentials from AuthClient to handle race conditions
    // where the bridge was created before auth was fully hydrated, or token rotated.
    const freshUserId = AuthClient.getUserId();
    const freshToken = AuthClient.getAuthToken();

    if (freshUserId && freshUserId !== this.userId) {
      console.log(`[Bridge:${this.agentId}] Refreshing UserId from AuthClient`);
      this.userId = freshUserId;
    }

    if (freshToken && freshToken !== this.authToken) {
      // Only log if we are actually changing from 'something' to 'something else' or 'nothing' to 'something'
      // Avoid logging on every call if stable.
      if (!this.authToken) {
        console.log(
          `[Bridge:${this.agentId}] Late-hydrating AuthToken from AuthClient`,
        );
      }
      this.authToken = freshToken;
    }

    // Enforce auth requirement based on bridge mode
    if (this.requireAuth) {
      // Strict mode: require both userId and authToken
      if (!this.userId) {
        throw AuthError.missingUserId();
      }
      if (!this.authToken) {
        throw AuthError.missingToken();
      }
      // Debug log (can be noisy, maybe comment out in prod)
      // console.log(`[Bridge:${this.agentId}] Auth context validated for user: ${this.userId}`);
    } else {
      // Unauthenticated mode: log if credentials are missing
      if (!this.userId || !this.authToken) {
        console.debug(
          `[Bridge:${this.agentId}:UNAUTHENTICATED_MODE] Making IPC call without full credentials.`,
        );
      }
    }
  }
}

/**
 * Bridge Factory
 * Creates and initializes agent bridges
 */
export class BridgeFactory {
  private static bridges: Map<AgentId, AgentBridge> = new Map();
  private static bridgeRefs: Map<AgentId, number> = new Map();

  static async createBridge<TProps>(
    Bridge: new (config: BridgeConfig) => AgentBridge<TProps>,
    agentId: AgentId,
    userId?: string,
    authToken?: string,
    requireAuth: boolean = true,
  ): Promise<AgentBridge<TProps>> {
    try {
      // Return existing bridge if already created
      if (this.bridges.has(agentId)) {
        const refCount = this.bridgeRefs.get(agentId) ?? 0;
        this.bridgeRefs.set(agentId, refCount + 1);
        return this.bridges.get(agentId) as AgentBridge<TProps>;
      }

      // Only validate auth if requireAuth is true
      if (requireAuth) {
        // HARDENING (Track 5): Explicit auth validation before bridge creation
        const effectiveUserId = userId ?? AuthClient.getUserId();
        const effectiveToken = authToken ?? AuthClient.getAuthToken();

        if (!effectiveUserId || !effectiveToken) {
          throw AuthError.missingCredentials();
        }

        // Create and initialize new bridge with explicit credentials
        const bridge = new Bridge({
          agentId,
          userId: effectiveUserId,
          authToken: effectiveToken,
          requireAuth,
        });
        await bridge.initialize();
        this.bridges.set(agentId, bridge);
        this.bridgeRefs.set(agentId, 1);

        console.log(
          `[BridgeFactory] Created bridge for agent "${agentId}" with authenticated user`,
        );

        return bridge;
      } else {
        // Create unauthenticated bridge (for auth flows like ArbiterBridge)
        const bridge = new Bridge({
          agentId,
          userId: userId ?? undefined,
          authToken: authToken ?? undefined,
          requireAuth: false,
        });
        await bridge.initialize();
        this.bridges.set(agentId, bridge);
        this.bridgeRefs.set(agentId, 1);

        console.log(
          `[BridgeFactory] Created unauthenticated bridge for agent "${agentId}" (for auth operations)`,
        );

        return bridge;
      }
    } catch (error) {
      if (isAuthError(error)) {
        console.error(
          `[BridgeFactory] Auth error creating bridge for "${agentId}":`,
          error.message,
        );
      }
      throw error;
    }
  }

  static async destroyBridge(agentId: AgentId): Promise<void> {
    const refCount = this.bridgeRefs.get(agentId) ?? 0;

    if (refCount > 1) {
      this.bridgeRefs.set(agentId, refCount - 1);
      return;
    }

    this.bridgeRefs.delete(agentId);

    const bridge = this.bridges.get(agentId);
    if (bridge) {
      this.bridges.delete(agentId);
      await bridge.destroy();
    }
  }

  static async destroyAll(): Promise<void> {
    const promises = Array.from(this.bridges.keys()).map((agentId) =>
      this.destroyBridge(agentId),
    );
    await Promise.all(promises);
    this.bridgeRefs.clear();
  }

  static getBridge(agentId: AgentId): AgentBridge | undefined {
    return this.bridges.get(agentId);
  }
}

