/**
 * EventBus - Cross-module communication via custom events
 *
 * Provides type-safe event emission and subscription for cross-persona
 * notifications (e.g., Alice escalating to Arbiter).
 *
 * @version 1.0.0
 * @date 2026-02-03
 */

export type EscalationSeverity = "info" | "warning" | "critical";

export interface EscalationEvent {
  id: string;
  source: string;
  target?: string;
  severity: EscalationSeverity;
  title: string;
  message: string;
  timestamp: string;
  metadata?: Record<string, unknown>;
}

export type EventBusEventMap = {
  "alice:escalation": EscalationEvent;
  "arbiter:alert": EscalationEvent;
  "system:notification": { type: string; payload: unknown };
};

type EventHandler<T> = (data: T) => void;

const EVENT_PREFIX = "hl:eventbus:";

function getEventName(key: string): string {
  return `${EVENT_PREFIX}${key}`;
}

export function emitEvent<K extends keyof EventBusEventMap>(
  event: K,
  data: EventBusEventMap[K]
): void {
  if (typeof window === "undefined") return;
  window.dispatchEvent(
    new CustomEvent(getEventName(event), { detail: data })
  );
}

export function subscribeEvent<K extends keyof EventBusEventMap>(
  event: K,
  handler: EventHandler<EventBusEventMap[K]>
): () => void {
  if (typeof window === "undefined") return () => {};

  const listener = (e: Event) => {
    const customEvent = e as CustomEvent<EventBusEventMap[K]>;
    if (customEvent.detail) {
      handler(customEvent.detail);
    }
  };

  window.addEventListener(getEventName(event), listener);
  return () => window.removeEventListener(getEventName(event), listener);
}

export function generateEventId(): string {
  return `evt_${Date.now()}_${Math.random().toString(36).slice(2, 9)}`;
}

