/**
 * useEventBus Hook
 *
 * Core hook for subscribing to Tauri event channels.
 * Provides type-safe event subscription with automatic cleanup.
 *
 * @version 1.0.0
 * @date 2026-02-03
 */

import { useEffect, useRef, useCallback, useState } from 'react';
import { listen } from '@tauri-apps/api/event';
import type { UnlistenFn, Event } from '@tauri-apps/api/event';
import type {
  EventChannel,
  EventPayloadMap,
  PayloadFor,
  EventHandler,
  EventSubscriptionOptions,
} from '../types/events/index';

/**
 * Hook return type for single channel subscription
 */
export interface UseEventChannelResult<C extends EventChannel> {
  /** Last received payload */
  lastPayload: PayloadFor<C> | null;
  /** Whether currently connected */
  isConnected: boolean;
  /** Any error that occurred */
  error: string | null;
}

/**
 * Subscribe to a single event channel with type-safe payload handling.
 *
 * @param channel - Event channel to subscribe to
 * @param handler - Callback function invoked on each event
 * @param options - Optional subscription filters
 *
 * @example
 * ```tsx
 * const { lastPayload, isConnected } = useEventChannel(
 *   'alice://behavioral-signal',
 *   (payload) => console.log('Signal:', payload.signalType)
 * );
 * ```
 */
export function useEventChannel<C extends EventChannel>(
  channel: C,
  handler?: EventHandler<C>,
  options?: EventSubscriptionOptions
): UseEventChannelResult<C> {
  const [lastPayload, setLastPayload] = useState<PayloadFor<C> | null>(null);
  const [isConnected, setIsConnected] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const handlerRef = useRef(handler);
  const unlistenRef = useRef<UnlistenFn | null>(null);

  // Keep handler ref updated without re-subscribing
  useEffect(() => {
    handlerRef.current = handler;
  }, [handler]);

  useEffect(() => {
    let mounted = true;

    const setupListener = async () => {
      try {
        unlistenRef.current = await listen(channel, (event: Event<PayloadFor<C>>) => {
          if (!mounted) return;

          const payload = event.payload;

          // Apply optional filters
          if (options?.sessionId || options?.source || options?.correlationId) {
            const envelope = payload as Record<string, unknown>;
            if (options.sessionId && envelope.sessionId !== options.sessionId) return;
            if (options.source && envelope.source !== options.source) return;
            if (options.correlationId && envelope.correlationId !== options.correlationId) return;
          }

          setLastPayload(payload);
          handlerRef.current?.(payload);
        });

        if (mounted) {
          setIsConnected(true);
          setError(null);
        }
      } catch (err) {
        if (mounted) {
          const message = err instanceof Error ? err.message : 'Failed to subscribe';
          console.warn(`[useEventChannel] ${channel} subscription failed:`, message);
          setError(message);
          setIsConnected(false);
        }
      }
    };

    setupListener();

    return () => {
      mounted = false;
      unlistenRef.current?.();
      unlistenRef.current = null;
    };
  }, [channel, options?.sessionId, options?.source, options?.correlationId]);

  return { lastPayload, isConnected, error };
}

/**
 * Hook return type for multi-channel subscription
 */
export interface UseEventChannelsResult {
  /** Map of channel to last received payload */
  payloads: Partial<{ [C in EventChannel]: EventPayloadMap[C] }>;
  /** Map of channel to connection status */
  connectionStatus: Partial<Record<EventChannel, boolean>>;
  /** Any errors keyed by channel */
  errors: Partial<Record<EventChannel, string>>;
}

/**
 * Subscribe to multiple event channels simultaneously.
 *
 * @param channels - Array of event channels to subscribe to
 * @param handlers - Optional map of channel-specific handlers
 *
 * @example
 * ```tsx
 * const { payloads, connectionStatus } = useEventChannels(
 *   ['alice://behavioral-signal', 'alice://deep-work-state'],
 *   {
 *     'alice://behavioral-signal': (p) => console.log('Signal:', p),
 *     'alice://deep-work-state': (p) => console.log('Deep work:', p),
 *   }
 * );
 * ```
 */
export function useEventChannels<C extends EventChannel>(
  channels: readonly C[],
  handlers?: Partial<{ [K in C]: EventHandler<K> }>
): UseEventChannelsResult {
  const [payloads, setPayloads] = useState<UseEventChannelsResult['payloads']>({});
  const [connectionStatus, setConnectionStatus] = useState<UseEventChannelsResult['connectionStatus']>({});
  const [errors, setErrors] = useState<UseEventChannelsResult['errors']>({});
  const handlersRef = useRef(handlers);
  const unlistenRefs = useRef<Map<EventChannel, UnlistenFn>>(new Map());

  useEffect(() => {
    handlersRef.current = handlers;
  }, [handlers]);

  useEffect(() => {
    let mounted = true;

    const setupListeners = async () => {
      for (const channel of channels) {
        try {
          const unlisten = await listen(channel, (event: Event<unknown>) => {
            if (!mounted) return;

            const payload = event.payload as EventPayloadMap[typeof channel];

            setPayloads((prev) => ({ ...prev, [channel]: payload }));

            const handler = handlersRef.current?.[channel as C];
            if (handler) {
              (handler as EventHandler<typeof channel>)(payload);
            }
          });

          unlistenRefs.current.set(channel, unlisten);

          if (mounted) {
            setConnectionStatus((prev) => ({ ...prev, [channel]: true }));
          }
        } catch (err) {
          if (mounted) {
            const message = err instanceof Error ? err.message : 'Failed to subscribe';
            console.warn(`[useEventChannels] ${channel} subscription failed:`, message);
            setErrors((prev) => ({ ...prev, [channel]: message }));
            setConnectionStatus((prev) => ({ ...prev, [channel]: false }));
          }
        }
      }
    };

    setupListeners();

    return () => {
      mounted = false;
      unlistenRefs.current.forEach((unlisten) => unlisten());
      unlistenRefs.current.clear();
    };
  }, [channels.join(',')]); // Re-subscribe only if channel list changes

  return { payloads, connectionStatus, errors };
}

/**
 * Imperatively subscribe to an event channel.
 * Useful for one-off subscriptions or dynamic channel selection.
 *
 * @param channel - Event channel to subscribe to
 * @param handler - Callback function
 * @returns Unsubscribe function
 */
export function subscribeToChannel<C extends EventChannel>(
  channel: C,
  handler: EventHandler<C>
): Promise<() => void> {
  return listen(channel, (event: Event<PayloadFor<C>>) => {
    handler(event.payload);
  }).then((unlisten) => unlisten);
}

/**
 * Hook to get a stable callback for emitting events.
 * Note: This is for frontend-to-frontend communication only.
 * Backend events should be emitted via Tauri commands.
 */
export function useEventEmitter() {
  const emit = useCallback(
    async <C extends EventChannel>(channel: C, payload: PayloadFor<C>): Promise<void> => {
      // Import dynamically to avoid issues in non-Tauri environments
      const { emit: tauriEmit } = await import('@tauri-apps/api/event');
      await tauriEmit(channel, payload);
    },
    []
  );

  return { emit };
}

