import type { EventChannel } from './channels';
import type {
  AudioLevelPayload, TranscriptPayload, SynthesisResultPayload,
  BehavioralSignalPayload, DeepWorkStatePayload, TaskEventPayload,
  ProactiveEventPayload, SecurityAlertPayload, PolicyViolationPayload,
  EscalationPayload, PluginStatusPayload, SystemHealthPayload,
} from './payloads';

/** Maps event channels to their corresponding payload types */
export interface EventPayloadMap {
  // Voice events
  'voice://audio': AudioLevelPayload;
  'voice://ready': Record<string, never>;
  'voice://transcript': TranscriptPayload;
  'voice://synthesis_result': SynthesisResultPayload;
  'voice://announcement': ProactiveEventPayload;
  'voice://error': { error: string; code?: string };
  // Alice events
  'alice://biometric-update': Record<string, unknown>;
  'alice://behavioral-signal': BehavioralSignalPayload;
  'alice://deep-work-state': DeepWorkStatePayload;
  'alice://insight-generated': { insightId: string; category: string; observation: string };
  'alice://pattern-detected': { patternId: string; type: string; confidence: number };
  // Alden events
  'alden://task-update': TaskEventPayload;
  'alden://calendar-sync': { status: 'syncing' | 'complete' | 'error'; eventCount?: number };
  'alden://email-received': { accountId: string; messageId: string; subject: string; from: string };
  'alden://directive-issued': { directiveId: string; urgency: string; content: string };
  // Core events
  'core://proactive-event': ProactiveEventPayload;
  'core://routing-decision': { path: string[]; reasoning: string; priority: string };
  'core://session-update': { sessionId: string; state: string; activeAgents: string[] };
  'core://execution-state': { state: 'active' | 'paused' | 'stopped'; reason?: string };
  'core://intent-drift': { detected: boolean; severity: string; originalIntent: string };
  // Arbiter events
  'arbiter://security-alert': SecurityAlertPayload;
  'arbiter://policy-violation': PolicyViolationPayload;
  'arbiter://compliance-check': { checkId: string; passed: boolean; findings: string[] };
  'arbiter://escalation': EscalationPayload;
  // Synapse events
  'synapse://plugin-status': PluginStatusPayload;
  'synapse://connection-state': { connectionId: string; status: string; service: string };
  'synapse://skill-execution': { skillId: string; status: string; result?: unknown };
  // System events
  'system://health-update': SystemHealthPayload;
}

/** Extract payload type for a specific channel */
export type PayloadFor<C extends EventChannel> = EventPayloadMap[C];

/** Typed event handler function */
export type EventHandler<C extends EventChannel> = (payload: PayloadFor<C>) => void | Promise<void>;

/** Event subscription options */
export interface EventSubscriptionOptions {
  sessionId?: string;
  source?: import('./channels').EventSource;
  correlationId?: string;
}

/** Result of subscribing to an event channel */
export interface EventSubscription {
  subscriptionId: string;
  channel: EventChannel;
  unsubscribe: () => void;
}

