//! Event Channel Definitions
//!
//! Defines all 28 event channels used for cross-module communication.
//! Each channel has a unique URI-style identifier for frontend routing.

use serde::{Deserialize, Serialize};

/// Event channels for cross-module communication.
///
/// URI format: `module://event-type`
#[derive(Debug, Clone, Copy, PartialEq, Eq, Hash, Serialize, Deserialize)]
#[serde(rename_all = "kebab-case")]
pub enum EventChannel {
    // ========================================================================
    // CORE CHANNELS (4)
    // ========================================================================
    /// System lifecycle events (startup, shutdown, restart)
    CoreLifecycle,
    /// Proactive system events (suggestions, reminders)
    CoreProactiveEvent,
    /// Orchestration decisions and routing
    CoreOrchestration,
    /// Execution state changes (pause, resume, stop)
    CoreExecutionState,

    // ========================================================================
    // ALICE CHANNELS (4)
    // ========================================================================
    /// Behavioral pattern signals (stress, focus, fatigue)
    AliceBehavioralSignal,
    /// Deep work session state changes
    AliceDeepWorkState,
    /// Biometric data updates (heart rate, etc.)
    AliceBiometricUpdate,
    /// Wellbeing alerts and recommendations
    AliceWellbeingAlert,

    // ========================================================================
    // ALDEN CHANNELS (4)
    // ========================================================================
    /// Task creation, update, completion events
    AldenTaskEvent,
    /// Calendar event notifications
    AldenCalendarEvent,
    /// Email processing events
    AldenEmailEvent,
    /// Scheduling conflicts and resolutions
    AldenScheduleConflict,

    // ========================================================================
    // ARBITER CHANNELS (4)
    // ========================================================================
    /// Security policy violations
    ArbiterPolicyViolation,
    /// Audit log events
    ArbiterAuditEvent,
    /// Escalation requests requiring user attention
    ArbiterEscalation,
    /// Governance decisions and outcomes
    ArbiterGovernance,

    // ========================================================================
    // VAULT CHANNELS (3)
    // ========================================================================
    /// Data access events (read/write)
    VaultDataAccess,
    /// Encryption key rotation events
