impl EventChannel {
    /// Returns the URI-style channel identifier for frontend routing.
    #[must_use]
    pub const fn uri(&self) -> &'static str {
        match self {
            // Core channels
            Self::CoreLifecycle => "core://lifecycle",
            Self::CoreProactiveEvent => "core://proactive-event",
            Self::CoreOrchestration => "core://orchestration",
            Self::CoreExecutionState => "core://execution-state",

            // Alice channels
            Self::AliceBehavioralSignal => "alice://behavioral-signal",
            Self::AliceDeepWorkState => "alice://deep-work-state",
            Self::AliceBiometricUpdate => "alice://biometric-update",
            Self::AliceWellbeingAlert => "alice://wellbeing-alert",

            // Alden channels
            Self::AldenTaskEvent => "alden://task-event",
            Self::AldenCalendarEvent => "alden://calendar-event",
            Self::AldenEmailEvent => "alden://email-event",
            Self::AldenScheduleConflict => "alden://schedule-conflict",

            // Arbiter channels
            Self::ArbiterPolicyViolation => "arbiter://policy-violation",
            Self::ArbiterAuditEvent => "arbiter://audit-event",
            Self::ArbiterEscalation => "arbiter://escalation",
            Self::ArbiterGovernance => "arbiter://governance",

            // Vault channels
            Self::VaultDataAccess => "vault://data-access",
            Self::VaultKeyRotation => "vault://key-rotation",
            Self::VaultNamespaceEvent => "vault://namespace-event",

            // Synapse/Plugin channels
            Self::PluginLifecycle => "synapse://plugin-lifecycle",
            Self::PluginActivation => "synapse://plugin-activation",
            Self::PluginHealthUpdate => "synapse://plugin-health",
            Self::PluginConfigChange => "synapse://plugin-config",

            // Voice channels
            Self::VoiceTranscript => "voice://transcript",
            Self::VoiceSynthesis => "voice://synthesis",
            Self::VoiceSystemState => "voice://system-state",

            // Mimic channels
            Self::MimicPersonaSwitch => "mimic://persona-switch",
            Self::MimicRoleplayEvent => "mimic://roleplay-event",
        }
    }

    /// Returns the module name for this channel.
    #[must_use]
    pub const fn module(&self) -> &'static str {
        match self {
            Self::CoreLifecycle
            | Self::CoreProactiveEvent
            | Self::CoreOrchestration
            | Self::CoreExecutionState => "core",

            Self::AliceBehavioralSignal
            | Self::AliceDeepWorkState
            | Self::AliceBiometricUpdate
            | Self::AliceWellbeingAlert => "alice",

            Self::AldenTaskEvent
            | Self::AldenCalendarEvent
            | Self::AldenEmailEvent
            | Self::AldenScheduleConflict => "alden",

            Self::ArbiterPolicyViolation
            | Self::ArbiterAuditEvent
            | Self::ArbiterEscalation
            | Self::ArbiterGovernance => "arbiter",

            Self::VaultDataAccess
            | Self::VaultKeyRotation
            | Self::VaultNamespaceEvent => "vault",

            Self::PluginLifecycle
            | Self::PluginActivation
            | Self::PluginHealthUpdate
            | Self::PluginConfigChange => "synapse",

            Self::VoiceTranscript
            | Self::VoiceSynthesis
            | Self::VoiceSystemState => "voice",

            Self::MimicPersonaSwitch | Self::MimicRoleplayEvent => "mimic",
        }
    }

