import React, {
  useCallback,
  useEffect,
  useMemo,
  useState,
  Suspense,
} from "react";
import { HearthlinkLoadingSequence } from "./shared";
import { Toaster } from "sonner";
import { useLocation, useNavigate } from "react-router-dom";
import { useZenMode } from "../context/ZenContext";
import { Minimize2, BookOpen } from "@/components/ui/HearthlinkIcons";
import { UniversalChatOverlay } from "./shared/chat/UniversalChatOverlay";
import { useUniversalChatOverlay } from "./shared/chat/useUniversalChatOverlay";
import { type SystemPersonaId } from "./shared/chat/types";
import { ADHDThemeProvider } from "./ui/ADHDOptimizedInterface";
import { AliceDeepWorkSession } from "./alice/AliceDeepWorkSession";
import { useSettingsBridge } from "../hooks/useBridge";
import { resolveAsset } from "@/utils/assets";
import { SettingsPanelEnhanced } from "./settings/SettingsPanelEnhanced";

// Lazy Load Agent Dashboards
const AldenCommandCenterOptimized = React.lazy(
  () => import("./alden/AldenCommandCenter"),
);
const AliceCommandCenter = React.lazy(
  () => import("./alice/AliceCommandCenter"),
);
const CoreDashboard = React.lazy(() =>
  import("./core/CoreDashboard").then((module) => ({
    default: module.CoreDashboard,
  })),
);
const VaultDashboard = React.lazy(() =>
  import("./vault/VaultDashboard").then((module) => ({
    default: module.VaultDashboard,
  })),
);
const ArbiterCommandCenter = React.lazy(
  () => import("./arbiter/ArbiterCommandCenter"),
);
const SynapseCommandCenter = React.lazy(
  () => import("./synapse/SynapseCommandCenter"),
);
const MimicCommandCenter = React.lazy(
  () => import("./mimic/MimicCommandCenter"),
);
const HelpCenter = React.lazy(() => import("./help/Help"));

export interface HearthlinkAppProps {
  useEnhancedUI: boolean;
  userId: string;
  focusFormulaLicenseToken?: string;
  onBackToLauncher?: () => void;
}

type AgentId =
  | "alden"
  | "alice"
  | "mimic"
  | "core"
  | "vault"
  | "arbiter"
  | "synapse"
  | "help";

/**
 * Main Hearthlink Application Component
 *
 * Provides comprehensive multi-agent interface with tabbed navigation
 * between all 7 agent dashboards.
 */
const HearthlinkApp: React.FC<HearthlinkAppProps> = ({
  useEnhancedUI: _useEnhancedUI,
  userId,
  focusFormulaLicenseToken: _focusFormulaLicenseToken,
  onBackToLauncher,
}) => {
  const location = useLocation();
  const navigate = useNavigate();
  const { isZenMode, toggleZenMode } = useZenMode();

  const agentRoutes: Record<AgentId, string> = useMemo(
    () => ({
      alden: "/alden",
      alice: "/alice",
      mimic: "/mimic",
      core: "/core",
      vault: "/vault",
      arbiter: "/arbiter",
      synapse: "/synapse",
      help: "/help",
    }),
    [],
  );

  const getAgentFromPath = useCallback(
    (pathname: string): AgentId => {
      const entry = Object.entries(agentRoutes).find(([, route]) =>
        pathname.startsWith(route),
      );
      if (entry) {
        return entry[0] as AgentId;
      }
      return "alden";
    },
    [agentRoutes],
  );

  // Settings & ADHD Config
  const { props: settingsProps } = useSettingsBridge(userId);
  const accessibility = settingsProps?.settings?.accessibility;

  const adhdConfig = useMemo(
    () => ({
      reduceMotion: accessibility?.reduceMotion ?? false,
      useHighContrast: accessibility?.highContrast ?? false,
      enableFocusIndicators: accessibility?.focusIndicators ?? true,
      minimizeClutter: accessibility?.minimizeClutter ?? false,
    }),
    [
      accessibility?.reduceMotion,
      accessibility?.highContrast,
      accessibility?.focusIndicators,
      accessibility?.minimizeClutter,
    ],
  );

  const [isFocusAssistOpen, setIsFocusAssistOpen] = useState(false);

  const [activeAgent, setActiveAgent] = useState<AgentId>(() =>
    getAgentFromPath(location.pathname),
  );

  useEffect(() => {
    const nextAgent = getAgentFromPath(location.pathname);
    if (nextAgent !== activeAgent) {
      setActiveAgent(nextAgent);
    }
  }, [location.pathname, activeAgent, getAgentFromPath]);

  const handleAgentChange = (agent: AgentId) => {
    setActiveAgent(agent);
    const targetRoute = agentRoutes[agent];
    if (location.pathname !== targetRoute) {
      navigate(targetRoute);
    }
  };

  const [showSettings, setShowSettings] = useState(false);

  const handleNavigateToModule = useCallback((moduleName: string) => {
    if (moduleName.toLowerCase() === "settings") {
      setShowSettings(true);
      return;
    }
    handleAgentChange(moduleName.toLowerCase() as AgentId);
  }, []);

  const renderDashboard = () => {
    switch (activeAgent) {
      case "alden":
        return (
          <AldenCommandCenterOptimized
            userId={userId}
            onBackToLauncher={onBackToLauncher}
            onNavigateToModule={handleNavigateToModule}
            onFocusAssist={() => setIsFocusAssistOpen(true)}
          />
        );
      case "alice":
        return (
          <AliceCommandCenter
            userId={userId}
            onBackToLauncher={onBackToLauncher}
            onNavigateToModule={handleNavigateToModule}
            onFocusAssist={() => setIsFocusAssistOpen(true)}
          />
        );
      case "mimic":
        return (
          <MimicCommandCenter
            userId={userId}
            onBackToLauncher={onBackToLauncher}
            onNavigateToModule={handleNavigateToModule}
            onFocusAssist={() => setIsFocusAssistOpen(true)}
          />
        );
      case "core":
        return (
          <CoreDashboard
            userId={userId}
            onBackToLauncher={onBackToLauncher}
            onNavigateToModule={handleNavigateToModule}
            onFocusAssist={() => setIsFocusAssistOpen(true)}
          />
        );
      case "vault":
        return (
          <VaultDashboard
            userId={userId}
            onBackToLauncher={onBackToLauncher}
            onNavigateToModule={handleNavigateToModule}
            onFocusAssist={() => setIsFocusAssistOpen(true)}
          />
        );
      case "arbiter":
        return (
          <ArbiterCommandCenter
            userId={userId}
            onBackToLauncher={onBackToLauncher}
            onNavigateToModule={handleNavigateToModule}
            onFocusAssist={() => setIsFocusAssistOpen(true)}
          />
        );
      case "synapse":
        return (
          <SynapseCommandCenter
            userId={userId}
            onBackToLauncher={onBackToLauncher}
            onNavigateToModule={handleNavigateToModule}
            onFocusAssist={() => setIsFocusAssistOpen(true)}
          />
        );
      case "help":
        return <HelpCenter />;
      default:
        return (
          <AldenCommandCenterOptimized
            userId={userId}
            onBackToLauncher={onBackToLauncher}
            onNavigateToModule={handleNavigateToModule}
          />
        );
    }
  };

  // Universal Chat Overlay State
  const {
    isOpen: isChatOpen,
    close: closeChat,
    toggle: toggleChat,
  } = useUniversalChatOverlay();

  // Resolve resident persona for current module
  const getResidentPersona = (agent: AgentId): SystemPersonaId => {
    switch (agent) {
      case "alice":
        return "alice";
      case "mimic":
        return "mimic";
      case "arbiter":
        return "arbiter";
      case "vault":
        return "arbiter";
      case "synapse":
        return "arbiter";
      default:
        return "alden";
    }
  };

  // Check for search params to skip boot or detect mode
  const searchParams = new URLSearchParams(location.search);
  const skipBoot = searchParams.get("skipBoot") === "true";

  const [hasBooted, setHasBooted] = useState(
    skipBoot || activeAgent === "help",
  );

  // Standalone Layout for Help Center (Pop-out Window)
  if (activeAgent === "help") {
    return (
      <ADHDThemeProvider config={adhdConfig}>
        <div
          className="h-screen w-screen bg-background text-foreground overflow-hidden"
          data-testid="help-standalone"
        >
          <Suspense
            fallback={
              <div className="h-full w-full flex items-center justify-center">
                <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-cyan-500"></div>
              </div>
            }
          >
            <HelpCenter isStandalone={true} />
          </Suspense>
          <Toaster position="top-right" richColors closeButton />
        </div>
      </ADHDThemeProvider>
    );
  }

  return (
    <ADHDThemeProvider config={adhdConfig}>
      <div
        className="h-screen flex flex-col bg-background text-foreground"
        data-testid="app-ready"
      >
        {/* Top Navigation Bar */}
        <header className="h-16 border-b border-border bg-background flex items-center justify-between px-6">
          <div className="flex items-center gap-6">
            <span
              className="text-xl font-bold bg-gradient-to-r from-primary to-accent bg-clip-text text-transparent select-none"
              role="img"
              aria-label="Hearthlink Logo"
            >
              Hearthlink
            </span>
            <nav aria-label="Main navigation">
              <ul className="flex items-center gap-1" role="list">
                {Object.entries(agentRoutes).map(([agent, route]) => (
                  <li key={agent}>
                    <button
                      data-testid={`nav-module-${agent}`}
                      onClick={() => handleAgentChange(agent as AgentId)}
                      aria-label={`Open ${agent} Dashboard`}
                      className={`px-3 py-1.5 rounded-md text-sm font-medium transition-all ${
                        activeAgent === agent
                          ? "bg-accent/10 text-accent border border-accent/20"
                          : "text-muted-foreground hover:text-foreground hover:bg-muted/50"
                      }`}
                    >
                      {agent.charAt(0).toUpperCase() + agent.slice(1)}
                    </button>
                  </li>
                ))}
              </ul>
            </nav>
          </div>
          <div className="flex items-center gap-4">
            <button
              onClick={toggleZenMode}
              data-testid="zen-mode-toggle"
              className={`p-2 rounded-full transition-colors ${
                isZenMode
                  ? "bg-slate-700 text-slate-300"
                  : "hover:bg-accent/10 text-muted-foreground hover:text-accent"
              }`}
              aria-label={
                isZenMode ? "Disable Visual Silence" : "Enable Visual Silence"
              }
              title={
                isZenMode
                  ? "Disable Visual Silence"
                  : "Enable Visual Silence (Ctrl+Shift+Z)"
              }
            >
              <Minimize2 size={20} aria-hidden="true" />
            </button>

            <button
              onClick={toggleChat}
              className="p-2 rounded-full hover:bg-accent/10 text-muted-foreground hover:text-accent transition-colors"
              aria-label="Toggle Universal Chat"
              title="Toggle Universal Chat (Ctrl+Space)"
            >
              <svg
                width="20"
                height="20"
                viewBox="0 0 24 24"
                fill="none"
                stroke="currentColor"
                strokeWidth="2"
                aria-hidden="true"
              >
                <path d="M21 15a2 2 0 0 1-2 2H7l-4 4V5a2 2 0 0 1 2-2h14a2 2 0 0 1 2 2z" />
              </svg>
            </button>

            {onBackToLauncher && (
              <button
                onClick={onBackToLauncher}
                data-testid="back-to-launcher"
                className="text-sm text-muted-foreground hover:text-foreground transition-colors"
              >
                Back to Launcher
              </button>
            )}
            <div className="w-8 h-8 rounded-full bg-accent/20 border border-accent/40 flex items-center justify-center text-xs font-bold text-accent">
              {userId.charAt(0).toUpperCase()}
            </div>
          </div>
        </header>

        {/* Main Content Area */}
        <main className="flex-1 overflow-auto bg-background relative p-6">
          <div className="h-full max-w-7xl mx-auto">
            <Suspense
              fallback={
                <div className="h-full w-full flex items-center justify-center bg-[#030712]">
                  <HearthlinkLoadingSequence variant="pulse" />
                </div>
              }
            >
              {renderDashboard()}
            </Suspense>
          </div>
        </main>

        <UniversalChatOverlay
          isOpen={isChatOpen}
          onClose={closeChat}
          userId={userId}
          moduleId={activeAgent}
          residentPersonaId={getResidentPersona(activeAgent)}
          theme={
            getResidentPersona(activeAgent) === "alden"
              ? "alden"
              : (getResidentPersona(activeAgent) as any)
          }
          onFocusAssist={() => {
            setIsFocusAssistOpen(true);
          }}
        />
        {/* Focus Assist Overlay */}
        {isFocusAssistOpen && (
          <div className="fixed inset-0 z-[99999] bg-background">
            <div className="max-w-5xl mx-auto h-full flex flex-col justify-center p-6">
              <AliceDeepWorkSession userId={userId} className="shadow-2xl" />
              <button
                onClick={() => setIsFocusAssistOpen(false)}
                className="absolute top-6 right-6 p-2 text-slate-500 hover:text-white transition-colors"
              >
                Exit Focus Mode
              </button>
            </div>
          </div>
        )}

        {/* Global Settings Modal */}
        {showSettings && (
          <div className="fixed inset-0 z-[10000] flex items-center justify-center bg-black/80 backdrop-blur-sm animate-fadeIn">
            <div className="relative w-full max-w-6xl max-h-[90vh] overflow-auto rounded-xl border border-cyan-500/30 bg-slate-900/95 shadow-2xl ring-1 ring-white/5">
              <SettingsPanelEnhanced onClose={() => setShowSettings(false)} />
            </div>
          </div>
        )}
      </div>
      <Toaster position="top-right" richColors closeButton />
    </ADHDThemeProvider>
  );
};

export default HearthlinkApp;

