      subtitle="Persona Forge"
      iconPath="/assets/Mimic.png"
      tabs={[
        { id: "overview", label: "Overview" },
        { id: "library", label: "Personas" },
        { id: "fabricator", label: "Fabricator" },
        { id: "marketplace", label: "Skills Library" },
        { id: "analytics", label: "Analytics" },
        { id: "chat", label: "Chat" },
      ]}
      activeTab={activeView}
      onTabChange={(id) => setActiveView(id as ViewMode)}
      onBackToLauncher={onBackToLauncher}
      onNavigateToModule={onNavigateToModule}
      onNavigate={(module) => {
        if (onNavigateToModule) {
          onNavigateToModule(module);
          return true;
        }
      }}
      themeColor="#e9d5ff"
      accentColor="#c084fc"
      titleColor="#c084fc"
      subtitleColor="#7e22ce"
      statusTone="success"
      statusLabel="Mimic Online"
      sidebar={sidebar}
      sidebarCollapsed={isSidebarCollapsed}
      headerStyle={{
