import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import {
  Download,
  Search,
  Star,
  Box,
  Shield,
  Zap,
  X,
  Check,
  Info,
} from "@/components/ui/HearthlinkIcons";
import {
  SynapseBridge,
  CreatePluginRequest,
  VersionComparison,
} from "../../bridges/SynapseBridge";
import {
  MarketplaceBridge,
  Permission as MarketplacePermission,
  InstallStatus,
} from "../../bridges/MarketplaceBridge";
import type { SynapsePlugin } from "../../types/agents";
import {
  PermissionConsentModal,
  PluginConsentInfo,
  PermissionType,
} from "./PermissionConsentModal";
import { RefreshCw } from "lucide-react";
import { AldenBridge } from "../../bridges/AldenBridge";
import { useAuth } from "../../contexts/AuthContext";
import { logger } from "../../utils/logger";

// --- Types ---

interface MarketplacePlugin {
  id: string;
  name: string;
  version: string;
  description: string;
  longDescription?: string;
  author: string;
  downloads: number;
  rating: number;
  category:
    | "Integration"
    | "Utility"
    | "Security"
    | "Entertainment"
    | "Communication";
  permissions: MarketplacePermission[];
  tags?: string[];
  lastUpdated?: string;
  status: InstallStatus;
}

// --- Components ---

interface PluginMarketplaceProps {
  synapseBridge: React.MutableRefObject<SynapseBridge | null>;
  installedPlugins: SynapsePlugin[];
  onInstall: (plugin: SynapsePlugin) => void;
  initialSearch?: string;
}

// Permission string mapping from frontend to backend types
const PERMISSION_STRING_MAP: Record<string, PermissionType> = {
  network: "NetworkAccess",
  read_fs: "FileSystemRead",
  write_fs: "FileSystemWrite",
  system: "SystemEvents",
  admin: "SystemEvents", // Map admin to SystemEvents
  sensitive: "SensitiveData",
  notifications: "SystemEvents",
  ipc: "SystemEvents",
  audio: "SystemEvents",
  location: "NetworkAccess", // Location requires network
};

function mapPermissionsToTypes(permissions: string[]): PermissionType[] {
  const mapped = new Set<PermissionType>();
  for (const perm of permissions) {
    const mappedType = PERMISSION_STRING_MAP[perm.toLowerCase()];
    if (mappedType) {
      mapped.add(mappedType);
    }
  }
  return Array.from(mapped);
}

export const PluginMarketplace: React.FC<PluginMarketplaceProps> = ({
  synapseBridge,
  installedPlugins,
  onInstall,
  initialSearch = "",
}) => {
  const { isAuthenticated, isDemoMode, isLoading: authLoading } = useAuth();
  const [searchQuery, setSearchQuery] = useState(initialSearch);
  const [filterCategory, setFilterCategory] = useState<string>("All");
  const [installingId, setInstallingId] = useState<string | null>(null);
  const [selectedPlugin, setSelectedPlugin] =
    useState<MarketplacePlugin | null>(null);
  const [marketplaceData, setMarketplaceData] = useState<MarketplacePlugin[]>(
    [],
  );
  const [isLoading, setIsLoading] = useState(false);
  const [isSyncing, setIsSyncing] = useState(false);
  const [errorMessage, setErrorMessage] = useState<string | null>(null);
  const [versionComparisons, setVersionComparisons] = useState<Map<string, VersionComparison>>(new Map());
  const [lastSyncTime, setLastSyncTime] = useState<Date | null>(null);

  const loadMarketplace = async (forceSync = false) => {
    // Allow loading in demo mode or when authenticated
    if (!isAuthenticated && !isDemoMode) {
      setErrorMessage("Authentication required to load marketplace.");
      return;
    }
    setIsLoading(true);
    setErrorMessage(null);
    if (forceSync) setIsSyncing(true);

    try {
      const data = forceSync
        ? await MarketplaceBridge.sync()
        : await MarketplaceBridge.list();

      const mapped: MarketplacePlugin[] = data.map((entry) => ({
        id: entry.metadata.id,
        name: entry.metadata.name,
        version: entry.metadata.version,
        description: entry.metadata.description,
        author: entry.metadata.author,
        downloads: 0, // Placeholder for metadata expansion
        rating: 5.0, // Placeholder for metadata expansion
        category: (entry.metadata.category.charAt(0).toUpperCase() +
          entry.metadata.category.slice(1)) as any,
        permissions: entry.metadata.permissions,
        longDescription: entry.metadata.long_description,
        tags: entry.metadata.tags,
        status: entry.status,
      }));

      setMarketplaceData(mapped);
    } catch (err) {
      logger.error("PluginMarketplace", "Failed to load marketplace", err);
      setErrorMessage("Unable to load marketplace right now.");
    } finally {
      setIsLoading(false);
      setIsSyncing(false);
    }
  };

  const handleSync = async () => {
    if (!synapseBridge.current) return;
    setIsSyncing(true);
    try {
      const comparisons = await synapseBridge.current.fetchRemoteRegistry();
      const comparisonMap = new Map(comparisons.map(c => [c.plugin_id, c]));
      setVersionComparisons(comparisonMap);
      setLastSyncTime(new Date());
      await loadMarketplace(true);
    } catch (error) {
      logger.error("PluginMarketplace", "Failed to sync with registry", error);
    } finally {
      setIsSyncing(false);
    }
  };

  const handleDownloadPlugin = async (pluginId: string) => {
    if (!synapseBridge.current) return;
    setInstallingId(pluginId);
    try {
      await synapseBridge.current.downloadPlugin(pluginId);
      await loadMarketplace(true);
    } catch (error) {
      logger.error("PluginMarketplace", "Failed to download plugin", error);
    } finally {
      setInstallingId(null);
    }
  };

  React.useEffect(() => {
    if (authLoading) return;
    if (!isAuthenticated && !isDemoMode) {
      setErrorMessage("Authentication required to load marketplace.");
      return;
    }
    loadMarketplace();
  }, [authLoading, isAuthenticated, isDemoMode]);

  // Permission consent modal state
  const [consentModalOpen, setConsentModalOpen] = useState(false);
  const [pendingInstallPlugin, setPendingInstallPlugin] =
    useState<MarketplacePlugin | null>(null);

  const isInstalled = (id: string) =>
    installedPlugins.some(
      (p) => p.plugin_id === id || p.name.toLowerCase().includes(id),
    );

  // Opens the consent modal before installing
  const handleInstall = (plugin: MarketplacePlugin) => {
    setPendingInstallPlugin(plugin);
    setConsentModalOpen(true);
  };

  // Called when user cancels the consent modal
  const handleConsentCancel = () => {
    setConsentModalOpen(false);
    setPendingInstallPlugin(null);
  };

  const handleActivate = async (plugin: MarketplacePlugin) => {
    // Special handling for Google Workspace to trigger Alden OAuth
    if (plugin.id === "google_workspace") {
      try {
        const aldenBridge = new AldenBridge();
        await aldenBridge.initialize();
        const result = await aldenBridge.startOAuthFlow("gmail");
        if (result.success && result.data?.auth_url) {
          // Open OAuth URL in browser
          window.open(result.data.auth_url, "_blank");
          console.log("OAuth flow started for Gmail");
        } else {
          console.error("Failed to start OAuth flow:", result.error);
        }
      } catch (err) {
        console.error("Error starting Google OAuth:", err);
      }
      return;
    }

    // Default activation logic for other enterprise plugins (Okta, Splunk, etc.)
    console.log("Activating plugin:", plugin.id);
    // In a real scenario, this might open a configuration wizard or settings page
    alert(
      `Activation for ${plugin.name} initiated. Please configure credentials in the Connections tab.`,
    );
  };

  // Called when user approves permissions and confirms install
  const handleConsentApprove = async (pluginId: string) => {
    if (!synapseBridge.current || !pendingInstallPlugin) return;

    const plugin = pendingInstallPlugin;
    setInstallingId(plugin.id);

    // Prepare request with consented permissions
    const request: CreatePluginRequest = {
      name: plugin.name,
      version: plugin.version,
      description: plugin.description,
      permissions: plugin.permissions,
      manifest: {
        author: plugin.author,
        category: plugin.category,
        marketplaceId: plugin.id,
      },
      // Include consented permissions for backend validation
      consentedPermissions: mapPermissionsToTypes(plugin.permissions),
    };

    try {
      // Simulate a slight delay for better UX feel (network request simulation if local)
      await new Promise((resolve) => setTimeout(resolve, 800));

      const result = await synapseBridge.current.installPlugin(request);
      if (result.success && result.data) {
        onInstall(result.data);
        // Close the consent modal on success
        setConsentModalOpen(false);
        setPendingInstallPlugin(null);
        // Also close the detail modal if it was open
        setSelectedPlugin(null);
      } else {
        console.error("Failed to install plugin:", result.error);
      }
    } catch (e) {
      console.error("Install exception:", e);
    } finally {
      setInstallingId(null);
    }
  };

  // Convert plugin to consent info format
  const getConsentInfo = (
    plugin: MarketplacePlugin | null,
  ): PluginConsentInfo | null => {
    if (!plugin) return null;
    return {
      id: plugin.id,
      name: plugin.name,
      version: plugin.version,
      description: plugin.longDescription || plugin.description,
      author: plugin.author,
      permissions: mapPermissionsToTypes(plugin.permissions),
    };
  };

  const filteredPlugins = marketplaceData.filter((p) => {
    const matchesSearch =
      p.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
      p.description.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesCategory =
      filterCategory === "All" || p.category === filterCategory;
    return matchesSearch && matchesCategory;
  });

  const getCategoryIcon = (category: string) => {
    switch (category) {
      case "Security":
        return <Shield size={20} />;
      case "Utility":
        return <Box size={20} />;
      case "Entertainment":
        return <Zap size={20} />;
      case "Communication":
        return <Info size={20} />; // Using Info as placeholder for Chat bubble if not avail
      default:
        return <Box size={20} />;
    }
  };

  const UpdateBadge: React.FC<{ comparison?: VersionComparison }> = ({ comparison }) => {
    if (!comparison) return null;
    if (comparison.status === 'up_to_date' || comparison.status === 'local_only') return null;

    const styles: Record<string, string> = {
      update_available: 'bg-yellow-500 text-white',
      new_plugin: 'bg-green-500 text-white',
    };

    const labels: Record<string, string> = {
      update_available: `v${comparison.remote_version}`,
      new_plugin: 'New',
    };

    return (
      <span className={`ml-2 px-2 py-0.5 text-xs rounded-full ${styles[comparison.status] || ''}`}>
        {labels[comparison.status] || ''}
      </span>
    );
  };

  return (
    <div className="h-full flex flex-col space-y-6 relative">
      {/* Search and Filters */}
      <div className="flex flex-col md:flex-row md:items-center space-y-4 md:space-y-0 md:space-x-4 z-10">
        <div className="relative flex-1 group">
          <div className="absolute inset-0 bg-emerald-500/20 blur-md rounded-lg opacity-0 group-focus-within:opacity-100 transition-opacity duration-500 pointer-events-none" />
          <Search
            className="absolute left-3 top-1/2 transform -translate-y-1/2 text-slate-500 group-focus-within:text-emerald-400 transition-colors"
            size={18}
          />
          <input
            type="text"
            placeholder="Search plugin marketplace..."
            className="w-full pl-10 pr-4 py-3 bg-black/60 border border-white/10 rounded-lg text-emerald-100 placeholder:text-slate-600 focus:outline-none focus:border-emerald-500/50 focus:bg-black/80 transition-all font-light"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </div>
        <div className="flex items-center">
          <button
            onClick={handleSync}
            disabled={isSyncing || (!isAuthenticated && !isDemoMode)}
            className={`p-3 bg-black/40 border border-white/10 rounded-lg text-slate-400 hover:text-emerald-400 hover:border-emerald-500/30 transition-all ${isSyncing ? "animate-pulse" : ""}`}
            title="Sync with Remote Registry"
          >
            <RefreshCw size={18} className={isSyncing ? "animate-spin" : ""} />
          </button>
          {lastSyncTime && (
            <span className="ml-2 text-xs text-gray-400">
              Last: {lastSyncTime.toLocaleTimeString()}
            </span>
          )}
        </div>
        <div className="flex space-x-2 overflow-x-auto pb-2 md:pb-0 scrollbar-hide">
          {["All", "Communication", "Utility", "Security", "Entertainment"].map(
            (cat) => (
              <button
                key={cat}
                onClick={() => setFilterCategory(cat)}
                className={`px-4 py-2 rounded-lg text-sm font-medium transition-all whitespace-nowrap ${
                  filterCategory === cat
                    ? "bg-emerald-500 text-black shadow-[0_0_15px_rgba(16,185,129,0.4)]"
                    : "bg-black/40 border border-white/5 text-slate-400 hover:bg-white/5 hover:text-emerald-400"
                }`}
              >
                {cat}
              </button>
            ),
          )}
        </div>
      </div>
      {errorMessage && (
        <div className="text-sm text-amber-300/90 bg-amber-900/20 border border-amber-400/20 rounded-lg px-4 py-2">
          {errorMessage}
        </div>
      )}

      {/* Plugin Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-5 overflow-y-auto pr-2 pb-4 scrollbar-thin scrollbar-thumb-emerald-900/50 scrollbar-track-transparent flex-grow">
        <AnimatePresence mode="popLayout">
          {filteredPlugins.map((plugin, index) => {
            const installed = isInstalled(plugin.id);
            return (
              <motion.div
                layout
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, scale: 0.95 }}
