    setStep("configure");
  };

  const handleOAuthStart = async () => {
    if (!selectedProviderId) return;

    setIsOAuthPending(true);
    setTestResult(null);

    try {
      const bridge = new SynapseBridge();
      const response = await bridge.startPluginOAuth(selectedProviderId, "user");

      if (!response.success) {
        throw new Error(response.error || "Failed to start OAuth flow");
      }

      const { auth_url } = response.data;

      // Open auth URL in system browser
      window.open(auth_url, "_blank");

      setTestResult({
        success: true,
        message: "Authorization started. Complete in your browser, then return here.",
      });
    } catch (e: any) {
      setTestResult({
        success: false,
        message: e.message || "Failed to start OAuth flow",
      });
    } finally {
      setIsOAuthPending(false);
    }
  };

  const handleInputChange = (key: string, value: string) => {
