import * as vscode from 'vscode';
import { StreamableHTTPServerTransport } from '@modelcontextprotocol/sdk/server/streamableHttp.js';
import type { McpServer } from '@modelcontextprotocol/sdk/server/mcp.js';
import * as http from 'http';
import * as net from 'net';
import { ChromeDevtoolsMcpOutputChannel } from './logger';
import type { ChromeDevtoolsMcp } from './types';
import { Mutex } from 'async-mutex';

const BROWSER_CDP_PORT_DEFAULT = 9222;

let proxyServer: net.Server | null = null;
let proxyPort: number | null = null;
let mcpServer: http.Server | null = null;
let mcpPort: number | null = null;
let retryCount = 0;
let retryTimeoutId: NodeJS.Timeout | null = null; // Track pending retry timeout
const MAX_RETRIES = 5;
const BASE_RETRY_DELAY_MS = 1000;

let chromeDevtoolsMcp: ChromeDevtoolsMcp | null = null;
// Current MCP server instance (created via factory function)
let currentMcpServerInstance: McpServer | null = null;

export async function activate(context: vscode.ExtensionContext) {
	const logger = ChromeDevtoolsMcpOutputChannel.getInstance();
	logger.appendLine('Extension activating...');

	try {
		// Have to do a dynamic import because the module has top-level await.
		// @ts-expect-error - types.d.ts provides the module declaration but TS doesn't pick it up for dynamic imports
		chromeDevtoolsMcp = await import('chrome-devtools-mcp/build/src/main.js');
	} catch (error) {
		logger.appendLine(`Failed to load chrome-devtools-mcp module: ${error instanceof Error ? error.message : String(error)}`);
		return;
	}

	// Initialize MCP server
	await startMcpServer();

	// Register command for Language Server to get proxy URL
	context.subscriptions.push(
		vscode.commands.registerCommand(
			'antigravity.getChromeDevtoolsMcpUrl',
			() => {
				if (!mcpPort) {
					return null;
				}
				logger.appendLine(`Returning MCP URL: http://127.0.0.1:${mcpPort}/mcp`);
				return `http://127.0.0.1:${mcpPort}/mcp`;
			}
		)
	);
}

/**
 * Start the MCP server.
 */
async function startMcpServer(): Promise<void> {
	const logger = ChromeDevtoolsMcpOutputChannel.getInstance();
	if (!chromeDevtoolsMcp) {
		throw new Error('chrome-devtools-mcp module not loaded');
	}

	try {
		// Get current CDP port
		const cdpPort = await vscode.antigravityUnifiedStateSync
			.BrowserPreferences.getBrowserCdpPort();

		// Configure browser URL for Chrome DevTools tools
		chromeDevtoolsMcp.args.browserUrl = `http://127.0.0.1:${cdpPort}`;
		logger.appendLine(`Configured browser URL: ${chromeDevtoolsMcp.args.browserUrl}`);

		// Create a new MCP server instance using factory function
		// The servers will be garbage collected when currentMcpServerInstance is set to a new value
		currentMcpServerInstance = chromeDevtoolsMcp.createServer();
		logger.appendLine('Created new MCP server instance');

		// Create StreamableHTTPServerTransport
		const transport = new StreamableHTTPServerTransport();

		// Connect MCP server to transport
		await currentMcpServerInstance.connect(transport);

		// Create HTTP server that delegates to transport
		mcpServer = http.createServer((req, res) => {
			transport.handleRequest(req, res);
		});

		// Start listening on dynamic port
		await new Promise<void>((resolve, reject) => {
			mcpServer!.listen(0, '127.0.0.1', () => {
				const address = mcpServer!.address();
				if (address && typeof address !== 'string') {
					mcpPort = address.port;
					retryCount = 0; // Reset retry count on successful start
					logger.appendLine(`Server ready at http://127.0.0.1:${mcpPort}/mcp`);
					// logger.appendLine(`[MCP Server] Now forwarding requests from :${proxyPort} to :${mcpPort}`);
					resolve();
				} else {
					reject(new Error('Failed to get server address'));
				}
			});

			mcpServer!.on('error', reject);
		});

		// Handle server errors after successful start
		mcpServer.on('error', (err) => {
			logger.appendLine(`Server error: ${err.message}`);
			scheduleRetryMcpServer();
		});

	} catch (error) {
		logger.appendLine(`Failed to start server: ${error instanceof Error ? error.message : String(error)}`);
		scheduleRetryMcpServer();
	}
}

/**
 * Schedule a retry for MCP server startup with exponential backoff.
 * Cleans up the existing server before retrying.
 */
function scheduleRetryMcpServer(): void {
	const logger = ChromeDevtoolsMcpOutputChannel.getInstance();

	// Cancel any existing retry to prevent multiple simultaneous retries
	if (retryTimeoutId) {
		clearTimeout(retryTimeoutId);
		retryTimeoutId = null;
	}

	if (retryCount >= MAX_RETRIES) {
		logger.appendLine(`Max retries (${MAX_RETRIES}) reached. Giving up.`);
		return;
	}

	const delayMs = BASE_RETRY_DELAY_MS * Math.pow(2, retryCount);
	retryCount++;

	logger.appendLine(
		`âš ï¸ Retry ${retryCount}/${MAX_RETRIES} in ${delayMs}ms...`
	);

	retryTimeoutId = setTimeout(() => {
		retryTimeoutId = null; // Clear the ID when retry executes
		cleanupMcpServer();
		startMcpServer();
	}, delayMs);
}

/**
 * Cleanup MCP server resources
 */
function cleanupMcpServer(): void {
	if (mcpServer) {
		currentMcpServerInstance?.server.close();
		mcpServer.close();
		mcpServer = null;
		mcpPort = null;
	}
	// Clear the server instance so a new one is created on next start
	currentMcpServerInstance = null;
}

/**
 * Cleanup proxy server resources
 */
function cleanupProxyServer(): void {
	if (proxyServer) {
		proxyServer.close();
		proxyServer = null;
		proxyPort = null;
	}
}

export function deactivate() {
	// Cancel any pending retry
	if (retryTimeoutId) {
		clearTimeout(retryTimeoutId);
		retryTimeoutId = null;
	}
	cleanupMcpServer();
	cleanupProxyServer();
}
