interface AppSettings {
  uiMode: UIMode;
  userId: string;
  focusFormulaLicenseToken?: string;
  debugMode: boolean;
}

/**
 * Inner App Component - Wrapped with AuthProvider
 * Contains authentication guard logic to show LoginScreen when needed
 */
const AppWithAuthGuard: React.FC = () => {
  const navigate = useNavigate();
  const { isAuthenticated, isDemoMode, isLoading: authLoading } = useAuth();

  // Loading Sequence State - Blocks main content until animation completes
  // Can be bypassed via URL param (?skipBoot=true) or localStorage (for E2E tests)
  const [isSequenceComplete, setIsSequenceComplete] = useState(() => {
    const params = new URLSearchParams(window.location.search);
    const skipViaUrl = params.get("skipBoot") === "true";
    const skipViaStorage =
      localStorage.getItem("hearthlink_skip_boot") === "true";
    return skipViaUrl || skipViaStorage;
  });
  const location = useLocation();
  const {
    showOnboarding,
    isComplete,
    config,
    completeOnboarding,
    skipOnboarding,
