// Check if we're running in Tauri environment
export const isTauriApp = (): boolean => {
  try {
    // Check if __TAURI_METADATA__ exists (injected by Tauri)
    return typeof window !== 'undefined' && '__TAURI_METADATA__' in window;
  } catch {
    return false;
  }
};

// Check if we're running in development browser mode
export const isBrowserDev = (): boolean => {
  return !isTauriApp() && (
    window.location.hostname === 'localhost' ||
    window.location.hostname === '127.0.0.1'
  );
};

// Get appropriate app window label
export const getWindowLabel = (): string => {
  if (isTauriApp() && (window as any).__TAURI_METADATA__) {
    return (window as any).__TAURI_METADATA__.__currentWindow?.label || 'main';
  }
  return 'main'; // Fallback for browser
};

