/**
 * Simple logger utility for Hearthlink
 */

type LogLevel = 'debug' | 'info' | 'warn' | 'error';
type LogPayload = unknown;

class Logger {
  private isDevelopment = process.env.NODE_ENV === 'development';

  private log(level: LogLevel, context: string, message: string, data?: LogPayload) {
    const timestamp = new Date().toISOString();
    const prefix = `[${timestamp}] [${level.toUpperCase()}] [${context}]`;

    if (this.isDevelopment) {
      switch (level) {
        case 'debug':
          console.debug(prefix, message, data || '');
          break;
        case 'info':
          console.info(prefix, message, data || '');
          break;
        case 'warn':
          console.warn(prefix, message, data || '');
          break;
        case 'error':
          console.error(prefix, message, data || '');
          break;
      }
    }
  }

  debug(context: string, message: string, data?: LogPayload) {
    this.log('debug', context, message, data);
  }

  info(context: string, message: string, data?: LogPayload) {
    this.log('info', context, message, data);
  }

  warn(context: string, message: string, data?: LogPayload) {
    this.log('warn', context, message, data);
  }

  error(context: string, message: string, data?: LogPayload) {
    this.log('error', context, message, data);
  }
}

export const logger = new Logger();

